float ReadKeyToggle(int keyCode) {return texelFetch(iChannel3, ivec2(keyCode, 2), 0).x;}

float Read(vec2 uv)
{
    //uv += off * 2.0;// * 2.0 because offsets assume that the target has a 1/(2x2) lower resolution (which usually is the case (but isn't here))

    vec2 res = vec2(SIZE);

    if(READ_TEXTURE_COND) res = iChannelResolution[0].xy;

    uv = fract(uv / res) * res;// wrap

    float v = textureLod(iChannel1, (uv + 0.) / iResolution.xy, 0.0).g;

    return v;
}

float filterType;// set in mainImage(..)

// 4x4 samples downsampling kernel implemented via 4 bilinear samples
vec4 BloomKernel4(vec2 uv)
{
    // optimal stop band
    float la = 0.3533943809268811;

	float oa = 0.5 + la;

	vec4 ca = vec4(0.0);
	ca += Read(uv + vec2(-1.0,-1.0) * oa);
	ca += Read(uv + vec2( 1.0,-1.0) * oa);
	ca += Read(uv + vec2(-1.0, 1.0) * oa);
	ca += Read(uv + vec2( 1.0, 1.0) * oa);

    return ca * 0.25;
}

// 24 samples downsampling kernel implemented via 8 bilinear samples
vec4 BloomKernel8B(vec2 uv)
{
#if 1
    // optimal stop band
    float la = 0.553811;
    float lb = 0.35856;    
#else
    float la = 0.245966;
    float lb = 0.333333;
#endif

	vec2 o = vec2(1.5, 0.5) + vec2(la, lb);
   //o*=.5;
	vec4 col = vec4(0.0);
	col += Read(uv + vec2( 1.0, 1.0) * o.xy);
	col += Read(uv + vec2(-1.0, 1.0) * o.xy);
	col += Read(uv + vec2( 1.0,-1.0) * o.xy);
	col += Read(uv + vec2(-1.0,-1.0) * o.xy);

    col += Read(uv + vec2( 1.0, 1.0) * o.yx);
	col += Read(uv + vec2(-1.0, 1.0) * o.yx);
	col += Read(uv + vec2( 1.0,-1.0) * o.yx);
	col += Read(uv + vec2(-1.0,-1.0) * o.yx);

    return col * 0.125;
}

