vec4 BloomKernel8(vec2 uv)
{
#if 0
    // optimal stop band
    float a = 0.13433339843769723; 
    float la = 0.9108132736411344;
    float lb = 0.37269896061302266;
#elif 0
    float a = 0.0181804; 
    float la = 0.0744041;
    float lb = 0.370778;
#elif 0
    float a = 0.055432; 
    float la = 0.553841;
