#else
    // new Nyquist = 0
    float a = 0.32761490937746784;
    float la = 0.5;
    float lb = 0.5;
#endif

	float oa = 1.5 + la;
	float ob = 0.5 + lb;

	vec4 ca = vec4(0.0);
	ca += Read(uv + vec2(-1.0, 0.0) * oa);
	ca += Read(uv + vec2( 1.0, 0.0) * oa);
	ca += Read(uv + vec2( 0.0,-1.0) * oa);
	ca += Read(uv + vec2( 0.0, 1.0) * oa);

    vec4 cb = vec4(0.0);
