	cb += Read(uv + vec2(-1.0, 1.0) * ob);
	cb += Read(uv + vec2( 1.0, 1.0) * ob);

    return (cb * (1.0 - a) + ca * a) * 0.25;
}

// 6x6 downsampling kernel implemented via 9 bilinear samples (non-separable variant)
vec4 BloomKernel9B(vec2 uv)
{
#if 0
    // optimal stop-band
    float w0 = 0.28397;
	float a  = 0.124413;
	float b  = 0.0545943;
	float oa = 1.5 + 0.262180;
	float ob = 1.5 + 0.262641;
#elif 0
    // radial symmetry + optimal stop-band
    float w0 = 0.309247;
	float a  = 0.129306;
	float b  = 0.0433824;
	float oa = 1.5 + 0.25;// ~ 0.246235
	float ob = 1.5 + (0.125+1.0/32.0);
#elif 1
    // radial symmetry + optimal stop-band
    float w0 = 0.302001;
	float a  = 0.127963;
	float b  = 0.0465365;
	float oa = 1.5 + 0.25;// ~ 0.250795
	float ob = 1.5 + (0.125+1.0/16.0);    
#elif 1
    // params by Mikkel Gjoel;; half Nyquist = 0 (along x & y)
    // (https://twitter.com/pixelmager/status/1691731512004071785)
    float w0 = 4.0/16.0;
	float a  = 2.0/16.0;
	float b  = 1.0/16.0;
	float oa = 1.5 + 0.25;
	float ob = 1.5 + 0.2052; //(1.0 - 0.7948);       
#elif 1
    // radial symmetry + half Nyquist = 0 (along x & y)
    float w0 = 4.0/16.0;
	float a  = 2.0/16.0;
	float b  = 1.0/16.0;
	float oa = 1.5 + 0.292864;
	float ob = 1.5 + 0.24;
#else    
    // optimal stop-band + half Nyquist = 0 (along x & y)
    float w0 = 4.0/16.0;
	float a  = 2.0/16.0;
	float b  = 1.0/16.0;
	float oa = 1.5 + 0.295266;
	float ob = 1.5 + 0.3019;
#endif

    if(filterType == 2.0)
    {
        // params by Mikkel Gjoel 
        // (https://twitter.com/pixelmager/status/1691731512004071785)
        w0 = 4.0/16.0;
        a  = 2.0/16.0;
        b  = 1.0/16.0;
        oa = 1.5 + 0.25;
        ob = 1.5 + 0.2052; //(1.0 - 0.7948);   
    }

	vec4 c = vec4(0.0);
    c += Read(uv + vec2(0.0)) * w0;

	c += Read(uv + vec2(-1.0, 0.0) * oa) * a;
	c += Read(uv + vec2( 1.0, 0.0) * oa) * a;
	c += Read(uv + vec2( 0.0,-1.0) * oa) * a;
	c += Read(uv + vec2( 0.0, 1.0) * oa) * a;

	c += Read(uv + vec2(-1.0,-1.0) * ob) * b;
	c += Read(uv + vec2( 1.0,-1.0) * ob) * b;
	c += Read(uv + vec2(-1.0, 1.0) * ob) * b;
	c += Read(uv + vec2( 1.0, 1.0) * ob) * b;

	return c;
}

// 6x6 downsampling kernel implemented via 9 bilinear samples
vec4 BloomKernel9(vec2 uv)
{
    float wa, o;

#if 0
    // optimal stop band
    wa = 7.46602 / 32.0;
	o  = 1.5 + 0.261629;    
#elif 1
    // optimal stop band but o is chosen so as to keep frequency response 
    // symmetric despite lower-precision hardware bilinear filtering
   #if 1
    wa = 7.49862 / 32.0;
	o  = 1.5 + (0.25+1.0/64.0);
   #elif 1
    wa = 7.44733 / 32.0;
	o  = 1.5 + (0.25+1.0/128.0);
   #else
    wa = 7.37167 / 32.0;
	o  = 1.5 + 0.25; 
   #endif
#elif 1
    // new Nyquist = 0
    wa = 8.0 / 32.0;
	o  = 1.5 + 0.297785;
#elif 0
    // pascal kernel (gauss approx), no ripples in stop band
	wa = 6.0 / 32.0;
	o  = 1.5 + (1.0 / 6.0);
#endif

	float wb = 1.0 - wa * 2.0;

	vec4 c = vec4(0.0);
    c += Read(uv + vec2(0.0)) * (wb * wb);

	c += Read(uv + vec2(-1.0, 0.0) * o) * (wa * wb);
	c += Read(uv + vec2( 1.0, 0.0) * o) * (wa * wb);
	c += Read(uv + vec2( 0.0,-1.0) * o) * (wa * wb);
	c += Read(uv + vec2( 0.0, 1.0) * o) * (wa * wb);

	c += Read(uv + vec2(-1.0,-1.0) * o) * (wa * wa);
	c += Read(uv + vec2( 1.0,-1.0) * o) * (wa * wa);
	c += Read(uv + vec2(-1.0, 1.0) * o) * (wa * wa);
	c += Read(uv + vec2( 1.0, 1.0) * o) * (wa * wa);

	return c;
}

// original 13 samples kernel design: "Next Generation Post Processing in Call of Duty Advanced Warfare" (Siggraph 2014)
// http://www.iryoku.com/next-generation-post-processing-in-call-of-duty-advanced-warfare

// 6x6 downsampling kernel implemented via 13 bilinear samples
vec4 BloomKernel13(vec2 uv)
{
    float w0, wa, wb, wc;

#if 1
    // optimal stop band
    w0 = 0.173633;
    wa = 0.0991668;
    wb = 0.0744867;
    wc = 0.0329382;    
#elif 0
    // radial symmetry
    w0 = 0.144622;
    wa = 0.105596;
    wb = 0.0822943;
    wc = 0.0259547;
#elif 0
    // hybrid
    w0 = 0.15377;
    wa = 0.106554;
    wb = 0.0781547;
    wc = 0.0268489;
#else
    // original weights (new Nyquist = 0)
    w0 = 4.0/32.0;// 0.125
    wa = 4.0/32.0;// 0.125
    wb = 2.0/32.0;// 0.0625
    wc = 1.0/32.0;// 0.03125
#endif

    if(filterType == 4.0)
    {
        // original weights (new Nyquist = 0)
        w0 = 4.0/32.0;// 0.125
        wa = 4.0/32.0;// 0.125
        wb = 2.0/32.0;// 0.0625
        wc = 1.0/32.0;// 0.03125
    }

	vec4 c = vec4(0.0);

	c += w0 * Read(uv + vec2(0.0));

	c += wa * Read(uv + vec2(-1.0,-1.0));
	c += wa * Read(uv + vec2( 1.0, 1.0));
	c += wa * Read(uv + vec2( 1.0,-1.0));
	c += wa * Read(uv + vec2(-1.0, 1.0));

	c += wb * Read(uv + vec2( 2.0, 0.0));
	c += wb * Read(uv + vec2(-2.0, 0.0));
	c += wb * Read(uv + vec2( 0.0, 2.0));
	c += wb * Read(uv + vec2( 0.0,-2.0));

	c += wc * Read(uv + vec2( 2.0, 2.0));
	c += wc * Read(uv + vec2(-2.0, 2.0));
	c += wc * Read(uv + vec2( 2.0,-2.0));
	c += wc * Read(uv + vec2(-2.0,-2.0));

    return c;
}

void mainImage(out vec4 outCol, in vec2 uv0)
{
    if(uv0.x > SIZE || uv0.y > SIZE)
    {
        discard;
    }

    filterType = texelFetch(iChannel1, ivec2(0, 0), 0).a;

    vec2 uv = floor(uv0);

    if(DO_DOWNSAMPLE_COND) uv *= 2.0;

    float v = 0.0;

    if(filterType == 0.0)
    {     
        v = BloomKernel9(uv).r;
    }
    else if(filterType == 1.0 ||
            filterType == 2.0)
    {     
        v = BloomKernel9B(uv).r;
    }
    else
    {        
        v = BloomKernel13(uv).r;
    }

    outCol = vec4(v, 0.0, 0.0, 1.0);
}

//Horizontal + Vertical Discrete Fourier Transform of the input 
//adapted from FabriceNeyret2's https://www.shadertoy.com/view/XtScWt

vec2 cmul (vec2 a,float b) { return mat2(a,-a.y,a.x) * vec2(cos(b),sin(b)); } 

void mainImage(out vec4 fragColor, vec2 fragCoord )
{
   	fragColor = vec4(0);

    vec2 uv = fragCoord.xy - 0.5;

    if(fragCoord.x >= SIZE || fragCoord.y >= SIZE) {fragColor = vec4(0.01); return;}

    for(float i = 0.; i < SIZE; i++)  
	{
		vec2 xn = texelFetch(iChannel0, ivec2(i, uv.y), 0).rg;
        //vec2 yn = texelFetch(iChannel0, ivec2(uv.x, i), 0).ba;
		vec2 ang = - 6.2831853 * (uv -SIZE/2.) * i/SIZE;
		fragColor.zw += cmul(xn, ang.x);
		//fragColor.xy += cmul(yn, ang.y);
    }
}

