#define n_tau 6.2831

float f_n_d_squircle_4(
    vec2 o_fc, 
    vec4 o_corner_normalized
){
    float n_crc = length(o_fc);
    float n_sqr = max(abs(o_fc.x), abs(o_fc.y));

    // 1|0
    // ---
    // 3|2

    float n_index_quadrant = float(o_fc.y > 0.)*2. + (float(o_fc.x > 0.)*1.);
    float n_crc_intensity = o_corner_normalized[int(n_index_quadrant)];
    return n_sqr*(1.-n_crc_intensity) + n_crc*n_crc_intensity;
    // return n_index_quadrant/4.;
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 o_fc = (fragCoord.xy-iResolution.xy*.5)/iResolution.y;
    float n_t = iTime *2.;
    float n = f_n_d_squircle_4(
        o_fc,
        vec4(
            sin(n_t+(n_tau/4.)*1.)*.5+0.5, 
            sin(n_t+(n_tau/4.)*2.)*.5+0.5, 
            sin(n_t+(n_tau/4.)*4.)*.5+0.5,
            sin(n_t+(n_tau/4.)*3.)*.5+0.5
        )
    );
    // n = sin(n*33.);
    fragColor = vec4(0.);
    // n = smoothstep(0., 0.2, n);
    float n_th = 0.2;
    float n_aa = (1./iResolution.x)*20.;
    n = smoothstep(n_th, n_th+n_aa, n);
    fragColor = vec4(n);

}
