// (Includes bits from "Mighty Mriya", "Spherical Metropolis" and more)

#define AA  0  // (= 0/1) optional antialiasing

#if 1
#define VAR_ZERO min (iFrame, 0)
#else
#define VAR_ZERO 0
#endif

float PrBoxDf (vec3 p, vec3 b);
float PrBox2Df (vec2 p, vec2 b);
float PrRoundBox2Df (vec2 p, vec2 b, float r);
float PrSphDf (vec3 p, float r);
float PrCylDf (vec3 p, float r, float h);
float PrRoundCylDf (vec3 p, float r, float rt, float h);
float PrCapsDf (vec3 p, float r, float h);
float PrCaps2Df (vec2 p, float r, float h);
float Minv2 (vec2 p);
float Maxv2 (vec2 p);
float SmoothMax (float a, float b, float r);
float SmoothBump (float lo, float hi, float w, float x);
mat3 StdVuMat (float el, float az);
vec2 Rot2D (vec2 q, float a);
vec2 Rot2Cs (vec2 q, vec2 cs);
vec3 HsvToRgb (vec3 c);
float Hashff (float p);
float Hashfv2 (vec2 p);
float Noisefv2 (vec2 p);
float IFbm1 (float p);
float Fbm2 (vec2 p);
vec3 VaryNf (vec3 p, vec3 n, float f);

mat3 flyerMat;
vec4 csWm, csWt;
vec3 flyerPos, flPos, qHit, sunDir, mistCol, dwf, dwr;
vec2 cId, cIdT, bIdV, cTimeV;
float dstFar, tCur, cCar, qcCar, cDir, flrHt, bShape, bWid, bWidU, bHt, bHtU,
   tWid, twHt, nFloor, flSzFac, angWm, drpWm, angWt, drpWt;
int idObj;
bool wlDown;
const float pi = 3.1415927;
const int idBldgF = 1, idBldgC = 2, idTwr = 3, idTwrTop = 4, idRoad = 5, idSWalk = 6, idTrLight = 7,
   idStLight = 8, idPool = 9, idTrnk = 10, idTree = 11, idCarBdy = 12, idCarWhl = 13, idFus = 21,
   idCkp = 22, idEng = 23, idWngM = 24, idWngT = 25, idFin = 26, idWhl = 27, idLeg = 28, idFr = 29;

#define DMINQ(id) if (d < dMin) { dMin = d;  idObj = id;  qHit = q; }

float FlyerDf (vec3 p)
{
  vec3 q;
  vec2 ww;
  float dMin, d, s, w, t, wg;
  dMin = dstFar / flSzFac;
  p = flyerMat * (p - flyerPos) / flSzFac;
  q = p;
  w = 1.;
  s = 0.;
  if (q.z < -1.5) {
    s = q.z + 1.5;
    s *= s;
    w *= 1. - 0.025 * s;
    s *= 0.026;
  } else if (q.z > 3.5) {
    s = q.z - 3.5;
    s *= s;
    w *= 1. - 0.05 * s;
    s *= -0.02;
  }
  d = PrCapsDf (q - vec3 (0., s, 0.), w, 7.);
  if (wlDown) d = max (d, - min (PrBoxDf (vec3 (abs (q.x) - 0.45, q.y + 1., q.z - 0.4),
     vec3 (0.2, 0.25, 1.6)), PrBoxDf (vec3 (abs (q.x) - 0.2, q.y + 1., q.z - 4.8),
     vec3 (0.1, 0.3, 0.2))));
  DMINQ (idFus);
  q = p;
  q.x = abs (q.x);
  q -= vec3 (0.65, 0.1, 6.);
  d = PrCapsDf (q, 0.02, 0.3);
  DMINQ (idFr);
  q = p;
  q.z -= -0.7;
  q.xz = abs (q.xz);
  q -= vec3 (0.2, 0.8, 4.);
  d = PrCapsDf (q.xzy, 0.07, 0.2);
  DMINQ (idFr);
  q = p;
  q.yz -= vec2 (0.4, 3.5);
  d = PrCapsDf (q, 0.6, 1.5);
  DMINQ (idCkp);
  ww = vec2 (4., 1.4);
  q = p;
  q.x = abs (q.x);
  q.yz -= vec2 (0.7, 1.3);
  q.xy = Rot2Cs (q.xy, csWm.xy);
  q.xz = Rot2Cs (q.xz, csWm.zw);
  t = ww.y * (1. - 0.3 * q.x / ww.x);
  s = 1.1 * (q.z - 0.2) / ww.y;
  d = SmoothMax (PrCaps2Df (q.yz, 0.15 * (t - s * s), t), abs (abs (p.x) - ww.x) - ww.x, 0.06);
  d = SmoothMax (d, - abs (PrBox2Df (vec2 (abs (p.x) - 6.9, q.z - 0.085 * q.x + 1.2),
     vec2 (0.85, 0.2))), 0.03);
  DMINQ (idWngM);
  for (float k = 0.; k < 4.; k ++) {
    q = p;
    q.x = abs (q.x);
    q -= (k + 1.1) * dwr + vec3 (0., 0.65, 0.1);
    d = PrCapsDf (q, 0.05, 0.25);
    DMINQ (idFr);
  }
  for (float k = 0.; k < 3.; k ++) {
    q = p;
    q.x = abs (q.x);
    q -= (k + 1.3) * dwf + vec3 (0., 0.22, 2.8);
    d = max (PrRoundCylDf (q.yzx - vec3 (0.25, -0.1, 0.), 0.25, 0.05, 0.), 0.3 - q.y);
    DMINQ (idFr);
    d = min (SmoothMax (abs (PrCapsDf (q, 0.3 * (1. - 0.5 * q.z * q.z), 0.9)) - 0.02,
       abs (q.z + 0.1) - 0.55, 0.02), PrCylDf (q, 0.28, 0.25));
    s = q.z + 0.2;
    d = max (min (d, PrCapsDf (q + vec3 (0., 0., 0.2), 0.25 * (1. - 2. * s * s), 0.7)),
       - PrCylDf (vec3 (q.xy, q.z + 0.8), 0.07, 0.2));
    DMINQ (idEng);
  }
  ww = vec2 (1.3, 0.85);
  q = p;
  q.x = abs (q.x);
  q.yz -= vec2 (0.8, -5.8);
  q.xy = Rot2Cs (q.xy, csWt.xy);
  q.xz = Rot2Cs (q.xz, csWt.zw);
  d = min (ww.x - abs (abs (p.x) - ww.x), abs (PrBox2Df (vec2 (abs (p.x) - 1.7,
     q.z - 0.07 * q.x + 0.6), vec2 (0.7, 0.25))));
  t = ww.y * (1. - 0.25 * q.x / ww.x);
  s = (q.z - 0.1) / ww.y;
  d = SmoothMax (PrCaps2Df (q.yz, 0.12 * (t - s * s), t), - d, 0.03);
  DMINQ (idWngT);
  ww = vec2 (1.5, 0.65);
  q = p;
  q.x = abs (q.x);
  q.xy = Rot2Cs (q.xy, sin (drpWt + vec2 (0.5 * pi, 0.)));
  q -= vec3 (2.7, 0.8, -6.9);
  t = ww.y * (1. - 0.25 * abs (q.y) / ww.x);
  s = (q.z + 0.1 * abs (q.y) / ww.x - 0.2) / ww.y;
  d = SmoothMax (PrCaps2Df (vec2 (q.x, q.z + 0.1 * abs (q.y) / ww.x), 0.1 * (t - s * s), t),
     abs (q.y - 0.35 * ww.x) - 0.65 * ww.x, 0.06);
  d = SmoothMax (d, - abs (PrBox2Df (vec2 (q.y - 0.8, q.z + 0.3), vec2 (0.5, 0.2))), 0.03);
  DMINQ (idFin);
  q.z -= -0.3;
  d = PrCapsDf (q, 0.05, 0.2);
  DMINQ (idFr);
  if (wlDown) {
    q = p;
    q.yz -= vec2 (-1., 0.4);
    t = 0.45;
    s = abs (q.z) - 3.5 * t;
    q.z = mod (q.z + 0.5 * t, t) - 0.5 * t;
    q.x = abs (q.x) - 0.5;
    d = max (PrRoundCylDf (vec3 (q.yz, abs (q.x) - 0.09), 0.16, 0.03, 0.02), s);
    DMINQ (idWhl);
    d = max (min (PrCylDf (q.yzx, 0.05, 0.09), PrCylDf (vec3 (q.xz, q.y - 0.2), 0.03, 0.2)), s);
    DMINQ (idLeg);
    q = p;
    q.x = abs (q.x);
    q -= vec3 (0.2, -1.06, 4.8);
    d = PrRoundCylDf (vec3 (q.yz, abs (q.x) - 0.07), 0.1, 0.03, 0.01);
    DMINQ (idWhl);
    d = min (PrCylDf (q.yzx, 0.05, 0.07), PrCylDf (vec3 (q.xz, q.y - 0.2), 0.03, 0.2));
    DMINQ (idLeg);
  }
  return 0.6 * flSzFac * dMin;
}

float FlyerRay (vec3 ro, vec3 rd)
{
  float dHit, d;
  dHit = 0.;
  for (int j = VAR_ZERO; j < 220; j ++) {
    d = FlyerDf (ro + dHit * rd);
    dHit += d;
    if (d < 0.0002 || dHit > dstFar) break;
  }
  return dHit;
}

vec3 FlyerNf (vec3 p)
{
  vec4 v;
  vec2 e = vec2 (0.0001, -0.0001);
  for (int j = VAR_ZERO; j < 4; j ++) {
    v[j] = FlyerDf (p + ((j < 2) ? ((j == 0) ? e.xxx : e.xyy) : ((j == 2) ? e.yxy : e.yyx)));
  }
  v.x = - v.x;
  return normalize (2. * v.yzw - dot (v, vec4 (1.)));
}

float FlyerSShadow (vec3 ro, vec3 rd)
{
  float sh, d, h;
  sh = 1.;
  d = 0.005;
  for (int j = VAR_ZERO; j < 30; j ++) {
    h = FlyerDf (ro + d * rd);
    sh = min (sh, smoothstep (0., 0.05 * d, h));
    d += h;
    if (sh < 0.05) break;
  }
  return 0.6 + 0.4 * sh;
}

void SetBldgParms ()
{ //  (based on "Urban Growth")
  float hiMid;
  if (Hashfv2 (16. * cId) > 0.5 || mod (cId.x, 4.) + mod (cId.y, 4.) != 0.) {
    bHt = (0.25 * Hashfv2 (13. * cId) + 0.05) * (1. + 0.15 / flrHt) + 0.1;
    nFloor = floor (bHt * (0.6 + 0.4 * length (sin (0.125 * pi * cId))) / flrHt);
    bHt = (nFloor + 0.3) * flrHt;
    bWid = floor ((0.17 + Hashfv2 (11. * cId) * 0.1) / flrHt + 0.5) * flrHt;
    bShape = Hashfv2 (17. * cId);
    cIdT = mod (cId + 8., vec2 (16.)) - 8.;
    hiMid = 0.75 * clamp (4. / max (length (cIdT), 1.), 0., 1.);
    bWidU = floor (bWid * (0.5 + 0.3 * Hashfv2 (12. * cId)) / flrHt + 0.5) * flrHt;
    bHt = (0.5 * Hashfv2 (13. * cId) + 0.05) * hiMid * (1.5 + (bWid - 0.15) / flrHt) + 0.1;
    bHtU = 0.25 * bHt + 0.75 * max (0., Hashfv2 (15. * cId) - 0.5) * hiMid + 0.05;
    bHt = (floor (bHt / flrHt) + 0.2) * flrHt;
    bHtU = floor (bHtU / flrHt) * flrHt;
    bHtU += 0.2 * flrHt * step (0., bHtU);
    if (cIdT == vec2 (0.) && length (cId) > 40.) {
      bHt = max (bHt, 30.2 * flrHt);
      bHtU = max (bHtU, 10.2 * flrHt);
    }
    tWid = ((bHtU > 0.) ? bWidU : bWid) - 0.0125;
  } else {
    bHt = 0.;
  }
}

float BldgDf (vec3 p, float dMin)
{
  vec3 pp, q;
  vec2 s;
  float d;
  q = p;
  d = q.y;
  DMINQ (idRoad);
  pp = p;
  pp.xz = fract (pp.xz) - vec2 (0.5);
  q = pp;
  q.y -= 0.0015;
  d = PrBoxDf (q, vec3 (0.35, 0.0015, 0.35));
  DMINQ (idSWalk);
  if (bHt > 0.) {
    q.y -= bHt - 0.2 * flrHt + 0.0005;
    if (bShape > 0.25) {
      d = PrBoxDf (q, vec3 (bWid, bHt, bWid));
      DMINQ (idBldgF);
    } else {
      d = PrCylDf (q.xzy, bWid, bHt);
      DMINQ (idBldgC);
    }
    q.y -= bHt + bHtU - 0.2 * flrHt - 0.001;
    if (bHtU > 0.) {
      if (bShape > 0.5) {
        d = max (PrBoxDf (q, vec3 (bWidU, bHtU, bWidU)),
           - PrBoxDf (q - vec3 (0., bHtU, 0.), vec3 (tWid, 0.1 * flrHt, tWid)));
        DMINQ (idBldgF);
      } else {
        d = max (PrCylDf (q.xzy, bWidU, bHtU),
           - PrCylDf ((q - vec3 (0., bHtU, 0.)).xzy, tWid, 0.1 * flrHt));
        DMINQ (idBldgC);
      }
    }
    q.y -= bHtU - 0.2 * flrHt - 0.001;
    if (bShape < 0.1) {
      d = PrCapsDf (q.xzy, 0.4 * bWidU, 1.25 * flrHt);
      DMINQ (idBldgC);
    } else if (bShape > 0.7) {
      d = PrBoxDf (q, vec3 (0.25 * bWidU, 1.25 * flrHt, 0.25 * bWidU));
      DMINQ (idBldgF);
    }
    if (bHt + bHtU > 30. * flrHt) {
      twHt = 0.1 * (bHt + bHtU);
      q.y -= twHt;
      d = PrCapsDf (q.xzy, 0.3 * flrHt, twHt);
      DMINQ (((q.y > 0.9 * twHt) ? idTwrTop : idTwr));
    }
    if (cIdT == vec2 (0.)) {
      q = pp;
      q.y -= 2. * (bHt + bHtU) + 0.2 * flrHt;
      d = PrCylDf (q.xzy, 0.3, flrHt);
      DMINQ (idBldgC);
    }
  } else {
    d = PrCylDf (q.xzy, 0.19, 0.005);
    DMINQ (idPool);
    q.xz = abs (q.xz) - 0.21;
    q.y -= 0.7 * flrHt;
    d = PrCylDf (q.xzy, 0.005, 0.7 * flrHt);
    DMINQ (idTrnk);
    q.y -= 0.6 * flrHt;
    d = PrSphDf (q, 0.4 * flrHt);
    DMINQ (idTree);
  }
  q = pp;
  q.y -= 0.025;
  q.xz = mod (q.xz + 0.05, 0.1) - 0.05;
  s = abs (pp.xz) - vec2 (0.5);
  d = max (min (PrCylDf (q.xzy, 0.0012, 0.025), PrCylDf ((q - vec3 (0., 0.025, 0.)).xzy, 0.005, 0.0007)),
     - min (PrBox2Df (pp.xz, vec2 (0.45)), max (PrBox2Df (s, vec2 (0.18)), - PrBox2Df (s, vec2 (0.05)))));
  DMINQ (idStLight);
  q = pp;
  q.y -= 0.023;
  q.xz = abs (q.xz) - vec2 (0.345);
  d = PrCylDf (q.xzy, 0.002, 0.02);
  DMINQ (idTrLight);
  return dMin;
}

vec3 CarPos (vec3 p)
{ // (noncolliding traffic flow from "Gotham City")
  vec3 q, s;
  float vDir, fqx;
  s.xz = abs (fract (p.xz) - 0.5) - 0.35;
  vDir = (cDir == 0. && s.z > 0. || cDir == 1. && s.x < 0.) ? 0. : 1.;
  if (vDir == 0.) p.xz = vec2 (- p.z, p.x);
  q = p;
  fqx = floor (q.x);
  q.x -= fqx + 0.5;
  q.z += 3. * fqx;
  q.z *= sign (q.x);
  q.z -= cTimeV.x + ((cDir == vDir) ? vDir + cTimeV.y : 1.);
  cCar = floor (20. * q.z);
  q.z = fract (q.z) - 0.5;
  q.x = abs (q.x) - 0.395 - 0.06 * (step (0.6, Hashff (11. * cCar)) + 0.5 * Hashff (13. * cCar));
  return q;
}

float CarDf (vec3 p, float dMin)
{  // (simplified version from "Truck Driving")
  vec3 q, qq, b;
  float d, h, bf, szFac;
  szFac = 0.06;
  q = CarPos (p);
  bf = PrBox2Df (q.xz + vec2 (0., -0.1), vec2 (0.015, 0.2));
  q.z = mod (q.z, 0.05) - 0.025;
  dMin /= szFac;
  q /= szFac;
  q.y -= 0.15;
  b = vec3 (0.13, 0.09, 0.3);
  qq = q;
  b.x -= 0.2 * abs (qq.y + 0.02 * qq.z + 0.01);
  h = ((qq.z > 0.05) ? 0.21 : 0.13) * abs (qq.z - 0.05);
  qq.y -= - h;
  b.yz -= vec2 (h, 0.1 * abs (qq.y - 0.03));
  d = max (PrBoxDf (qq, b), - PrBoxDf (q - vec3 (0., 0.01, -0.2), vec3 (0.115, 0.05, 0.09)));
  qq = q;
  qq.xz = abs (qq.xz) - vec2 (0.11, 0.2);
  h = 0.15 * abs (qq.z);
  qq.y += 0.08 + h;
  d = max (d, - PrBoxDf (qq, vec3 (0.015, 0.04, 0.06) - vec3 (0., h, 0.06 * qq.y)));
  d = max (d, bf);
  if (d < dMin) qcCar = cCar;
  DMINQ (idCarBdy);
  qq = q;
  qq.y -= -0.088;
  d = PrBoxDf (qq, vec3 (0.13, 0.003, 0.14));
  d = max (d, bf);
  if (d < dMin) qcCar = cCar;
  DMINQ (idCarBdy);
  qq = vec3 (abs (q.xz) - vec2 (0.118, 0.2), q.y + 0.095).xzy;
  d = PrRoundCylDf (qq.yzx, 0.048, 0.007, 0.02);
  d = max (d, bf);
  DMINQ (idCarWhl);
  return 0.7 * szFac * dMin;
}

vec4 CarCol ()
{
  vec4 col4;
  float z, r, s;
  col4 = vec4 (HsvToRgb (vec3 (Hashff (qcCar * 37.), 0.5,
     0.4 + 0.6 * vec3 (Hashff (qcCar * 47.)))), 0.5);
  if (idObj == idCarBdy) {
    z = abs (qHit.z - 0.05);
    if (qHit.y > 0.015 && abs (z - 0.078) < 0.072 && (abs (qHit.x) < 0.12 - 0.2 * qHit.y ||
       qHit.y < 0.085 - 2. * ((qHit.z > 0.05) ? 0.21 : 0.13) * z)) col4 = vec4 (0., 0., 0., -2.);
    else if (qHit.z < - 0.298 && abs (qHit.y + 0.01) < 0.005 && abs (qHit.x) < 0.125)
       col4 = vec4 (1., 0., 0., -1.);
    else if (qHit.z > 0.298 && abs (qHit.y + 0.02) < 0.005 && abs (qHit.x) < 0.125)
       col4 = vec4 (1., 1., 0.8, -1.);
    else if (qHit.y < - 0.075 || abs (qHit.y) < 0.085 && z < 0.0008 || 
       abs (qHit.y + 0.009) < 0.031 && abs (qHit.z - 0.201) < 0.0008 ||
       abs (qHit.y + 0.015) < 0.061 && abs (qHit.z + 0.101) < 0.0008) col4.rgb *= 0.6;
    else if (qHit.z > 0.28 && abs (qHit.y + 0.05) < 0.015 && abs (qHit.x) < 0.08)
       col4.rgb *= 1. - 0.3 * step (0.2, cos (80. * pi * qHit.x));
    else if (qHit.y > -0.05 && abs (qHit.x) < 0.11 && abs (qHit.z + 0.2) < 0.085)
       col4.rgb *= 0.9 + 0.1 * cos (160. * pi * qHit.x);
  } else if (idObj == idCarWhl) {
    s = sign (qHit.z);
    qHit = vec3 (abs (qHit.xz) - vec2 (0.118, 0.2), qHit.y + 0.095).xzy;
    r = length (qHit.yz);
    if (r < 0.02) {
      qHit.yz = Rot2D (qHit.yz, - pi * tCur * s);
      if (abs (abs (qHit.y) - abs (qHit.z)) < 0.01) col4 = vec4 (0.9, 0.9, 1., 0.3);
    } else if (r > 0.03) col4 = vec4 (0.3, 0.3, 0.3, 0.);
  }
  return col4;
}

float ObjDf (vec3 p)
{
  float dMin;
  dMin = dstFar;
  dMin = BldgDf (p, dMin);
  dMin = CarDf (p, dMin);
  return dMin;
}

float ObjRay (vec3 ro, vec3 rd)
{
  vec3 p;
  vec2 cIdP, rdi;
  float dHit, d;
  if (rd.x == 0.) rd.x = 0.0001;
  if (rd.y == 0.) rd.y = 0.0001;
  if (rd.z == 0.) rd.z = 0.0001;
  rdi = 1. / rd.xz;
  cIdP = vec2 (-999.);
  dHit = 0.01;
  for (int j = VAR_ZERO; j < 220; j ++) {
    p = ro + dHit * rd;
    cId = floor (p.xz);
    if (cId != cIdP) {
      cIdP = cId;
      SetBldgParms ();
    }
    d = ObjDf (p);
    dHit += min (d, 0.001 + max (0., Minv2 ((cId + step (0., rd.xz) - p.xz) * rdi.xy)));
    if (d < 0.0002 || dHit > dstFar) break;
  }
  return dHit;
}

vec3 ObjNf (vec3 p)
{
  vec4 v;
  vec2 e;
  e = vec2 (0.0001, -0.0001);
  for (int j = VAR_ZERO; j < 4; j ++) {
    v[j] = ObjDf (p + ((j < 2) ? ((j == 0) ? e.xxx : e.xyy) : ((j == 2) ? e.yxy : e.yyx)));
  }
  v.x = - v.x;
  return normalize (2. * v.yzw - dot (v, vec4 (1.)));
}

float ObjSShadow (vec3 ro, vec3 rd)
{
  vec3 p, qHitT;
  vec2 cIdP;
  float sh, d, h;
  int idObjT;
  idObjT = idObj;
  qHitT = qHit;
  sh = 1.;
  cIdP = vec2 (-999.);
  d = 0.02;
  for (int j = VAR_ZERO; j < 40; j ++) {
    p = ro + d * rd;
    cId = floor (p.xz);
    if (cId != cIdP) {
      cIdP = cId;
      SetBldgParms ();
    }
    h = BldgDf (p, dstFar);
    sh = min (sh, smoothstep (0., 0.05 * d, h));
    d += min (0.05, 3. * h);
    if (h < 0.001) break;
  }
  idObj = idObjT;
  qHit = qHitT;
  return 0.6 + 0.4 * sh;
}

vec4 BldgCol (vec3 vn)
{
  vec4 col4;
  vec2 g;
  float wFac, f;
  wFac = 1.;
  col4 = vec4 (HsvToRgb (vec3 (0.7 * Hashfv2 (19. * cId), 0.2,
     0.4 + 0.2 * Hashfv2 (21. * cId))), 0.3);
  if (abs (vn.y) < 0.05) {
    f = mod (qHit.y / flrHt - 0.2, 1.) - 0.5;
    wFac = 1. - (step (0., f) - 0.5) * step (abs (abs (f) - 0.24), 0.02) -
       0.801 * step (abs (f), 0.22);
    if (wFac < 0.2) {
      f = (idObj == idBldgF) ? 1.5 * dot (qHit.xz, normalize (vn.zx)) :
         length (qHit.xz) * (atan (qHit.z, qHit.x) + 0.5 * pi);
      wFac = min (0.2 + 0.8 * floor (fract (f / flrHt + 0.25) *
         (1. + Hashfv2 (51. * cId))), 1.);
    }
    col4.rgb *= wFac;
  } else if (vn.y > 0.95) {
    g = step (0.05, fract (qHit.xz * 70.));
    col4.rgb *= mix (0.8, 1., g.x * g.y);
  }
  if (vn.y < 0.95 && wFac > 0.5)
     col4.rgb *= (0.8 + 0.2 * Noisefv2 (512. * vec2 (qHit.x + qHit.z, qHit.y)));
  if (wFac < 0.5) col4.a = -2.;
  return col4;
}

vec4 ObjCol (vec3 ro, vec3 rd, vec3 vn)
{
  vec4 col4;
  vec2 g, b;
  float f, ff, y, fDayLt;
  col4 = vec4 (0.);
  fDayLt = 1.;
  if (idObj == idBldgF || idObj == idBldgC) {
    col4 = BldgCol (vn);
  } else if (idObj == idTwr) {
    col4 = vec4 (0.3);
  } else if (idObj == idTwrTop) {
    col4 = vec4 (1., 0., 0., -2.);
  } else if (idObj == idRoad) {
    g = abs (fract (qHit.xz) - 0.5);
    if (g.x < g.y) g = g.yx;
    col4.rgb = mix (vec3 (0.1, 0.1, 0.12), vec3 (0.1), step (g.x, 0.355));
    f = ((step (abs (g.x - 0.495), 0.002) + step (abs (g.x - 0.365), 0.002)) +
       step (abs (g.x - 0.44), 0.0015) * step (fract (g.y * 18. + 0.25), 0.7)) *
       step (g.y, 0.29);
    col4.rgb = mix (col4.rgb, vec3 (0.6, 0.5, 0.1), f);
    f = step (0.6, fract (g.x * 30. + 0.25)) * step (0.36, g.x) * step (abs (g.y - 0.32), 0.02);
    col4 = mix (col4, vec4 (0.7, 0.7, 0.7, 0.2), f);
    g = abs (g) - 0.493;
    f = step (0., Minv2 (g));
    col4 = mix (col4, vec4 (0.7, 0.7, 0.7, 0.2), f);
    if (fDayLt > 0.5) {
      b = CarPos (ro).xz;
      g = abs (b + vec2 (0., -0.1)) - vec2 (0.015, 0.2);
      if (Maxv2 (g) < 0.) col4 *= 0.6 + 0.4 * smoothstep (0.001, 0.003,
         length (max (abs (vec2 (b.x, mod (b.y, 0.05) - 0.025)) - 0.06 * vec2 (0.13, 0.3), 0.)));
    } else {
      g = abs (fract (qHit.xz) - 0.5);
      f = 1.3 * (1. - smoothstep (0.05, 0.2, length (g - 0.5)));
      if (g.x < g.y) g = g.yx;
      f += step (g.y, 0.35) * (1. - smoothstep (0.02, 0.05,
         length ((vec2 (0.8 * abs (g.x - 0.437) - 0.015, mod (g.y + 0.05, 0.1) - 0.05)))));
      col4.rgb = mix (col4.rgb, vec3 (1., 1., 0.7), 0.2 * f);
    }
  } else if (idObj == idSWalk) {
    g = step (0.05, fract (qHit.xz * 35.));
    col4.rgb = vec3 (0.2) * mix (0.7, 1., g.x * g.y);
    if (bHt > 0.) {
      f = ((bShape > 0.25) ? max (abs (qHit.x), abs (qHit.z)) : length (qHit.xz)) - bWid;
      if (f < 0.02) col4.rgb *= 2.;
      else if (f < 0.025) col4 = vec4 (vec3 (1., 1., 0.7) * max (0.5 - 0.5 * rd.y, 0.), -1.);
    } else {
      if (length (abs (qHit.xz) - 0.21) < 0.01) col4.rgb *= 0.7;
    }
  } else if (idObj == idTrLight) {
    f = 2. * (atan (qHit.z, qHit.x) / pi + 1.) + 0.5;
    ff = floor (f);
    y = qHit.y - 0.014;
    if (abs (y) < 0.004 && abs (f - ff) > 0.3) {
      col4 = vec4 (mix (vec3 (0., 1., 0.), vec3 (1., 0., 0.), step (0., y)), -1.);
      col4.rgb *= 1. - 0.8 * step (0., y * sign (cDir - 0.5) * sign (mod (ff, 2.) - 0.5));
    } else {
      col4 = vec4 (0.4, 0.2, 0.1, 0.5);
    }
  } else if (idObj == idStLight) {
    if (qHit.y > 0.024 && vn.y < 0.1) {
      col4 = vec4 (vec3 (0.8, 0.8, 0.4) * (0.7 - 0.3 * vn.y), -1.);
    } else {
      col4 = vec4 (vec3 (0.5, 0.5, 0.55) * (1. + 0.3 * sin (128. * 2. * pi * qHit.y)), 0.5);
    }
  } else if (idObj == idPool) {
    col4 = (length (qHit.xz) < 0.18) ? vec4 (0.4, 0.4, 0.5, -2.) :
       vec4 (0.2, 0.3, 0.5, 0.1) * (0.6 + 0.4 * fDayLt);
  } else if (idObj == idTrnk) {
    col4 = vec4 (0.4, 0.2, 0., 0.1);
  } else if (idObj == idTree) {
    col4 = vec4 (0., 0.5, 0.1, 0.1);
  } else if (idObj == idCarBdy || idObj == idCarWhl) {
    col4 = CarCol ();
    if (idObj == idCarBdy && fDayLt < 0.5 && col4.a > 0.) {
      g = abs (fract (ro.xz) - 0.5);
      f = 1.3 * (1. - smoothstep (0.05, 0.2, length (g - 0.5)));
      if (g.x < g.y) g = g.yx;
      f += step (g.y, 0.35) * (1. - smoothstep (0.02, 0.05, length ((vec2 (0.8 *
         abs (g.x - 0.437) - 0.015, mod (g.y + 0.05, 0.1) - 0.05)))));
      col4.rgb = mix (col4.rgb, vec3 (0.7, 0.7, 0.5), 0.4 * f);
    }
  }
  return col4;
}

vec4 FlyerCol ()
{
  vec4 col4, colY4, colB4;
  vec2 s;
  float t;
  col4 = vec4 (1., 1., 0.95, 0.2);
  colY4 = vec4 (0.8, 0.8, 0., 0.2);
  colB4 = vec4 (0.2, 0.2, 1., 0.2);
  if (idObj == idFus) {
    col4 = mix (colB4, col4, smoothstep (0.07, 0.08, abs (qHit.y + 0.6)));
    col4 = mix (colY4, col4, smoothstep (0.07, 0.08, abs (qHit.y + 0.35)));
    if (PrCylDf (qHit - vec3 (0., 0., 0.4), 0.98, 1.7) < 0. ||
       abs (qHit.x) < 0.4 && qHit.y < 0. && PrCylDf (qHit - vec3 (0., 0., 4.7), 0.93, 0.3) < 0.)
       col4 *= vec4 (vec3 (0.2), 0.);
    if (! wlDown && qHit.y < 0.) col4 *= 0.7 + 0.3 * smoothstep (0.008, 0.012, 
       min (abs (PrBox2Df (vec2 (abs (abs (qHit.x) - 0.45) - 0.1, qHit.z - 0.4), vec2 (0.1, 1.6))),
       abs (PrBox2Df (vec2 (abs (abs (qHit.x) - 0.2) - 0.05, qHit.z - 4.8), vec2 (0.05, 0.2)))));
    col4 *= 0.7 + 0.3 * smoothstep (0.008, 0.01, abs (PrRoundBox2Df (qHit.yz - vec2 (-0.4, 3.5),
       vec2 (0.3, 0.15), 0.05)));
  } else if (idObj == idCkp) {
    if (qHit.z > 0.4 && abs (qHit.y - 0.45) < 0.07 && abs (abs (qHit.x) - 0.2) > 0.03 &&
       abs (abs (qHit.z - 1.35) - 0.2) > 0.03) col4 = vec4 (0., 0., 0., -1.);
  } else if (idObj == idEng) {
    t = length (qHit.xy);
    if (PrCapsDf (qHit, 0.3 * (1. - 0.5 * qHit.z * qHit.z), 0.85) < 0.) {
      if (qHit.z > 0. && t < 0.14) col4 = vec4 (1., 0., 0., 0.2);
      else col4 *= vec4 (vec3 (0.3), 0.);
    } else {
      if (abs (qHit.z + 0.1) > 0.5) col4 = colY4;
      else col4 = mix (colB4, col4, smoothstep (0.02, 0.03, abs (qHit.z - 0.3)));
    }
  } else if (idObj == idWngM) {
    t = qHit.z + 0.1 * abs (qHit.x);
    col4 = mix (colB4, col4, smoothstep (0.04, 0.05, abs (t - 1.2)));
    col4 = mix (colY4, col4, smoothstep (0.04, 0.05, abs (t - 1.)));
    if (qHit.y < 0.) {
      if (abs (abs (qHit.x) - 3.9) < 2.1) col4 *= 0.7 + 0.3 *
         smoothstep (0.008, 0.012, abs (qHit.z - 0.1 * abs (qHit.x) + 1.));
    } else {
      s = Rot2D (qHit.xz - vec2 (3.5, -0.3), -0.1 * pi);
      s.x = abs (abs (s.x) - 0.8) - 0.3;
      col4 *= 0.7 + 0.3 * smoothstep (0.008, 0.012, abs (PrBox2Df (s, vec2 (0.3, 0.15))));
    }
  } else if (idObj == idWngT) {
    t = qHit.z + 0.15 * abs (qHit.x);
    col4 = mix (colB4, col4, smoothstep (0.02, 0.03, abs (t - 0.7)));
    col4 = mix (colY4, col4, smoothstep (0.02, 0.03, abs (t - 0.6)));
  } else if (idObj == idFin) {
    t = qHit.z + 0.15 * abs (qHit.y);
    col4 = mix (colB4, col4, smoothstep (0.02, 0.03, abs (t - 0.4)));
    col4 = mix (colY4, col4, smoothstep (0.02, 0.03, abs (t - 0.3)));
  } else if (idObj == idWhl) {
    col4 = mix (vec4 (0.7, 0.7, 0.75, 0.1), vec4 (0.3, 0.3, 0.3, 0.),
       step (0.07, length (qHit.yz)));
  } else if (idObj == idLeg) {
     col4 = vec4 (0.8, 0.8, 0.85, 0.1);
  }
  return col4;
}

vec3 BgCol (vec3 ro, vec3 rd)
{
  vec3 col, skCol, clCol;
  vec2 p;
  float fd, d, f, ff;
  rd = normalize (rd);
  col = mistCol;
  if (rd.y >= 0.) {
    p = 0.01 * (ro.xz + rd.xz * (200. - ro.y) / max (rd.y, 0.0001) + 4. * tCur);
    ff = Fbm2 (p);
    f = smoothstep (0.2, 0.8, ff);
    fd = smoothstep (0.2, 0.8, Fbm2 (p + 0.01 * sunDir.xz)) - f;
    clCol = (0.7 + 0.5 * ff) * (vec3 (0.7) - 0.7 * vec3 (0.3, 0.3, 0.2) * sign (fd) *
       smoothstep (0., 0.05, abs (fd)));
    col = mix (mistCol, clCol, 0.1 + 0.9 * f * smoothstep (0.01, 0.1, rd.y));
  }
  return col;
}

void FlyerParms ()
{
  angWm = 0.1 * pi;
  drpWm = 0.02 * pi;
  csWm = vec4 (sin (drpWm + vec2 (0.5 * pi, 0.)), sin (angWm + vec2 (0.5 * pi, 0.)));
  angWt = 0.12 * pi;
  drpWt = -0.03 * pi;
  csWt = vec4 (sin (drpWt + vec2 (0.5 * pi, 0.)), sin (angWt + vec2 (0.5 * pi, 0.)));
  dwr = vec3 (1.4, 0., 0.);
  dwr.xy = Rot2Cs (dwr.xy, sin (- drpWm + vec2 (0.5 * pi, 0.)));
  dwr.xz = Rot2Cs (dwr.xz, sin (-0.7 * angWm + vec2 (0.5 * pi, 0.)));
  dwf = vec3 (1.5, 0., 0.);
  dwf.xy = Rot2Cs (dwf.xy, sin (-0.9 * drpWm + vec2 (0.5 * pi, 0.)));
  dwf.xz = Rot2Cs (dwf.xz, sin (-1.3 * angWm + vec2 (0.5 * pi, 0.)));
  wlDown = (mod (0.05 * tCur, 2.) > 1.);
}

vec3 ShowScene (vec3 ro, vec3 rd)
{
  vec4 col4;
  vec3 col, vn, rdr, roo, qHitF;
  vec2 g, vf;
  float dstObj, dstFlyer, nDotL, sh, s, f;
  int idObjF;
  FlyerParms ();
  flrHt = 0.07;
  vf = vec2 (0.);
  mistCol = 0.3 + 0.8 * vec3 (0.4, 0.5, 0.8);
  dstFlyer = FlyerRay (ro, rd);
  idObjF = idObj;
  qHitF = qHit;
  roo = ro;
  dstObj = ObjRay (ro, rd);
  if (dstObj < min (dstFlyer, dstFar)) {
    ro += dstObj * rd;
    cId = floor (ro.xz);
    SetBldgParms ();
    vn = ObjNf (ro);
    col4 = ObjCol (ro, rd, vn);
    col = col4.rgb;
    if (col4.a >= 0.) {
      if (idObj == idRoad) vf = vec2 (512., 1.);
      else if (idObj == idBldgF || idObj == idBldgC) vf = vec2 (512., 0.5);
      else if (idObj == idTrnk) vf = vec2 (256., 0.5);
      else if (idObj == idTree) vf = vec2 (128., 2.);
      else if (idObj == idSWalk && bHt == 0.) {
        s = length (qHit.xz) - 0.2;
        vn = normalize (vec3 ((1. - smoothstep (0., 0.02,
           abs (s))) * sign (s) * normalize (qHit.xz), 2.)).xzy;
      }
      if (vf.x > 0.) vn = VaryNf (vf.x * qHit, vn, vf.y);
      sh = ObjSShadow (ro + 0.01 * vn, sunDir);
      col = col * (0.3 + 0.3 * max (dot (vn.xz, - normalize (sunDir.xz)), 0.) +
         0.2 * max (vn.y, 0.) + 0.8 * sh * max (dot (vn, sunDir), 0.)) +
         step (0.95, sh) * col4.a * pow (max (dot (reflect (sunDir, vn), rd), 0.), 32.);
    }
    if (col4.a == -2.) {
      rdr = reflect (rd, vn);
      g = Rot2D (rdr.xz, 5.1 * atan (20. + cId.y, 20. + cId.x));
      f = step (1., 0.3 * ro.y + 4. * rdr.y -
         0.1 * floor (5. * IFbm1 (0.6 * atan (g.y, g.x) + pi) + 0.05));
      if (idObj == idBldgF || idObj == idBldgC) {
        if (f == 1.) col = mix (0.3 * col, BgCol (ro, rdr), 0.5);
        else col *= 0.3;
      } else if (idObj == idPool) {
        if (f == 1.) col = 0.9 * BgCol (ro, rdr);
        else col *= 0.3;
      } else if (idObj == idCarBdy) {
        if (f == 1.) col = mix (0.3 * col, BgCol (ro, rdr), 0.5);
        else col *= 0.3;
      }
    }
    if (col4.a == -2.) col *= 1. - 0.95 * smoothstep (0.4, 0.7, dstObj / dstFar);
    col = mix (mistCol, col, exp2 (min (0., 7. * (1. - 2. * dstObj / dstFar))));
  } else if (dstFlyer < dstFar) {
    idObj = idObjF;
    qHit = qHitF;
    ro += dstFlyer * rd;
    vn = FlyerNf (ro);
    col4 = FlyerCol ();
    sh = FlyerSShadow (ro + 0.01 * vn, sunDir);
    if (col4.a >= 0.) {
      nDotL = max (dot (vn, sunDir), 0.);
      if (col4.a > 0.) nDotL *= nDotL;
      col = col4.rgb * (0.2 + 0.1 * max (- dot (vn, sunDir), 0.) + 0.1 * max (- vn.y, 0.) +
         0.8 * sh * nDotL) + col4.a * step (0.95, sh) * pow (max (dot (sunDir,
         reflect (rd, vn)), 0.), 32.);
    } else col = BgCol (ro, reflect (rd, vn));
  } else col = (rd.y < 0.) ? mistCol : BgCol (ro, rd);
  return clamp (col, 0., 1.);
}

vec3 TrkPos (float t)
{
  vec3 r;
  vec2 cs;
  t *= 0.05 / (2. * pi);
  t = mod (t, 1.);
  cs = sin (2. * pi * t + vec2 (0.5 * pi, 0.));
  r.xz = vec2 (1., cs.y);
  r.xz = 40. * cs.x * r.xz / dot (r.xz, r.xz);
  r.y = 5. + 0.4 * sin (2. * pi * t);
  return r;
}

void FlyerPM (float t, out vec3 flPos, out mat3 flMat)
{
  vec3 vel, acc, va, flVd, fpF, fpB;
  vec2 cs;
  float oRl, dt;
  dt = 1.;
  flPos = TrkPos (t);
  fpF = TrkPos (t + dt);
  fpB = TrkPos (t - dt);
  vel = (fpF - fpB) / (2. * dt);
  acc = (fpF - 2. * flPos + fpB) / (dt * dt);
  va = cross (acc, vel) / length (vel);
  flVd = normalize (vel);
  oRl = 2. * length (va) * sign (va.y);
  oRl = smoothstep (0.01, 0.03, abs (oRl)) * oRl;
  cs = sin (oRl + vec2 (0.5 * pi, 0.));
  flMat = mat3 (cs.x, - cs.y, 0., cs.y, cs.x, 0., 0., 0., 1.) *
     mat3 (flVd.z, 0., flVd.x, 0., 1., 0., - flVd.x, 0., flVd.z);
}

void mainImage (out vec4 fragColor, in vec2 fragCoord)
{
  mat3 vuMat;
  vec4 mPtr;
  vec3 ro, rd, col, vuPos, vd;
  vec2 canvas, uv, uvv;
  float zmFac, el, az, asp, sr, cTime, flyVel, tPos;
  canvas = iResolution.xy;
  uv = 2. * fragCoord.xy / canvas - 1.;
  uv.x *= canvas.x / canvas.y;
  tCur = iTime;
  mPtr = iMouse;
  mPtr.xy = mPtr.xy / canvas - 0.5;
  asp = canvas.x / canvas.y;
  flSzFac = 0.07;
  flyVel = 1.2;
  tPos = flyVel * tCur;
  FlyerPM (tPos, flyerPos, flyerMat);
  vuPos = TrkPos (tPos - 2.);
  vuPos.y += 1. + 2. * sin (0.2 * tCur);
  vuPos.xz = Rot2D (vuPos.xz - flyerPos.xz, 0.05 * tCur) + flyerPos.xz;
  zmFac = 6.;
  vd = normalize (flyerPos - vuPos);
  az = 0.5 * pi + atan (- vd.z, vd.x);
  el = asin (vd.y);
  if (mPtr.z > 0.) {
    az += 0.3 * pi * mPtr.x;
    el += 0.2 * pi * mPtr.y;
  }
  vuMat = StdVuMat (el, az);
  sunDir = normalize (vec3 (1., 2., -1.));
  cTime = 0.15 * tCur;
  cDir = mod (floor (cTime), 2.);
  cTimeV = vec2 (floor (0.5 * floor (cTime)), fract (cTime));
  dstFar = 60.;
#if ! AA
  const float naa = 1.;
#else
  const float naa = 3.;
#endif  
  col = vec3 (0.);
  sr = 2. * mod (dot (mod (floor (0.5 * (uv + 1.) * canvas), 2.), vec2 (1.)), 2.) - 1.;
  for (float a = float (VAR_ZERO); a < naa; a ++) {
    uvv = uv + step (1.5, naa) * Rot2D (vec2 (0.5 / canvas.y, 0.), sr * (0.667 * a + 0.5) * pi);
    uvv /= vec2 (asp, 1.) * zmFac;
    rd = vuMat * normalize (vec3 (2. * tan (0.5 * atan (uvv.x)) * asp, uvv.y, 1.));
    col += (1. / naa) * ShowScene (vuPos, rd);
  }
  fragColor = vec4 (col, 1.);
}

float PrBoxDf (vec3 p, vec3 b)
{
  vec3 d;
  d = abs (p) - b;
  return min (max (d.x, max (d.y, d.z)), 0.) + length (max (d, 0.));
}

float PrBox2Df (vec2 p, vec2 b)
{
  vec2 d;
  d = abs (p) - b;
  return min (max (d.x, d.y), 0.) + length (max (d, 0.));
}

float PrRoundBox2Df (vec2 p, vec2 b, float r)
{
  return length (max (abs (p) - b, 0.)) - r;
}

float PrSphDf (vec3 p, float r)
{
  return length (p) - r;
}

float PrCylDf (vec3 p, float r, float h)
{
  return max (length (p.xy) - r, abs (p.z) - h);
}

float PrRoundCylDf (vec3 p, float r, float rt, float h)
{
  return length (max (vec2 (length (p.xy) - r, abs (p.z) - h), 0.)) - rt;
}

float PrCapsDf (vec3 p, float r, float h)
{
  return length (p - vec3 (0., 0., clamp (p.z, - h, h))) - r;
}

float PrCaps2Df (vec2 p, float r, float h)
{
  return length (p - vec2 (0., clamp (p.y, - h, h))) - r;
}

float Minv2 (vec2 p)
{
  return min (p.x, p.y);
}

float Maxv2 (vec2 p)
{
  return max (p.x, p.y);
}

float SmoothMin (float a, float b, float r)
{
  float h;
  h = clamp (0.5 + 0.5 * (b - a) / r, 0., 1.);
  return mix (b - h * r, a, h);
}

float SmoothMax (float a, float b, float r)
{
  return - SmoothMin (- a, - b, r);
}

float SmoothBump (float lo, float hi, float w, float x)
{
  return (1. - smoothstep (hi - w, hi + w, x)) * smoothstep (lo - w, lo + w, x);
}

mat3 StdVuMat (float el, float az)
{
  vec2 ori, ca, sa;
  ori = vec2 (el, az);
  ca = cos (ori);
  sa = sin (ori);
  return mat3 (ca.y, 0., - sa.y, 0., 1., 0., sa.y, 0., ca.y) *
         mat3 (1., 0., 0., 0., ca.x, - sa.x, 0., sa.x, ca.x);
}

vec2 Rot2D (vec2 q, float a)
{
  vec2 cs;
  cs = sin (a + vec2 (0.5 * pi, 0.));
  return vec2 (dot (q, vec2 (cs.x, - cs.y)), dot (q.yx, cs));
}

vec2 Rot2Cs (vec2 q, vec2 cs)
{
  return vec2 (dot (q, vec2 (cs.x, - cs.y)), dot (q.yx, cs));
}

vec3 HsvToRgb (vec3 c)
{
  return c.z * mix (vec3 (1.), clamp (abs (fract (c.xxx + vec3 (1., 2./3., 1./3.)) * 6. - 3.) - 1., 0., 1.), c.y);
}

const float cHashM = 43758.54;

float Hashff (float p)
{
  return fract (sin (p) * cHashM);
}

float Hashfv2 (vec2 p)
{
  return fract (sin (dot (p, vec2 (37., 39.))) * cHashM);
}

vec2 Hashv2f (float p)
{
  return fract (sin (p + vec2 (0., 1.)) * cHashM);
}

vec2 Hashv2v2 (vec2 p)
{
  vec2 cHashVA2 = vec2 (37., 39.);
  return fract (sin (dot (p, cHashVA2) + vec2 (0., cHashVA2.x)) * cHashM);
}

vec4 Hashv4v3 (vec3 p)
{
  vec3 cHashVA3 = vec3 (37., 39., 41.);
  return fract (sin (dot (p, cHashVA3) + vec4 (0., cHashVA3.xy, cHashVA3.x + cHashVA3.y)) * cHashM);
}

float Noiseff (float p)
{
  vec2 t;
  float ip, fp;
  ip = floor (p);
  fp = fract (p);
  fp = fp * fp * (3. - 2. * fp);
  t = Hashv2f (ip);
  return mix (t.x, t.y, fp);
}

float Noisefv2 (vec2 p)
{
  vec2 t, ip, fp;
  ip = floor (p);  
  fp = fract (p);
  fp = fp * fp * (3. - 2. * fp);
  t = mix (Hashv2v2 (ip), Hashv2v2 (ip + vec2 (0., 1.)), fp.y);
  return mix (t.x, t.y, fp.x);
}

float IFbm1 (float p)
{
  float s, a;
  p *= 5.;
  s = 0.;
  a = 10.;
  for (int j = 0; j < 4; j ++) {
    s += floor (a * Noiseff (p));
    a *= 0.5;
    p *= 2.;
  }
  return 0.1 * s;
}

float Fbm2 (vec2 p)
{
  float f, a;
  f = 0.;
  a = 1.;
  for (int j = 0; j < 5; j ++) {
    f += a * Noisefv2 (p);
    a *= 0.5;
    p *= 2.;
  }
  return f * (1. / 1.9375);
}

float Fbmn (vec3 p, vec3 n)
{
  vec3 s;
  float a;
  s = vec3 (0.);
  a = 1.;
  for (int j = 0; j < 5; j ++) {
    s += a * vec3 (Noisefv2 (p.yz), Noisefv2 (p.zx), Noisefv2 (p.xy));
    a *= 0.5;
    p *= 2.;
  }
  return dot (s, abs (n));
}

vec3 VaryNf (vec3 p, vec3 n, float f)
{
  vec4 v;
  vec3 g;
  vec2 e = vec2 (0.1, 0.);
  for (int j = VAR_ZERO; j < 4; j ++)
     v[j] = Fbmn (p + ((j < 2) ? ((j == 0) ? e.xyy : e.yxy) : ((j == 2) ? e.yyx : e.yyy)), n);
  g = v.xyz - v.w;
  return normalize (n + f * (g - n * dot (n, g)));
}
