void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    vec2 uv = (fragCoord.xy - 0.5 * iResolution.xy) / iResolution.y;
    float radius = length(uv);

    // Add time-based animation
    float time = iTime * 0.5;

    // Create a symbol pattern with animation
    float symbol = sin(uv.x * 10.0 + time) * sin(uv.y * 10.0 + time) + cos(radius * 40.0 + time);

    // Add vivid colors to the symbol
    vec3 color = vec3(
        sin(symbol * 3.1415 + time),
        cos(symbol * 1.618 + time),
        sin(symbol * 2.236 + time)
    );

    // Create a circular mask to isolate the symbol
    float mask = smoothstep(0.1, 0.11, radius);
    color *= mask;

    fragColor = vec4(color, 1.0);
}
