#define AA(d) smoothstep(-px, px, d)
float box(in vec2 uv, vec2 b)
{
    uv = abs(uv)-b; return max(uv.x, uv.y);
}

struct displayInfo
{
    bool a, b, c, d, e, f, g;
};

#define t true
#define f false
displayInfo[] info = displayInfo[] (
displayInfo(t, t, t, t, t, t, f),
displayInfo(f, t, t, f, f, f, f),
displayInfo(t, t, f, t, t, f, t),
displayInfo(t, t, t, t, f, f, t),
displayInfo(f, t, t, f, f, t, t),
displayInfo(t, f, t, t, f, t, t),
displayInfo(t, f, t, t, t, t, t),
displayInfo(t, t, t, f, f, f, f),
displayInfo(t, t, t, t, t, t, t),
displayInfo(t, t, t, t, f, t, t));
#undef t
#undef f

#define vc(p, z, s) if(z) d = min(s d, box(uv-size.xx*p.xy, size.yx+vec2(0, size.y-st*0.75)))
#define ve(p, z, s) if(z) d = min(s d, box(uv-size.xx*p.xy, size.yx+vec2(0, size.y*10.)))
#define hc(p, z, s) if(z) d = min(s d, box(uv-vec2(0, size.x*p*2.), size.xy+vec2(size.y-st, 0)))
float segDisplay(vec2 uv, vec2 size, displayInfo i)
{
    float st = size.y/size.x; // Thickness of segments
    float d = 1e20;
    hc(1.,               i.a,  );
    vc(vec2(1, 1),       i.b,  );
    vc(vec2(1, -1),      i.c,  );
    hc(-1.,              i.d,  );
    vc(vec2(-1, -1),     i.e,  );
    vc(vec2(-1, 1),      i.f,  );
    hc(0.,               i.g,  );
    hc(st ,               true, -);
    hc(-st ,              true,  );
    ve(vec2(1.-st*2., 0), true, );
    ve(vec2(-1.+st*2., 0), true, );

    return d;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = (fragCoord*2.-iResolution.xy)/iResolution.y;
    float px = 2./iResolution.y;
    // Time varying pixel color
    vec3 col = vec3(AA(segDisplay(uv, vec2(0.3, 0.05), info[int(mod(iTime, 10.))])));
    #define t true
    col += vec3(AA(segDisplay(uv, vec2(0.3, 0.05), displayInfo(t, t, t, t, t, t, t))))*0.2;

    // Output to screen
    fragColor = vec4(col,1.0);
}
