const float PI = 3.14159265;

// Line segment SDF by Inigo Quilez
// from https://iquilezles.org/articles/distfunctions2d/
float sdSegment( in vec2 p, in vec2 a, in vec2 b )
{
    vec2 pa = p-a, ba = b-a;
    float h = clamp( dot(pa,ba)/dot(ba,ba), 0.0, 1.0 );
    return length( pa - ba*h );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    uv *= 2.0;
    uv -= 1.0;
    uv.x *= iResolution.x / iResolution.y;

    const float len = 0.25;
    const float spacing = 0.5;
    float t = mod(iTime * 0.5, 2.0 * spacing / len);
    float dist = 9999999.;
    for (int i = -10; i <= 10; i++) {
        for (int j = -1; j <= 1; j++) {
            float x = floor(t) * len + float(i) * spacing;
            float y = float(j);
            y += (float(i % 2 == 0) - 0.5) * 0.3;
            vec2 a = vec2(x, y);
            float theta = mod(t, 1.0) * PI;
            vec2 b = a + vec2(-cos(theta), sin(theta)) * len;
            dist = min(dist, sdSegment(uv, a, b));
         }
    }
    vec3 col = 0.5 + 0.5*sin(sin(iTime) * 10.0 + dist * 50.0 + vec3(0,1,2));
//    vec3 col = vec3(float(dist < 0.05));

    fragColor = vec4(col,1.0);
}
