// Analytic projection of an ellipsoidal splat (ie, anisotropic
// surfel) to screen pixels. 
//
// Ellipses in world space become ellipses when projected to the
// camera view plane. In fact, these ellipses can be analytically
// determined from the camera parameters and the 3D ellipse geometry,
// such that their exact position, orientation and surface area
// can be computed. This means that, given a ellipse and a camera
// and buffer resolution, there is an analytical formula that 
// provides the amount of pixels covered by a disk in the image.
// This can be very useful for implementing LOD on oriented point
// clouds based on their size in screen.
//
// This shader implements this formula, and provides too the
// center and axes of the ellipse.
//
// More info: xxx
// 
// Sphere  projection: https://www.shadertoy.com/view/XdBGzd
// Disk    projection: https://www.shadertoy.com/view/mltyDN
// Ellipse projection: https://www.shadertoy.com/view/ctGyRR
//
// NOTE - not working at grazing angles when area tends to zero
//
// ---------------------------------------------------------------------------------------------
// Derivation:
//
// Ray  : r(t) = t*d  and d = (x,y,l)
// Disk : <r-c,n> = 0 and <r-c,u>²+<r-c,v>²-1 = 0 with n = u⨯v
// So
//
//   <t*d-c,n> = 0 --> t*<d,n>-<c,n> = 0 --> t = <c,n>/<d,n> --> r = d<c,n>/<d,n>
//
// and
//
//   <d*<c,n>/<d,n>-c,u>² + <d*<c,n>/<d,n>-c,v>² - 1 = 0
//
// If N=<c,n>, U=<c,u> and V=<c,v>, then
//
//   <d*N/<d,n>-c,u>² + <d*N/<d,n>-c,v>² - 1 = 0
//
//   (<d*N/<d,n>,u>-U)² + (<d*N/<d,n>,v>-V)² - 1 = 0
//
//   (<d,u>*N/<d,n>-U)² + (<d,v>*N/<d,n>-V)² - 1 = 0
//
//   (<d,u>² + <d,v>²)N²/<d,n>² + M - 2N(U<d,u>/<d,n> + V<d,v>)/<d,n> = 0
//
// With M = (U²+V²-1). Multiplying by <d,n>²,
//
//    M<d,n>² - 2N<d,n>(U<d,u> + V<d,v>) + N²(<d,u>² + <d,v>²) = 0
//
// so
//
//   M*(nx*x+ny*y+nz*l)² 
// - 2N(nx*x+ny*y+nz*l)*[U*(x*ux+y*uy+l*uz) + V*(x*vx+y*vy+l*vz)]
// + N²((x*ux+y*uy+l*uz)² + (x*vx+y*vy+l*vz)² ) = 0
//
// We collect the x², xy, y², x, y and 1 terms
//
//  f(x,y) = Ax² + Bxy + Cy² + Dx + Ey + F = 0
//
// So,
//
//  A =    M*nx²   - N*(nx*wx*2    ) + N²*(ux²  +vx²  )
//  C =    M*ny²   - N*(ny*wy*2    ) + N²*(uy²  +vy²  )
//  F = l²[M*nz²   - N*(nz*wz*2    ) + N²*(uz²  +vz²  )]
//  B = 2 [M*nx*ny - N*(nx*wy+ny*wx) + N²*(ux*uy+vx*vy)]
//  D = 2l[M*nx*nz - N*(nx*wz+nz*wx) + N²*(ux*uz+vx*vz)]
//  E = 2l[M*ny*nz - N*(ny*wz+nz*wy) + N²*(uy*uz+vy*vz)]
//
// with w = U*u + V*v
//
// This is an ellipse if B²-4AC<0, otherwise it's a hyperbola

struct ProjectionResult
{
  float area;      // probably all we care about is the area
  vec2  center;    // but i'm outputing all the information 
  vec2  axisA;     // for debugging and illustration purposes
  vec2  axisB;     // for debugging and illustration purposes
  float a, b, c, d, e, f; // implicit ellipse f(x,y) = Ax² + Bxy + Cy² + Dx + Ey + F 
};

ProjectionResult projectSplat( /* disk center     */ in vec3  splat_c, 
                               /* disk u          */ in vec3  splat_u,
                               /* disk v          */ in vec3  splat_v,
				               /* world to camera */ in mat4  cam,
							   /* projection      */ in float fle )
{
    // transform to camera space	
    vec3 splat_n = cross(splat_u,splat_v );
	vec3  c = (cam*vec4(splat_c,1.0)).xyz;
    vec3  u = (cam*vec4(splat_u,0.0)).xyz;
    vec3  v = (cam*vec4(splat_v,0.0)).xyz;
    vec3  n = (cam*vec4(splat_n,0.0)).xyz;

    // get implicit f(x,y) = Ax² + Bxy + Cy² + Dx + Ey + F = 0 
    float U = dot(c,u);
    float V = dot(c,v);
    float N = dot(c,n);
    float M = U*U + V*V - 1.0;
    vec3  w = U*u + V*v;

    float A =          M*n.x*n.x - N*(n.x*w.x*2.0    ) + N*N*(u.x*u.x+v.x*v.x);
    float C =          M*n.y*n.y - N*(n.y*w.y*2.0    ) + N*N*(u.y*u.y+v.y*v.y);
    float F = fle*fle*(M*n.z*n.z - N*(n.z*w.z*2.0    ) + N*N*(u.z*u.z+v.z*v.z));
    float B = 2.0*    (M*n.x*n.y - N*(n.x*w.y+n.y*w.x) + N*N*(u.x*u.y+v.x*v.y));
    float D = 2.0*fle*(M*n.x*n.z - N*(n.x*w.z+n.z*w.x) + N*N*(u.x*u.z+v.x*v.z));
    float E = 2.0*fle*(M*n.y*n.z - N*(n.y*w.z+n.z*w.y) + N*N*(u.y*u.z+v.y*v.z));

    // get ellipse/hyperbola parameters from implicit
    float k0 = B*B - 4.0*A*C;

    /*
    // alternative computation for k0
    vec3  z = vec3( u.z*dot(v,v), v.z*dot(u,u), n.z );
    float q = dot( vec3(U,V,N), z );
    k0 = 4.0*N*N * (z.x*z.x + z.y*z.y - q*q);
    */

    if( k0>0.0 ) // hyperbola
    {
        return ProjectionResult( -1.0, vec2(0.0), vec2(0.0), vec2(0.0), A, B, C, D, E, F );
    }

    float k1 = B*B + (A-C)*(A-C);
    float k2 = sqrt(k1);
    float k3 = k1 - k2*(A-C);
    float k4 = A*E*E + C*D*D - B*D*E + F*k0;
    float k5 = k4 / k3;

    vec2  cen = vec2( 2.0*C*D-B*E, 2.0*A*E-B*D )/k0;
    float lea = sqrt( (A+C+k2)*k5 )/k0;
    float leb = sqrt( (A+C-k2)*k5 )/k0;
    vec2  axa = vec2( k2-(A-C),-B ) * lea;
    vec2  axb = vec2(  B,k2-(A-C) ) * leb;

  //float area = 3.141593*sqrt(dot(axa,axa)*dot(axb,axb));
    float area = 6.283185*lea*leb*k3;
  //float area = 6.283185*sqrt(-k0)*abs(k4)/(k0*k0);

	return ProjectionResult( area, cen, axa, axb, A, B, C, D, E, F );
}

