// Digit drawing function by P_Malin (https://www.shadertoy.com/view/4sf3RN)

float SampleDigit(const in float n, const in vec2 vUV)
{		
	if(vUV.x  < 0.0) return 0.0;
	if(vUV.y  < 0.0) return 0.0;
	if(vUV.x >= 1.0) return 0.0;
	if(vUV.y >= 1.0) return 0.0;

	float data = 0.0;

	     if(n < 0.5) data = 7.0 + 5.0*16.0 + 5.0*256.0 + 5.0*4096.0 + 7.0*65536.0;
	else if(n < 1.5) data = 2.0 + 2.0*16.0 + 2.0*256.0 + 2.0*4096.0 + 2.0*65536.0;
	else if(n < 2.5) data = 7.0 + 1.0*16.0 + 7.0*256.0 + 4.0*4096.0 + 7.0*65536.0;
	else if(n < 3.5) data = 7.0 + 4.0*16.0 + 7.0*256.0 + 4.0*4096.0 + 7.0*65536.0;
	else if(n < 4.5) data = 4.0 + 7.0*16.0 + 5.0*256.0 + 1.0*4096.0 + 1.0*65536.0;
	else if(n < 5.5) data = 7.0 + 4.0*16.0 + 7.0*256.0 + 1.0*4096.0 + 7.0*65536.0;
	else if(n < 6.5) data = 7.0 + 5.0*16.0 + 7.0*256.0 + 1.0*4096.0 + 7.0*65536.0;
	else if(n < 7.5) data = 4.0 + 4.0*16.0 + 4.0*256.0 + 4.0*4096.0 + 7.0*65536.0;
	else if(n < 8.5) data = 7.0 + 5.0*16.0 + 7.0*256.0 + 5.0*4096.0 + 7.0*65536.0;
	else if(n < 9.5) data = 7.0 + 4.0*16.0 + 7.0*256.0 + 5.0*4096.0 + 7.0*65536.0;

	vec2 vPixel = floor(vUV * vec2(4.0, 5.0));
	float fIndex = vPixel.x + (vPixel.y * 4.0);

	return mod(floor(data / pow(2.0, fIndex)), 2.0);
}

float PrintInt(const in vec2 uv, const in float value )
{
	float res = 0.0;
	float maxDigits = 1.0+ceil(log2(value)/log2(10.0));
	float digitID = floor(uv.x);
	if( digitID>0.0 && digitID<maxDigits )
	{
        float digitVa = mod( floor( value/pow(10.0,maxDigits-1.0-digitID) ), 10.0 );
        res = SampleDigit( digitVa, vec2(fract(uv.x), uv.y) );
	}
	return res;	
}

// <o+t*d-c,n> = 0
// <o-c+t*d,n> = 0
// <o-c,n>+t*<d,n> = 0
// t = -<o-c,n>/<d,n>
float iEllipse( in vec3 o, in vec3 d, vec3 c, vec3 u, vec3 v )
{
    vec3  n = cross(u,v);
    float t = -dot(o-c,n)/dot(d,n);
    vec3  q = o + d*t;
    float x = dot(q-c,u);
    float y = dot(q-c,v);
    return (x*x+y*y-1.0<0.0) ? t : -1.0;
}

float sdSegment( vec2 p, vec2 a, vec2 b )
{
	vec2 pa = p - a;
	vec2 ba = b - a;
	float h = clamp( dot(pa,ba)/dot(ba,ba), 0.0, 1.0 );
	return length( pa - ba*h );
}

float gridTextureGradBox( in vec2 p, in vec2 ddx, in vec2 ddy )
{
    const float N = 10.0;
    vec2 w = max(abs(ddx), abs(ddy)) + 0.01;
    vec2 a = p + 0.5*w;
    vec2 b = p - 0.5*w;           
    vec2 i = (floor(a)+min(fract(a)*N,1.0)-
              floor(b)-min(fract(b)*N,1.0))/(N*w);
    return (1.0-i.x)*(1.0-i.y);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 p = (2.0*fragCoord-iResolution.xy) / iResolution.y;

	float fov = 1.0;

	float an = 12.0 + 0.5*iTime + 10.0*iMouse.x/iResolution.x;
	vec3 ro = vec3( 3.0*cos(an), 0.0, 3.0*sin(an) );
    vec3 ta = vec3( 0.0, 0.0, 0.0 );
	vec3 ww = normalize( ta - ro );
    vec3 uu = normalize( cross(ww,vec3(0.0,1.0,0.0) ) );
    vec3 vv = normalize( cross(uu,ww));
	vec3 rd = normalize( p.x*uu + p.y*vv + fov*ww );
	mat4 cam = mat4( uu.x, uu.y, uu.z, 0.0,
					 vv.x, vv.y, vv.z, 0.0,
					 ww.x, ww.y, ww.z, 0.0,
					 -dot(uu,ro), -dot(vv,ro), -dot(ww,ro), 1.0 );

    const vec3 disc[3] = vec3[3]( vec3(-2.0, 1.0, 0.0),
                                  vec3( 3.0, 1.5, 1.0),
                                  vec3( 1.0,-1.0, 1.0) );
    const vec3 disu[3] = vec3[3]( vec3( 0.1, 0.2, 0.1)*4., 
                                  vec3( 0.1,-0.1, 0.2)*4., 
                                  vec3( 0.2, 0.1,-0.1)*4. );
    const vec3 disv[3] = vec3[3]( cross(disu[0],vec3(0,1,0))*1., 
                                  cross(disu[1],vec3(0,1,0))*1., 
                                  cross(disu[2],vec3(0,1,0))*1.);

	float tmin = 10000.0;
	vec3  nor = vec3(0.0);
	vec3  pos = vec3(0.0);

	vec3 sur = vec3(1.0);
    for( int i=0; i<3; i++ )
    {
        float h = iEllipse( ro, rd, disc[i], disu[i], disv[i] );
        if( h>0.0 && h<tmin ) 
        { 
            tmin = h; 
            pos = ro + h*rd;
            nor = normalize(cross(disu[i],disv[i])); 
            sur = 0.5 + 0.5*cos(float(i)*2.0+vec3(0.0,2.0,4.0));
        }
    }

    float h = (-2.0-ro.y)/rd.y;
	if( h>0.0 && h<tmin ) 
	{ 
		tmin = h; 
		pos = ro + h*rd;
		nor = vec3(0.0,1.0,0.0); 
        sur = vec3(1.0)*gridTextureGradBox( pos.xz, dFdx(pos.xz), dFdy(pos.xz) );
	}

	vec3 col = vec3(0.0);

	if( tmin<100.0 )
	{
	    pos = ro + tmin*rd;
        col = vec3(1.0);

		vec3 lig = normalize( vec3(2.0,1.4,-1.0) );
        nor = nor * sign( -dot(nor,rd) );

		float ndl = clamp( dot(nor,lig), 0.0, 1.0 );

		float sha = 1.0;
        if( ndl>0.0 )
        {
            for( int i=0; i<3; i++ )
            {
                if( iEllipse( pos+nor*0.001, lig, disc[i], disu[i], disv[i] ) > 0.0 )
                {
                    sha = 0.0;
                    break;
                }
            }
        }
		col = (0.5+0.5*nor.y)*vec3(0.2,0.3,0.4) + sha*vec3(1.0,0.9,0.8)*ndl + sha*vec3(1.5)*ndl*pow( clamp(dot(normalize(-rd+lig),nor),0.0,1.0), 16.0 );
		col *= sur;
        col *= exp( -0.25*(max(0.0,tmin-3.0)) );
	}

	col = pow( col, vec3(0.45) );

	//-------------------------------------------------------
    // display ellipses
	//-------------------------------------------------------
    for( int i=0; i<3; i++ )
	{
        ProjectionResult r = projectSplat( disc[i], disu[i], disv[i], cam, fov );

        // display conic section border
        float f = r.a*p.x*p.x + r.b*p.x*p.y + r.c*p.y*p.y + r.d*p.x + r.e*p.y + r.f;
        vec2  g = 2.0*vec2(r.a,r.c)*p.xy + r.b*p.yx + vec2(r.d,r.e);                   
        float d = abs(f)/length(g);
        col = mix( col, vec3(1.0,0.0,0.0), 1.0-smoothstep(0.00,0.01, d) );

        // if ellipse, display area, center and axes
        if( r.area>0.0 )
        {
            r.area *= iResolution.y*iResolution.y*0.25;
            col = mix( col, vec3(1.0,1.0,0.0), 1.0-smoothstep(0.00,0.01, sdSegment( p, r.center-r.axisA, r.center+r.axisA ))  );
            col = mix( col, vec3(1.0,1.0,0.0), 1.0-smoothstep(0.00,0.01, sdSegment( p, r.center-r.axisB, r.center+r.axisB ))  );
            col = mix( col, vec3(1.0,0.0,0.0), 1.0-smoothstep(0.03,0.04, length(p-r.center)) );
            vec2 pp = r.center + 0.5*max( max( r.axisA, -r.axisA ), max( r.axisB, -r.axisB ) );
            col = mix( col, vec3(1.0), PrintInt( ((p-pp)-vec2(0.0,0.0))/0.07, floor(r.area) ) );
        }
    }
    //-------------------------------------------------------

	fragColor = vec4( col, 1.0 );
}
