/* "Chrome Dreams" by @kishimisu (2022) - https://www.shadertoy.com/view/ctX3RM
   458 chars => 399 chars by @Xor in the comments

   Playing with rotations and space repetition.

   If you have a good GPU you can increase the 
   "2e2" value (line 20) to "3e2" or more 
   in order to reduce aliasing.
*/

#define a(a,b,f,o) mix(a,b,sin(o+e*f)*.5+.5)
#define s(a) mat2(cos(a+vec4(0,33,11,0)))

void mainImage(out vec4 O, vec2 F) {
    float c , 
          o , 
          d = 3., 
          e = iTime*.4;
    vec2  r = iResolution.xy; 

    for (;o++<2e2 && d>.001;) {
        vec3 p = abs(.7*c*normalize(vec3((F+F-r)/r.y, 1)));
        p.xy *= s(e);
        p.zy += e + c*.2;
        p = fract(p)-.5;
        p.xy *= s(c);
        p.xz *= s(e);
        p.y = max(abs(p.y)-a(0.,.2,1.,),0.);
        c += d = (length(vec2(length(p.xy)-.2,p.z)) 
                 -a(.04,.1,.5,4.) - c*.01)*.5;
    }

    O.rgb = 1.2*(cos(c*6. + .8*vec3(0,1.+c*.04,2))+.2)/exp(c*.14);
}
