// Based loosely on the Godot Shaders version.
// : https://godotshaders.com/shader/old-movie-shader/ (CC0)
// My changes remain as CC0.

// These define the intended width of the bezier curves serving as film scratches
const float grain_edge_width = 0.01; // 0.005 for a smooth line (but pft, it looks cooler a little too fat)
const float grain_smooth = 0.0025;

// from: https://hhoppe.com/ravg.pdf?from=https://research.microsoft.com/en-us/um/people/hoppe/ravg.pdf&type=path
// and referencing: https://www.shadertoy.com/view/MtS3Dy
// This calculates the distance vector from the origin to the given curve where:
// b0: is the start point
// b1: is the control point
// b2: is the end point
vec2 get_distance_vector(vec2 b0, vec2 b1, vec2 b2) {
    float a = determinant(mat2(b0, b2));
    float b = 2.0 * determinant(mat2(b1, b0));
    float d = 2.0 * determinant(mat2(b2, b1)); // ğ›¼,ğ›½,ğ›¿(ğ‘)
    float f = (b * d) - (a * a); // ğ‘“(ğ‘)

    vec2 d21 = b2 - b1;
    vec2 d10 = b1 - b0;
    vec2 d20 = b2 - b0;

    vec2 gf = 2.0 * (b * d21 + d * d10 + a * d20);

    gf = vec2(gf.y, -gf.x); // âˆ‡ğ‘“(ğ‘)

    vec2 pp = -f * gf / dot(gf, gf); // ğ‘â€²

    vec2 d0p = b0 - pp; // ğ‘â€² to origin

    float ap = determinant(mat2(d0p, d20)); //  ğ›¼,ğ›½(ğ‘â€²)

    float bp = 2.0 * determinant(mat2(d10, d0p)); // ğ‘¡Ì…

    // (note that 2*ap+bp+dp=2*a+b+d=4*area(b0,b1,b2))
    float t = clamp((ap + bp) / (2.0 * a + b + d), 0.0, 1.0); // ğ‘¡Ì…

    return mix(mix(b0, b1, t), mix(b1, b2, t), t); // ğ‘£ğ‘– = ğ‘(ğ‘¡Ì…)
}

// Get the approximate distance from the point 'p' to the curve defined as
// it is above in 'get_distance_vector'.
float approx_distance(vec2 p, vec2 b0, vec2 b1, vec2 b2) {
    return length(get_distance_vector(b0 - p, b1 - p, b2 - p));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    // Get our UV coordinate
    vec2 UV = fragCoord.xy / iResolution.xy;

	// Get the film grained and slowed buffer
	vec4 frame = texture(iChannel1, UV);

	if (saturation < 1.0) {
		float desat = clamp(frame.r * 0.2126 + frame.g * 0.7152 + frame.b * 0.0722, 0., 1.);
		frame.rgb = mix(vec3(desat, desat, desat), frame.rgb, saturation);
	}

	// Make 'constrast' go from 0.0 to 2.0 instead of -1.0 to 1.0
	float contrast_multiplier = (contrast < 0.0) ? (1.0 / (-contrast + 1.0)) : (contrast + 1.0);

	// Ensure gamma is similarly used
	float gamma_multiplier = (gamma < 0.0) ? (-gamma + 1.0) : (1.0 / (gamma + 1.0));

    // Determine seed for random film grain location based on frame time
    float stepped_time = float(int(floor(iTime * 10.0)));

    // Obscure via noise
    vec4 grain_color = vec4(0.0, 0.0, 0.0, 0.0);

    // Compute the aspect ratio and a UV relative to the aspect ratio (relative_uv)
    float aspectRatio = iResolution.x / iResolution.y;
    vec2 relative_uv = UV * aspectRatio;

    // Randomly pick a starting point
    float RTIME = stepped_time * PI;
    vec2 A3 = (clamp(vec2(
        texture(iChannel2, vec2(RTIME, RTIME)).r,
        texture(iChannel2, vec2(RTIME/2.0, RTIME/2.0)).g
    ), 0.2, 0.8) - 0.2) * (1.0 / 0.6) * aspectRatio;

    // Randomly pick a control point near the start
    RTIME *= PI;
    vec2 B3 = A3 + vec2(
        texture(iChannel2, vec2(RTIME, RTIME)).r - 0.5,
        texture(iChannel2, vec2(RTIME/2.0, RTIME/2.0)).g - 0.5
    ) * 0.1;
    RTIME *= PI;

    // Randomly pick an end point near the start
    vec2 C3 = A3 + vec2(
        texture(iChannel2, vec2(RTIME, RTIME)).r - 0.5,
        texture(iChannel2, vec2(RTIME/2.0, RTIME/2.0)).g - 0.5
    ) * 0.5;

    // Calculate the distance (smudged a bit for better effect) to this bezier curve
    float dist = approx_distance(relative_uv, A3, B3, C3);
    if (dist < grain_edge_width + grain_smooth) {
        dist = smoothstep(grain_edge_width - grain_smooth, grain_edge_width + grain_smooth, dist);
        grain_color += 1.0 - dist;
    }

    // Randomly pick a new start
    A3 = (clamp(vec2(
        texture(iChannel2, vec2(RTIME, RTIME)).r,
        texture(iChannel2, vec2(RTIME/2.0, RTIME/2.0)).g
    ), 0.2, 0.8) - 0.2) * (1.0 / 0.6) * aspectRatio;

    // Randomly pick a control point near the start
    RTIME *= PI;
    B3 = A3 + vec2(
        texture(iChannel2, vec2(RTIME, RTIME)).r - 0.5,
        texture(iChannel2, vec2(RTIME/2.0, RTIME/2.0)).g - 0.5
    ) * 0.1;
    RTIME *= PI;

    // Randomly pick an end point near the start
    C3 = A3 + vec2(
        texture(iChannel2, vec2(RTIME, RTIME)).r - 0.5,
        texture(iChannel2, vec2(RTIME/2.0, RTIME/2.0)).g - 0.5
    ) * 0.5;

    // Calculate the distance (smudged a bit for better effect) to this bezier curve
    dist = approx_distance(relative_uv, A3, B3, C3);
    if (dist < grain_edge_width + grain_smooth) {
        dist = smoothstep(grain_edge_width - grain_smooth, grain_edge_width + grain_smooth, dist);
        grain_color += 1.0 - dist;
    }

    // Mix in film grain scratch lines
    frame.rgb = mix(frame.rgb, grain_color.rgb * 0.2 + frame.rgb, texture(iChannel2, UV).r);

    // Apply gamma
	frame = vec4(
		pow(
			frame.rgb,
			vec3(gamma_multiplier, gamma_multiplier, gamma_multiplier)
		) * contrast_multiplier + brightness,
		frame.a
	);

	// Compute the vignette filter 'power'
    // (not capped by frame rate... this is the projector bulb flicker)
	float vignette_power = vignette + 0.5 * (
        texture(iChannel2, vec2(iTime / 60.0, iTime / 59.0)).x
    );

    // Compute the amount of light via the distance to the center
	float vig = -vignette_power * ((UV.x - 0.5) * (UV.x - 0.5) + (UV.y - 0.5) * (UV.y - 0.5));

    // Essentially an amount of light we will be adding into the frame.
	vec4 vignette_value = vec4(vig, vig, vig, 1.0);

	// Return result through vignette
	fragColor = mix(frame, vignette_value, vignette_amount);
}

// We want to encode how 'close' we are to 10fps interval and which frame in
// the second we are within. So, we encode the iTime value to a quantized value
// and encode it in every pixel of our buffer.

float encode_time() {
	// TIME might be 150402.45 (seconds), so we can get the distance from
	// the 10fps range by knowing that a frame is happening every 100ms (0.1).
	int current_frame = int(floor((iTime - 0.05) * 10.0)) % 9;

	// Now we can get a frame number between 0 and 8 (as a fraction between
	// 0.1 and 0.9).
	return 0.1 + (float(current_frame) * 0.1);
}

// This really actually computes the current frame information for each
// step of our 'slowed' down video stream.
void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    float encoded = encode_time();

	// Encode it into 4 floats
	fragColor = vec4(encoded, 1.0, 1.0, 1.0);
}

// This is the shader that computes the capped frame. This essentially gives you the
// last frame until buffer A tells us that we should be making a new frame.

float encode_time() {
	// TIME might be 150402.45 (seconds), so we can get the distance from
	// the 10fps range by knowing that a frame is happening every 100ms (0.1).
	int current_frame = int(floor(iTime * 10.0)) % 9;

	// Now we can get a frame number between 0 and 8 (as a fraction between
	// 0.1 and 0.9).
	return 0.1 + (float(current_frame) * 0.1);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    vec2 UV = fragCoord / iResolution.xy;

	// Get the encoded time
    float encoded = encode_time();

	// Get last encoded value
	float last_encoded = texture(iChannel0, vec2(0.5, 0.5)).r;

	// We should slow down the fps?
	// We need to truncate the frames per second to 10fps (just output the prior frame)
	if ((encoded + 0.05 > last_encoded && encoded - 0.05 < last_encoded) && last_encoded != 0.0) {
		// Copy over old frame
		fragColor = texture(iChannel1, UV);
	}
	else {
        // Adjust for texture resolution to stretch it

		// Apply vertical "flutter"
		float flutter_max = (1.0 / iResolution.y) * y_flutter_power;
		float flutter = mod(iTime, flutter_max) - (flutter_max / 2.0);
		fragColor = texture(iChannel3, UV + vec2(0.0, flutter));

		// Small speckles:
		float RTIME1=iTime;
		vec4 speckle = vec4(0.0, 0.0, 0.0, 0.0);
		float energy = 1000000.0 * (1.0 + 0.5 * texture(iChannel2, vec2(RTIME1,RTIME1)).r);
		for (int i = 0; i < speckle_count; i++) {
			RTIME1 *= PI;
			vec2 position1=(clamp(vec2(
                texture(iChannel2, vec2(RTIME1, RTIME1)).r,
                texture(iChannel2, vec2(RTIME1/2.0, RTIME1/2.0)).g
            ), 0.2, 0.8) - 0.2) * (1.0 / 0.6);

			float xdev1=(UV.x-position1.x);
			float ydev1=(UV.y-position1.y);

			energy = 1000000.0 * (1.0 + 0.5 * texture(iChannel2, vec2(RTIME1,RTIME1)).r);
			float spec=energy*(xdev1*xdev1+0.5*ydev1*ydev1)+.1*texture(iChannel2, vec2(UV.x,UV.y)).r;
			speckle+=vec4(max(0.0,20.0-spec),max(0.0,20.0-spec),max(0.0,20.0-spec),1.0);
		}

		speckle *= speckle_power;

		// lines:
		float RTIME2=iTime;
		vec2 positionline = vec2(
			texture(iChannel2, vec2(RTIME2,RTIME2)).r,
			texture(iChannel2, vec2(RTIME2,RTIME2)).g
		);

		// Distance between us and the line
		float xline=(UV.x-positionline.x);
		float yline=(UV.y-positionline.y);

		energy = 1000000.0 * (1.0 + 0.5 * texture(iChannel2, vec2(RTIME2,RTIME2)).r);
		float lin = energy * (xline*xline+0.00001*yline*yline) + 0.1 * texture(iChannel2, vec2(UV.x,UV.y)).r;
		vec4 line=vec4(max(0.0,8.0-lin),max(0.0,8.0-lin),max(0.0,8.0-lin),1.0);
		line *= line_power;

		vec4 dirt=mix(speckle,line,0.5) / 5.0;
		fragColor += dirt; //, frame, 0.7);
	}
}

const float line_power = 0.1;
const int speckle_count = 2;
const float speckle_power = 0.1;
const float y_flutter_power = 1.2;

const float PI = 3.14159;

const float vignette = 0.7;
const float vignette_amount = 0.2;
const float saturation = 0.0;
const float contrast = 0.3;
const float brightness = -0.4;
const float gamma = 0.7;
