void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float theta = -iTime * 0.5;
    vec2 pxl = fragCoord.xy/iResolution.xy;

    vec3 colour = vec3(0.);
    mat2 scale = mat2(iResolution.x/iResolution.y, 0., 0., 1.);
    vec2 q = scale * (pxl - vec2(0.4 * iResolution.y/iResolution.x, 0.));
    if (pow(q.x - 0.5 , 2.) + pow(q.y - 0.5, 2.) < 0.25) { 
        q -= vec2(0.5, 0.5);
        q *= mat2(cos(theta), sin(theta), -sin(theta), cos(theta));
        q += vec2(0.5, 0.5);

        vec3 red    = vec3(0.929, 0.125, 0.141);
        vec3 green  = vec3(0.153, 0.557, 0.263);
        vec3 yellow = vec3(0.996, 0.741, 0.067);
        vec3 white  = vec3(1.0, 1.0, 1.0);

        vec2 centre = vec2(0.5, 0.5);

        vec2 qxl = q - centre;

        float r = 0.2 + 0.05 * cos(atan(qxl.y, qxl.x) * 21.0 + iTime)+ 0.05 * cos(atan(qxl.y, qxl.x) * 10.0 - iTime);
        float star = smoothstep(r+0.05, r+0.06, length(qxl));


        if(pxl.y > (2./3.))
            colour = (star < 0.001)? yellow : red;
        else if (pxl.y < (1./3.))
            colour = (star < 0.001)? yellow : green;
        else
            colour = (star < 0.001)? yellow : white;
    }

    fragColor = vec4(colour, 1.0);

}
