/*
This demo shows the process of translating a vector between tangent 
and world space for sphere mapping without relying on any vertices.
This methodology proves particularly valuable for tasks like 
translating wind directions between world and tangent space.

To achieve this, it is necessary to generate the normal, tangent, 
and bitangent vectors corresponding to your mapping. The normal 
vector points upwards from the surface of the mapping, while the 
tangent vector aligns with the +X direction of your UV mapping in 
world space. Similarly, the bitangent vector corresponds to the +Y 
direction of your UV mapping.

These vectors are combined within a 3x3 matrix. When this matrix is 
multiplied by a vector, it effectively transforms the vector to 
world space. Conversely, the inverse of this matrix facilitates the 
translation of a vector from world space back to the tangent space 
of your mapping.

The visual representation is a fusion of a flow plot, which showcases 
3D coordinates in world space on a surface by displacing flowlines. 
The wind direction is oriented perpendicular to this surface, 
mimicking the behavior of water waves. The arrows within the plot 
are in tangent space, indicating the direction of the vector in 
tangent space.

See here how the quiverplot works:
https://www.shadertoy.com/view/dssyzf

See here how the flow plot works:
https://www.shadertoy.com/view/dlSyDw

This plot has mouse controls to look around. Play with the constants
below to edit the visualized vectors.
*/

// Define your custom direction here
#define DIRECTION vec3(1.,1.,1.)

// enable DIRECTION is worldspace, disable DIRECTION is tangent space
//#define DIRECTIONWORLDSPACE

//Spherical mapping
vec2 sphereMap(vec3 uvw) {
    // merkator projection
    float u = 0.5 + atan(uvw.y, uvw.x) / (2.*3.1415);
    float v = 0.5 + asin(uvw.z) / 3.1415;
    return vec2(u, v);
}

// TBN Matrix for the spheremapping above
mat3 sphereTBNMatrix(vec3 normal) {
    // vec3 poleAxis = vec3(0,0,1.);
    // vec3 tangent = normalize(cross(poleAxis, normal));
    vec3 tangent = safeNormalize(vec3(-normal.y, normal.x, 0.), vec3(1,0,0));
    vec3 bitangent = cross(normal, tangent);
    return mat3(tangent, bitangent, normal);
}

// sphere normals form morgan3d https://www.shadertoy.com/view/4dsGD2
vec3 inverseSphereMap(vec2 uv) {
    // Inverse mapping for u
    float theta = uv.y * 3.1415;
	float phi   = uv.x * 3.1415 * 2.0;     
    float sinTheta = sin(theta);  

    // Equation from http://graphicscodex.com  [sphry]
	vec3 dir = - vec3(sinTheta * cos(phi),                     
                    sinTheta * sin(phi),
                    cos(theta));
    return dir;
}

// TBN Matrix finite difference to find the TBN matrix for the spheremap above
mat3 sphereTBNMatrixApprox(vec3 normal) {
    // Tiny offset in UV coordinates
    vec2 offsetUV = sphereMap(normal) + vec2(0.001, 0.); 
    vec3 normalOffset = inverseSphereMap(offsetUV);
    vec3 tangent = safeNormalize(normalOffset - normal, vec3(1,0,0));
    vec3 bitangent = cross(normal, tangent);
    return mat3(tangent, bitangent, normal);
}

vec3 TangentSpacePlot(vec3 uvw, vec3 direction) {
    // generate mercator projection
    vec2 uv = sphereMap(uvw);

    // tangent space matrix
    mat3 tbnMatrix = sphereTBNMatrix(uvw);

    #ifdef DIRECTIONWORLDSPACE
        // translate world space vector to tangent space
        vec3 directionWS = direction;
        vec2 directionTS = vec3(inverse(tbnMatrix) * direction).xy;
    #else
        // translate tangent space vector to world space
        vec3 directionWS = tbnMatrix * vec3(direction.xy,0);
        vec2 directionTS = direction.xy;
    #endif

    // quiver plot in tangent space
    float arrowscale = 20.;
    float tsPlot = quiverPlot(uv, directionTS, arrowscale);   

    // World Space flow plot
    float time = iTime * 0.25;
    vec3 wsPlot = flowPlot(uvw, directionWS, time);

    return max(wsPlot, vec3(tsPlot));
}

// _____________________________3D Section_____________________________
// https://www.shadertoy.com/view/4sjXW1 by nimitz (twitter: @stormoid)

mat2 mm2(in float a){float c = cos(a), s = sin(a);return mat2(c,-s,s,c);}

float iSphere(in vec3 ro, in vec3 rd)
{
    vec3 oc = ro;
    float b = dot(oc, rd);
    float c = dot(oc,oc) - 1.;
    float h = b*b - c;
    if(h <0.0) return -1.;
    return -b - sqrt(h);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{	
	vec2 p = fragCoord.xy/iResolution.xy-0.5;
	p.x*=iResolution.x/iResolution.y;
	vec2 um = iMouse.xy / iResolution.xy-.5;
	um.x *= iResolution.x/iResolution.y;
    p*= 1.5;

    //camera
	vec3 ro = vec3(0.,0.,2.4);
    vec3 rd = normalize(vec3(p,-1.5));
    mat2 mx;
    mat2 my;
    if(iMouse.x > 0.0)	{    
        mx = mm2(um.x*5.);
        my = mm2(um.y*5.);
    } else {
        mx = mm2(iTime*.1+um.x*5.);
        my = mm2(iTime*0.05+um.y*5.);
    }
    ro.xz *= mx;rd.xz *= mx;
    ro.xy *= my;rd.xy *= my;

    float t = iSphere(ro,rd);
    vec3 col = vec3(0);

    if (t > 0.)
    {
    	vec3 pos = ro+rd*t;
        // Sphere mapping
    	col = TangentSpacePlot(pos, DIRECTION);
    } else {
        // Background mapping
        vec3 pos = rd;  
        col = DIRECTION;
    }

	fragColor = vec4(col, 1.0);
}

// Generic Functions _________________________________________

// Safe Normalization avoiding division by zero with alternate
#define SMALLESTFLOAT (1.0 / float(0xffffffffU))
vec3 safeNormalize(vec3 vector, vec3 alternate) {

    // Calculate the square of the length of the vector
    float lenSq = dot(vector, vector);

    // Check if the squared length is greater than a tiny threshold
    if (lenSq > SMALLESTFLOAT) {
        // If so, normalize the vector resuing the squared length
        return vector / sqrt(lenSq);

    } else {
        // Otherwise, return a alternate vector
        return alternate;
    }
}

//_____________________Flow Plot______________________________

// 3D cube grid with lines in RGB for XYZ axes
vec3 cubeGrid(vec3 uvw, float lineThickness) {
    // Side distance of the cube
    vec3 sideDistBig = abs(fract(uvw + 0.5) - 0.5);

    // Smaller scaled side distance of the cube
    float detailScale = 4.0;
    uvw *= detailScale;
    vec3 sideDistDetail = abs(fract(uvw + 0.5) - 0.5);

    // Merge grids
    vec3 joined = min(sideDistBig, sideDistDetail);

    // Create hard outlines of sides
    return step(joined, vec3(lineThickness)); 
}

// viszalizes 3D Vector
vec3 flowPlot(vec3 uvw, vec3 direction, float time) {
    // should probably be input paramters
    float gridScale = 1.3;
    float lineThickness = 0.02;
    float vectorScale = 0.15;

    direction *= vectorScale;

    uvw *= gridScale;

    // Static grid (looks calmer)
    vec3 grid = cubeGrid(uvw, lineThickness);

    // Animated grid
    int count = 7;
    for (int index = 1; index < count; index += 1) {
        // Unique percentage of each individual grid 
        float percent = fract(float(index) / float(count - 1) + time);        
        // Fading brightness
        float brightness = 1.0 - percent;

        // Scale offset
        vec3 offset = percent * direction;

        // Sample grid
        vec3 aniGrid = cubeGrid(uvw - offset, lineThickness);

        // Apply brightness which fades with distance
        aniGrid *= brightness;

        // Add to the existing grid color output
        grid = max(grid, aniGrid);
    }

    return grid;
}

//_____________________Quiver Plot______________________________

// Rotate coordinates in vector direction
vec2 rotateUV(vec2 uv, vec2 direction) {
    // Calculate the rotation matrix
    mat2 rotationMatrix = mat2( direction.y, direction.x,
                                -direction.x, direction.y );

    // Rotate the UV coordinates
    return rotationMatrix * uv;
}

#define SMALLESTFLOAT (1.0 / float(0xffffffffU))
// Generates a grid of arrows that point in the direction
float quiverPlot(vec2 uv, vec2 direction, float gridSize) {
    float lineThickness = 0.03;    // Thickness of the arrow line
    float tipSteepness = 3.0;      // Controls the angle of the arrow tip

    float maxSize = 0.9;           // Maximum arrow length (1 should be the max)
    float minSize = 0.2;           // Minimum arrow length

    // Calculate the square of the length of the vector
    float lenSq = dot(direction, direction);

    // Check if the squared length is greater than a tiny threshold
    if (lenSq > SMALLESTFLOAT) {

        // break UV coordinates into grid sections
        uv = fract(uv * gridSize) - 0.5;

        // caculate the direction vector length    
        float vectorLen = sqrt(lenSq); 

        // Scale uv by vector size
        float size = mix(minSize, maxSize, clamp(vectorLen, 0.,1.)); 
        uv /= size;

        // Rotate UV coordinates based on the normalized direction vector
        uv = rotateUV(uv, direction / vectorLen);

        // absolute position
        float absV = abs(uv.x);

        // Calculate center line of the arrow shape
        float lineOffset = tipSteepness * lineThickness; // offset so lines doesn't go over the tip
        float line = max(absV, abs(uv.y + lineOffset) - 0.5 + lineOffset);

        // Caculation of the Arror Tip
        float tip = max(abs((uv.y / tipSteepness) + absV - (0.5 / tipSteepness) + lineThickness), -uv.y + 0.1);

        // Combine line and tip to create the arrow shape
        float arrowDist = min(tip, line) - lineThickness;

        // Create a mask based on the arrow shape
        return step(arrowDist, 0.0); // no AA
        //float blur = (gridSize * 1.0)/(size * 720.);
        //return  smoothstep(blur, -blur, arrowDist) ; // cheap AA

    } else {
        // if the direction length is 0. then no arrows
        return 0.;
    }    
}
