/*
This shader demonstrates a comparison between two methods for enhancing the 
contrast of weights used in interpolation. A contrast function increases the 
rate of change, while meantaining normalized weights. The first is straight 
contrast, which I may be the first to make for 3 and 4 sample interpolation, 
and the second is smooth contrast, which I have seen only a few times.

Smooth interpolation operates by normalizing the weights and applying a power
factor to them. It is highly adaptable as it can be easily applied to any 
interpolation technique. However, with high power values (~70+), you may start 
to encounter floating point errors.

Straight contrast, on the other hand, technically modifies the coordinates. It 
works pretty straightforwardly for linear weights. However, for higher level 
interpolation, it becomes increasingly complex. It is faster than smooth 
contrast and doesn't have a problem with floating point errors. 

The columns, from left to right, illustrate linear interpolation with both 
Straight and Smooth Contrast, followed by barycentric interpolation with 
the same contrasts, and finally bilinear interpolation with both contrasts 
applied.

This shader allows mouse control for the contrast on the Y-axis and enables 
you to enlarge one of comparison pair columns by moving the X-axis to the 
column center. Global constants below allow you to switch to alternate 
views.

See a performance comparison between the contrast methods here:
https://www.shadertoy.com/view/mtyyR3

Contrast funcitons are also known as a top-k filter:
https://proceedings.mlr.press/v202/sander23a/sander23a.pdf#cite.lim2016efficient
*/

// Enable to show the weights (Alpha); disable to show textures
#define SHOWWEIGHTS

//_____________________________Straight Contrast___________________________

// Linearly increases contrast of Alpha
// See: https://www.desmos.com/calculator/hs1nsjb32q
float straightContrast(float alpha, float contrast) {
    return clamp(contrast * (alpha - 0.5) + 0.5, 0., 1.);
}

// Straight Contrast to 3 Barycentric Weights, oh the pain
// See: https://www.desmos.com/calculator/n23ntt1nys
vec3 straightContrast(vec3 weights, float contrast) {
    // Offset of weights to move towards the center
    // InverseStraightContrast(0)*(1/3)
    float offset = (contrast-1.) / (6. * contrast);

    // Merge the offset with the diagonal offsets
    float offsetA = min(offset, min(weights.y, weights.z) * 0.5);
    float offsetB = min(offset, min(weights.x, weights.z) * 0.5);

    // Apply the contrast to the merged weights
    float contrastA = straightContrast(weights.x + offsetA, contrast);
    float contrastB = straightContrast(weights.y + offsetB, contrast);
    // No proper calculation needed as sum(weights)=1
    float contrastC = clamp(1. - contrastA - contrastB, 0.,1.); // clamp prevents float errors 

    // merge into vector
    return vec3(contrastA, contrastB, contrastC);
}

// Straight Contrast for 4 Bilinear Weights 
// See: https://www.desmos.com/calculator/629sxxdp5g
vec4 straightContrast(vec4 weights, float contrast) {
    // recreate coordinates
    float ab = weights.x + weights.y; // = UV.x
    float ac = weights.x + weights.z; // = UV.y

    // apply contrast
    ab = straightContrast(ab, contrast);
    ac = straightContrast(ac, contrast);

    // recreate weights
    return bilinearWeights(vec2(ab, ac));
}

//_____________________________Smooth Contrast_____________________________

// Increases the steepness of Alpha while preserving 0-1 range and 1 sum
// See: https://www.desmos.com/calculator/hs1nsjb32q
float smoothContrast(float alpha, float contrast) {
    // increase steepness using power
    float powAlpha = pow(alpha, contrast);
    float powInfAlpha = pow(1.-alpha, contrast);

    // normalize back to precentage of 1
    return powAlpha/(powAlpha + powInfAlpha);
}

// Increases the steepness of Alpha while preserving 0-1 range and 1 sum
// See: https://www.desmos.com/calculator/n23ntt1nys
vec3 smoothContrast(vec3 alpha, float contrast) {
    // increase steepness using power
    vec3 powAlpha = pow(alpha, vec3(contrast));

    // normalize back to precentage of 1
    return powAlpha/(powAlpha.x + powAlpha.y + powAlpha.z);
}

// Increases the steepness of Alpha while preserving 0-1 range and 1 sum
// See: https://www.desmos.com/calculator/629sxxdp5g
vec4 smoothContrast(vec4 alpha, float contrast) {
    // increase steepness using power
    vec4 powAlpha = pow(alpha, vec4(contrast));

    // normalize back to precentage of 1
    return powAlpha/(powAlpha.x + powAlpha.y + powAlpha.z + powAlpha.w);
}

//__________________________Blending Functions_____________________________

// blends between two samples using different contrast methods
vec3 linearBlend(vec2 uv, float contrast, bool straight) {

    // Calculate zigzag linear weight
    float weight = abs(fract(uv.y * 0.5) * 2.0 - 1.0);

    // alternate between contrast methods
    if(straight) {
        // contrast for this starts at 0, so to make it comparible
        //contrast -= 1.;

        // a bit cheaper
        weight = straightContrast(weight, contrast);
    } else {
        // prettier and easier to use
        weight = smoothContrast(weight, contrast);
    }

    // Debug view of weights
    #ifdef SHOWWEIGHTS
        // Apply a pretty colormap for visual clarity
        return viridis(weight);
    #endif

    // Apply arbitrary detail scale
    uv *= 0.81;

    // Sample albedo textures
    vec3 colorA = texture(iChannel0, uv).xyz;
    vec3 colorB = bone(texture(iChannel1, uv).x);

    // Interpolation between the textures based on the final weight
    return mix(colorA, colorB, weight);
}

// Barycentric interpolation between 3 samples using different contrast
vec3 baryBlend(vec2 uv, float contrast, bool straight) {

    // Calculate barycentric weights for a square pattern
    vec3 weights = baryTiling(uv);

    if(straight) {
        // contrast for this starts at 0, so to make it comparible
        //contrast -= 1.;

        // Apply straight contrast on weights
        weights = straightContrast(weights, contrast);
    } else {
        // Apply smooth contrast on weights
        weights = smoothContrast(weights, contrast);
    }

    // Debug view of weights
    #ifdef SHOWWEIGHTS
        // Return weights / Alpha values for debugging
        return weights;
    #endif

    // Apply arbitrary detail scale
    uv *= 0.81;

    // Sample albedo textures
    vec3 colorA = texture(iChannel0, uv).xyz;
    vec3 colorB = bone(texture(iChannel1, uv).x);
    vec3 colorC = texture(iChannel2, uv).xyz;

    // Interpolate between the textures based on the final weights
    return colorA * weights.x + colorB * weights.y + colorC * weights.z;
}

// Bilinear interpolation with between 4 samples using different contrast
vec3 bilinearBlend(vec2 uv, float contrast, bool straight) {

    // Calculate vanilla bilinear weights
    vec4 weights = bilinearTiling(uv);

    // Alternatively use smooth contrast on weights 
    if(straight) {
        // contrast for this starts at 0, so to make it comparible
        //contrast -= 1.;

        // Apply straight contrast on weights
        weights = straightContrast(weights, contrast);

    } else {
        // Apply smooth contrast on weights
        weights = smoothContrast(weights, contrast);
    }    

    // Debug view of weights
    #ifdef SHOWWEIGHTS
        // Show weights / Alpha values
        return weights.xyz;
    #endif

    // Apply arbitrary detail scale
    uv *= 0.81;

    // Sample albedo textures
    vec3 colorA = texture(iChannel0, uv).xyz;
    vec3 colorB = bone(texture(iChannel1, uv).x);
    vec3 colorC = texture(iChannel2, uv).xyz;
    vec3 colorD = texture(iChannel3, uv).xyz;

    // Interpolation between the textures
    vec3 color = colorA * weights.x + 
                 colorB * weights.y + 
                 colorC * weights.z +
                 colorD * weights.w;
    return color;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {   
    // Calculate UVs for the x-axis of the window
    vec2 uv = fragCoord / iResolution.x;

    // Calculate UVs for the blend grids
    vec2 gridUV = uv * 6.0;

    // Create a float to divide into sections for comparisons
    float comparisonX = uv.x * 6.0;

    // Enable mouse controls when clicked; otherwise, animate
    float contrast = 1.;
    float pointer = 0.;
    if (iMouse.x > 0.0) {
        // Mouse controls
        vec2 mouse = iMouse.xy / iResolution.xy;
        contrast = mix(1.0, 8.0, pow(mouse.y, 2.0));
        pointer = mouse.x * 3.0;
    } else {
        // Animation when no mouse input is used
        float myTime = iTime * 1.0;
        contrast = mix(1.0, 8.0, pow(sin(myTime) * 0.5 + 0.5, 2.0));
    }

    // Create zigzag weights
    float weight = abs(fract(pointer - 0.5) * 2.0 - 1.0);

    // make transitions deadzones for easier use
    weight = straightContrast(weight, 2.);

    // Define default edge locations for the columns
    vec2 columnEdges = vec2(2., 4.); 
    vec3 columnCenters = vec3(1., 3., 5.); 

    // Mouse-controlled animations to zoom into the columns    
    vec2 zoomColumnEdges = columnEdges;
    vec3 zoomColumnCenters = columnCenters;
    if (pointer <= 1.0) {
        // Zoom into the left side        
        zoomColumnEdges = vec2(5.2, 5.6);
        zoomColumnCenters = vec3(2.6 ,5.4, 5.8);
    } else if (pointer <= 2.0) {
        // Zoom into the center column
        zoomColumnEdges = vec2(0.4, 5.6);
        zoomColumnCenters = vec3(0.2, 3.0, 5.8);
    } else {
        // Zoom into the right column
        zoomColumnEdges = vec2(0.4, 0.8);
        zoomColumnCenters = vec3(0.2, 0.6, 3.4);
    }

    // Interpolate between default column edge positions and zoom
    columnEdges = mix(columnEdges, zoomColumnEdges, weight);  
    columnCenters = mix(columnCenters, zoomColumnCenters, weight);

    // Choose interpolation method based on the x-coordinate
    vec3 color;
    if (comparisonX <= columnCenters.x) {
        // straight contrast linear interpolation
        color = linearBlend(gridUV, contrast, true);

    } else if (comparisonX <= columnEdges.x) {
        // smooth contrast linear interpolation
        color = linearBlend(gridUV, contrast, false);

    } else if (comparisonX <= columnCenters.y) {
        // straight contrast barycentric interpolation
        color = baryBlend(gridUV, contrast, true);

    } else if (comparisonX <= columnEdges.y) {
        // smooth contrast barycentric interpolation
        color = baryBlend(gridUV, contrast, false);

    } else if (comparisonX <= columnCenters.z) {
        // straight contrast bilinear interpolation
        color = bilinearBlend(gridUV, contrast, true);

    } else {
        // smooth contrast bilinear interpolation
        color = bilinearBlend(gridUV, contrast, false);
    }

    // Add lines between columns
    float thickness = 0.005;       
    color += step(abs(comparisonX - columnEdges.x), thickness*2.);
    color += step(abs(comparisonX - columnEdges.y), thickness*2.);
    color += step(abs(comparisonX - columnCenters.x), thickness);
    color += step(abs(comparisonX - columnCenters.y), thickness);
    color += step(abs(comparisonX - columnCenters.z), thickness);

    // Add arrows pointing at the center of columns
    color += step(uv.y + abs(fract(comparisonX*0.5)-0.5), thickness*5.);

    // Set the output color
    fragColor = vec4(color, 1.0);
}

// Color Maps ____________________________________________________________________

// makes viridis colormap with polynimal 6
vec3 viridis(float t) {
    const vec3 c0 = vec3(0.274344,0.004462,0.331359);
    const vec3 c1 = vec3(0.108915,1.397291,1.388110);
    const vec3 c2 = vec3(-0.319631,0.243490,0.156419);
    const vec3 c3 = vec3(-4.629188,-5.882803,-19.646115);
    const vec3 c4 = vec3(6.181719,14.388598,57.442181);
    const vec3 c5 = vec3(4.876952,-13.955112,-66.125783);
    const vec3 c6 = vec3(-5.513165,4.709245,26.582180);
    return c0+t*(c1+t*(c2+t*(c3+t*(c4+t*(c5+t*c6)))));
}

// makes bone colormap with polynimal 6
vec3 bone(float t) {
    const vec3 c0 = vec3(-0.005007,-0.003054,0.004092);
    const vec3 c1 = vec3(1.098251,0.964561,0.971829);
    const vec3 c2 = vec3(-2.688698,-0.537516,2.444353);
    const vec3 c3 = vec3(12.667310,-0.657473,-8.158684);
    const vec3 c4 = vec3(-27.183124,8.398806,10.182004);
    const vec3 c5 = vec3(26.505377,-12.576925,-5.329155);
    const vec3 c6 = vec3(-9.395265,5.416416,0.883918);
    return c0+t*(c1+t*(c2+t*(c3+t*(c4+t*(c5+t*c6)))));
}

// Interpolation ________________________________________________________________

// Make bilinear weights
vec4 bilinearWeights(vec2 zigZag) {

    // Invert zigzag as a partial of the weights
    vec2 infZigZag = 1.0 - zigZag;

    // Generate alpha values (weights) for bilinear interpolation.
    float weightA = zigZag.x * zigZag.y;
    float weightB = zigZag.x * infZigZag.y;
    float weightC = infZigZag.x * zigZag.y;
    float weightD = infZigZag.x * infZigZag.y;

    // Combine weight/alpha values. 
    return vec4(weightA, weightB, weightC, weightD);
}

// Make tiling bilinear weights
vec4 bilinearTiling(vec2 uv) {
    // Generate zigzag pattern
    vec2 zigZag = abs(fract(uv * 0.5) * 2.0 - 1.0);

    // Generate weights
    return bilinearWeights(zigZag);
}

// Make square barycentric weights
vec3 baryWeights(vec2 zigZag) {

    // make individual weights for pattern
    float weightA = min(zigZag.x, zigZag.y);
    float weightB = min(1.-zigZag.x, 1.-zigZag.y);
    float weightC = 1. - weightA - weightB;

    // when to alternate the 2 C samples
    //float switchC = float(zigZag.x < zigZag.y);

    // Join weights together
    return vec3(weightA, weightB, weightC);//, switchC);
}

// Make tiling barycentric weights
vec3 baryTiling(vec2 uv) {
    // Generate zigzag pattern
    vec2 zigZag = abs(fract(uv * 0.5) * 2.0 - 1.0);

    // Generate weights
    return baryWeights(zigZag);
}
