float f_n_rnd(float n){

    n = fract(n*.1031);
    n *= n+33.33;
    n *= n+n;
    return fract(n);

}
vec2 f_o_rnd(vec2 p)
{
	vec3 p3 = fract(vec3(p.xyx) * vec3(.1031, .1030, .0973));
    p3 += dot(p3, p3.yzx+33.33);
    return fract((p3.xx+p3.yz)*p3.zy);

}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 o_fc = (fragCoord.xy - iResolution.xy*.5)/iResolution.y;
    vec2 o_fccp = o_fc;
    vec2 o_mc = (iMouse.xy - iResolution.xy*.5)/iResolution.y;
    if(iMouse.z == 0.){
        float n_amp = sin(iTime*0.2);
        o_mc = vec2(sin(iTime), cos(iTime))*n_amp;
    }
    vec2 o_mccp = o_mc;

    o_fc *= 18.;
    o_mc *= 18.;
    float n_tau =6.2831;
    float n_amp = 2.;
    float n_dmin = 1.;

    vec2 o_fcmcdlt = o_fc-o_mc;
    float n_ang = fract(atan(o_fcmcdlt.y, o_fcmcdlt.x)/n_tau);
    float n_intensity = clamp(length(o_fccp - o_mccp), 0., 1.);
    vec2 o_fcf = fract(o_fc)-.5;
    vec2 o_sclk = vec2(9.);
    float n_min = floor(o_sclk.x/2.);
    for(float n_x = -n_min; n_x <= n_min; n_x+=1.){
        for(float n_y = -n_min; n_y <= n_min; n_y+=1.){
            vec2 o_fck = vec2(n_x, n_y);
            vec2 o_rnd = f_o_rnd(floor(o_fc)+o_fck);
            vec2 o_amp_rnd = n_amp*o_rnd;
            n_intensity = n_ang*(1.-length(o_fcmcdlt));
            vec2 o_dlt = (floor(o_fc)+o_fck)-o_mc;
            float n_ang = atan(o_dlt.x, o_dlt.y);
            vec2 o_off = vec2(
                    sin(n_ang)*n_amp,//sin(iTime+(o_fc.x/2.)),//sin(iTime+o_rnd.x)*o_amp_rnd.x, 
                    cos(n_ang)*n_amp//sin(iTime+(o_fc.y/2.))//0.//cos(iTime+o_rnd.y)*o_amp_rnd.y 
            );

            o_fck += o_off;

            float n = length(o_fcf-o_fck);
            if(n < n_dmin ){
                n_dmin = n;
            }
        }
    }
    fragColor = vec4(sqrt(n_dmin));
}
