//
//  Interesting optical illusion! Stare at the green dot, and one or more of the yellow
//  dots will seem to disappear.
//
//  Reproduction of this tweet:
//
//      https://twitter.com/gunsnrosesgirl3/status/1610211567966224387
//  
//  See also:
//
//      https://whyevolutionistrue.com/2014/09/02/why-do-the-dots-disappear/
//
//      "Jack Pettigrew, a neuroscientist at the University of Queensland in Brisbane,
//      believes that the illusion results from a tussle for supremacy between the left
//      and right halves of the brain."
//
// ---------------------------------------------------------------------------------------

// First attempt at golfing by me: (384 chars)
//*
#define D(X, Y) smoothstep(.01 + 3./R.y, .01, length(u - vec2(X, Y))) // dot
#define L(X) smoothstep(27./R.y, 0., max(abs(X.y), abs(X.x) - .25))   // line

void mainImage( out vec4 O, vec2 u )
{
    vec2 w, R = iResolution.xy,
         v = (u = 1.2 * (u - .5 * R) / R.y) * mat2(w = sin(iTime+vec2(1.6,0)), -w.y, w.x),
         m = fract(v * 8. - .5) - .5;

    O = vec4(w.x = D(   0, -.3)
                 + D(-.26,  .15)
                 + D( .26,  .15),
             fract(iTime) > .5 ? D(0, 0) + w.x : w.x,
             abs(v.x) < .45 && abs(v.y) < .45 ? L(m) + L(m.yx) : 0.,
             1);
}
/**/

// Original: (710 chars)
/*
mat2 r(float a) { vec2 s = vec2(sin(a), cos(a)); return mat2(s.y, s.x, -s.x, s.y); }

void mainImage( out vec4 O, vec2 u )
{
    vec2 R = iResolution.xy;
    u = 1.2*(u - .5*R) / R.y;

    O = vec4(0);

    vec2 v = u * r(iTime);

    if (v.x < .45 && v.x > -.45 && v.y < .45 && v.y > -.45)
    {
        vec2 m = mod(v - .0625, .125) - .0625;
        O = mix(vec4(0,0,1,1), O, smoothstep(0., 3./R.y, max(abs(m.y), abs(m.x) - .03125)));
        O = mix(vec4(0,0,1,1), O, smoothstep(0., 3./R.y, max(abs(m.y) - .03125, abs(m.x))));
    }

    if (fract(iTime) > .5)
        O = mix(vec4(0,1,0,1), O, smoothstep(.01, .01 + 3./R.y, length(u)));

    O = mix(vec4(1,1,0,1), O, smoothstep(.01, .01 + 3./R.y, length(u - vec2(0, -.3))));
    O = mix(vec4(1,1,0,1), O, smoothstep(.01, .01 + 3./R.y, length(r(2.1) * u - vec2(0, -.3))));
    O = mix(vec4(1,1,0,1), O, smoothstep(.01, .01 + 3./R.y, length(r(4.1) * u - vec2(0, -.3))));
}
/**/
