void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 o_trn_fc_nor = fragCoord.xy / iResolution.xy;
    vec2 o_trn_mo_nor = iMouse.xy / iResolution.xy;

    float n_ratio_x_to_y = iResolution.x / iResolution.y; 

    vec2 o_trn_fc_nor_offset = (o_trn_fc_nor - vec2(0.5))*vec2(n_ratio_x_to_y, 1.);
    vec2 o_trn_mo_nor_offset = (o_trn_mo_nor - vec2(0.5))*vec2(n_ratio_x_to_y, 1.);

    vec2 o_diff = vec2(0.)-o_trn_fc_nor_offset;
    float n_tau = 6.2831;
    float n_ang_nor = fract(((atan(o_diff.y, o_diff.x)+(n_tau/2.))/n_tau));

    float n_its = 200.;

    float n_ang_nor_snapped = floor(n_ang_nor * n_its)/n_its;

    float n_it = floor(n_ang_nor * n_its);
    float n_radius = (sin(iTime+(n_tau/n_its)*n_it)*0.5+0.5)*0.4;
    vec2 o_p = vec2(
        cos((n_ang_nor_snapped+(1./n_its/2.))*n_tau)*n_radius, 
        sin((n_ang_nor_snapped+(1./n_its/2.))*n_tau)*n_radius
    );

    float n_dist = length(o_p-o_trn_fc_nor_offset);


    fragColor = vec4(n_dist);
}
