/*
q - fire upper left thruster
a - fire lower left thruster
s - fire main (bottom) thruster
d - fire lower right thruster
e - fire upper right thruster
space - increase main thrust

Or instead of q/a/s/d/e you can use the arrow keys.
If the arrow controls feel inverted to you,
uncomment CONTROLS_INVERTED in the Common tab.

Try and hit platforms!

Pro tip: to right the rocket after a crash landing,
try jostling the rocket with small pulses from the
main thruster and then catch it before it over
corrects with the auxiliary thrusters.

Or just hit r to engage the emergency break :)
*/

#define drawSDF(dist, col) color = mix(color, col, smoothstep(unit, 0.0, dist))
void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    // Setup
    float aspect = iResolution.x / iResolution.y;
    vec2 uv = (fragCoord - 0.5 * iResolution.xy) / iResolution.y;
    float unit = 2.0 / iResolution.y;
    vec3 color = vec3(0.0);

    // Load rocket state
    vec4 stateBlock1 = load(iChannel0, ivec2(0, 0));
    vec4 stateBlock2 = load(iChannel0, ivec2(1, 0));

    vec2 pos = stateBlock1.xy, vel = stateBlock1.zw;
    float angle = stateBlock2.x, angVel = stateBlock2.y;
    float score = stateBlock2.z, flag = stateBlock2.w;

    // Camera zoom
    float zoom = clamp(length(pos - INITIAL_POSITION) / 0.6, 1.0, 5.0);

    // Reference frame
    vec2 refUv = uv - aspectCorrect(REF_FRAME_POS, aspect);
    if (abs(refUv.x) < 0.2 && abs(refUv.y) < 0.2) {
        uv = refUv;
    }

    // Zoom out on the scene
    else {
        uv *= zoom;
        unit *= zoom;
    }

    // Draw environment
    vec2 envUv = uv + pos;

    // Reference grid
    drawSDF(abs(mod(envUv.x, 0.25) - 0.125), vec3(0.25));
    drawSDF(abs(mod(envUv.y, 0.25) - 0.125), vec3(0.25));

    vec3 envMaterial = TERRAIN_COLOR;
    if (envUv.y > PLATFORM_OFFSET - PLATFORM_THICKNESS - 2.0 * unit) { // In the area populated with platforms
        envMaterial = PLATFORM_COLOR; // Default platform color
        float cell = round(envUv.x / PLATFORM_SPACING) * PLATFORM_SPACING;
        for (int platform=0; platform < int(score); platform++) {
            if (load(iChannel0, ivec2(platform, 1)).x == cell) { // Has been collided with
                envMaterial = PLATFORM_HIT_COLOR; // Platform color after collision
                break;
            }
        }
    }

    drawSDF(mapEnv(envUv), envMaterial);

    // Thruster animation
    vec2 rocketUv = Rotate2D(-angle) * uv;

    // Draw upper left thruster flame
    if (keypress(iChannel1, KEY_q)) {
        vec2 thrusterUv = axisAlign(rocketUv - thrusters[0].xy, thrusters[0].zw);
        drawSDF(sdIsosceles2(thrusterUv, vec2(0.02, FLAME_SIZE)), vec3(1.0, 0.3, 0.0));
        drawSDF(sdIsosceles2(thrusterUv, FLAME_RATIO * vec2(0.02, FLAME_SIZE)), vec3(1.0, 1.0, 0.0));
    }

    // Draw lower left thruster flame
    if (keypress(iChannel1, KEY_a) || keypress(iChannel1, LEFT_CONTROL)) {
        vec2 thrusterUv = axisAlign(rocketUv - thrusters[1].xy, thrusters[1].zw);
        drawSDF(sdIsosceles2(thrusterUv, vec2(0.02, FLAME_SIZE)), vec3(1.0, 0.3, 0.0));
        drawSDF(sdIsosceles2(thrusterUv, FLAME_RATIO * vec2(0.02, FLAME_SIZE)), vec3(1.0, 1.0, 0.0));
    }

    // Draw main thruster flame
    if (keypress(iChannel1, KEY_s) || keypress(iChannel1, MAIN_CONTROL)) {
        float mainFlameSize = MAIN_FLAME_SIZE;
        if (keypress(iChannel1, KEY_SPACE)) {
            mainFlameSize = MAX_FLAME_SIZE;
        }

        vec2 thrusterUv = axisAlign(rocketUv - thrusters[2].xy, thrusters[2].zw);
        drawSDF(sdIsosceles2(thrusterUv, vec2(0.02, mainFlameSize)), vec3(1.0, 0.3, 0.0));
        drawSDF(sdIsosceles2(thrusterUv, FLAME_RATIO * vec2(0.02, mainFlameSize)), vec3(1.0, 1.0, 0.0));
    }

    // Draw lower right thruster flame
    if (keypress(iChannel1, KEY_d) || keypress(iChannel1, RIGHT_CONTROL)) {
        vec2 thrusterUv = axisAlign(rocketUv - thrusters[3].xy, thrusters[3].zw);
        drawSDF(sdIsosceles2(thrusterUv, vec2(0.02, FLAME_SIZE)), vec3(1.0, 0.3, 0.0));
        drawSDF(sdIsosceles2(thrusterUv, FLAME_RATIO * vec2(0.02, FLAME_SIZE)), vec3(1.0, 1.0, 0.0));
    }

    // Draw upper right thruster flame
    if (keypress(iChannel1, KEY_e)) {
        vec2 thrusterUv = axisAlign(rocketUv - thrusters[4].xy, thrusters[4].zw);
        drawSDF(sdIsosceles2(thrusterUv, vec2(0.02, FLAME_SIZE)), vec3(1.0, 0.3, 0.0));
        drawSDF(sdIsosceles2(thrusterUv, FLAME_RATIO * vec2(0.02, FLAME_SIZE)), vec3(1.0, 1.0, 0.0));
    }

    // Draw rocket
    rocketUv = vec2(abs(rocketUv.x), rocketUv.y - 0.0243594417401);
    drawSDF(sdIsosceles(rocketUv + vec2(0.0, 0.09), vec2(0.03, 0.08)), vec3(1.0, 0.0, 0.0));
    drawSDF(sdIsosceles(Rotate2D(-0.7) * (rocketUv - vec2(0.08, -0.06)), vec2(0.03, 0.08)), vec3(0.0, 1.0, 0.0));
    drawSDF(sdIsosceles(Rotate2D(-1.8) * (rocketUv - vec2(0.07, 0.03)), vec2(0.03, 0.08)), vec3(0.0, 0.0, 1.0));
    drawSDF(sdIsosceles(rocketUv, vec2(0.08, 0.2)), vec3(1.0));

    if (abs(refUv.x) > 0.2 || abs(refUv.y) > 0.2) {
        // Reset zoom
        uv /= zoom;
        unit /= zoom;

        // Draw velocity vector
        vec2 vectorBase = normalize(vel) * 0.2;
        drawSDF(sdVectorArrow(uv - vectorBase, 0.05 * vel), vec3(1.0));

        // Show score
        color = digitIn(color, vec3(1.0), uv - aspectCorrect(SCORE_POS, aspect), 100.0, score);
    }

    // Outline the reference frame
    float frame = sdBox(refUv, vec2(0.2));
    drawSDF(abs(frame) - 0.001, vec3(1.0));

    fragColor = vec4(color, 1.0);
}

// Constants
#define PI 3.1415926536
#define EPS 0.001

#define GRAVITY 0.6
#define FRICTION 0.5

#define KEY_q     81
#define KEY_a     65
#define KEY_s     83
#define KEY_d     68
#define KEY_e     69
#define KEY_r     82
#define KEY_SPACE 32
#define KEY_LEFT  37
#define KEY_UP    38
#define KEY_RIGHT 39
#define KEY_DOWN  40

// Uncomment if the arrow controls feel inverted to you
//#define CONTROLS_INVERTED

// Alternate controls
#ifdef CONTROLS_INVERTED
#define LEFT_CONTROL KEY_RIGHT
#define MAIN_CONTROL KEY_UP
#define RIGHT_CONTROL KEY_LEFT
#else
#define LEFT_CONTROL KEY_LEFT
#define MAIN_CONTROL KEY_DOWN
#define RIGHT_CONTROL KEY_RIGHT
#endif

// GUI settings
#define SCORE_POS vec2(-0.52, -0.442)
#define REF_FRAME_POS vec2(0.359, -0.25)

// Rocket settings
#define MAIN_THRUST 15.0
#define MAX_THRUST 25.0 // For main thruster
#define AUXILIARY_THRUST 0.04 // Mainly for small angle corrections

#define FLAME_SIZE 0.04 // Thruster flame length
#define FLAME_RATIO 0.4 // Changes inner flame size
#define MAIN_FLAME_SIZE 0.06
#define MAX_FLAME_SIZE 0.09 // For main thruster

#define MASS 5.0
#define MOI 0.000144426534534

#define INITIAL_POSITION vec2(0.0, 2.75)

// Environment settings
#define PLATFORM_WIDTH 0.65
#define PLATFORM_THICKNESS 0.075
#define PLATFORM_OFFSET 2.5
#define PLATFORM_SPACING 1.5
#define PLATFORM_VARIATION 20.0
#define PLATFORM_COLOR vec3(0.6)
#define PLATFORM_HIT_COLOR vec3(1.0, 0.8, 0.0)

#define TERRAIN_HEIGHT 2.0
#define TERRAIN_EXAGGERATION 4.0
#define TERRAIN_ROUGHNESS 2
#define TERRAIN_COLOR vec3(0.8, 0.5, 0.0)

// Utilities
#define load(buf, addr) texelFetch(buf, addr, 0)
#define keypress(buf, key) (texelFetch(buf, ivec2(key, 0), 0).x > 0.5)
#define aspectCorrect(p, aspect) vec2(p.x * aspect, p.y)

vec2 perp(in vec2 v) {
    return vec2(-v.y, v.x);
}

mat2 Rotate2D(in float a) {
    float co = cos(a), si = sin(a);
    return mat2(co, si, -si, co);
}

vec2 axisAlign(in vec2 p, in vec2 up) {
    return p * mat2(up.y, -up.x, up);
}

// https://www.shadertoy.com/view/4djSRW
float noise(in float p) {
    p = fract(p * 0.1031);
    p *= p + 33.33;
    p *= p + p;
    return fract(p);
}

float snoise(in float p) {
    return mix(noise(floor(p)), noise(ceil(p)), smoothstep(0.0, 1.0, fract(p)));
}

float fbm(in float p) {
    float val = 0.0;
    float noiseScale = 1.0;
    float totalScale = 0.0;
    for (int oct=0; oct < TERRAIN_ROUGHNESS; oct++) {
        val += snoise(p) * noiseScale;
        totalScale += noiseScale;
        noiseScale *= 0.5;
        p *= 2.0;
    }

    return val / totalScale;
}

// SDFs
float sdVerticalLine(in vec2 p, in float h) {
    return length(vec2(p.x, p.y - h * clamp(p.y / h, 0.0, 1.0)));
}

float sdIsosceles(in vec2 p, in vec2 q) {
    p = vec2(abs(p.x), 2.0 * q.y / 3.0 - p.y);
    vec2 a = p - q * clamp(dot(p, q) / dot(q, q), 0.0, 1.0);
    vec2 b = p - q * vec2(clamp(p.x / q.x, 0.0, 1.0), 1.0);
    float s = -sign(q.y);
    vec2 d = min(vec2(dot(a, a), s * (p.x * q.y - p.y * q.x)), vec2(dot(b, b), s * (p.y - q.y)));
    return -sqrt(d.x) * sign(d.y);
}

// Uncentered isosceles triangle
float sdIsosceles2(in vec2 p, in vec2 q) {
    p = vec2(abs(p.x), q.y - p.y);
    vec2 a = p - q * clamp(dot(p, q) / dot(q, q), 0.0, 1.0);
    vec2 b = p - q * vec2(clamp(p.x / q.x, 0.0, 1.0), 1.0);
    float s = -sign(q.y);
    vec2 d = min(vec2(dot(a, a), s * (p.x * q.y - p.y * q.x)), vec2(dot(b, b), s * (p.y - q.y)));
    return -sqrt(d.x) * sign(d.y);
}

float sdBox(in vec2 p, in vec2 b) {
    p = abs(p) - b;
    return length(max(p, 0.0)) + min(0.0, max(p.x, p.y));
}

float sdVectorArrow(in vec2 p, in vec2 v) {
    float mag = length(v);
    p = axisAlign(p, v / mag);
    return min(sdVerticalLine(p, mag), sdIsosceles(p - vec2(0.0, mag), vec2(0.02, 0.03)));
}

// Rocket geometry
const vec2[11] convexHull = vec2[11](
    vec2( 0.0000000000,  0.1576927751),
    vec2(-0.0891532073,  0.0896335932),
    vec2(-0.1027853330,  0.0312027353),
    vec2(-0.1201244039, -0.0367098193),
    vec2(-0.0742338727, -0.0753628805),
    vec2(-0.0300000000, -0.0923072249),
    vec2( 0.0300000000, -0.0923072249),
    vec2( 0.0742338727, -0.0753628805),
    vec2( 0.1201244039, -0.0367098193),
    vec2( 0.1027853330,  0.0312027353),
    vec2( 0.0891532073,  0.0896335932)
);

// vec4(position, direction)
// Listed in counterclockwise order from the upper left
const vec4[5] thrusters = vec4[5](
    vec4(-0.0959692702,  0.0604181643, -0.9738476309,  0.2272020947),
    vec4(-0.0971791383, -0.0560363499, -0.6442176872, -0.7648421873),
    vec4( 0.0000000000, -0.0923072249,  0.0000000000, -1.0000000000),
    vec4( 0.0971791383, -0.0560363500,  0.6442176872, -0.7648421873),
    vec4( 0.0959692702,  0.0604181643,  0.9738476309,  0.2272020947)
);

// Environment distance
float mapEnv(in vec2 p) {
    float ground = p.y - TERRAIN_HEIGHT * pow(fbm(p.x), TERRAIN_EXAGGERATION);

    // Randomly positioned platforms
    float cell = round(p.x / PLATFORM_SPACING) * PLATFORM_SPACING;
    p -= vec2(cell, PLATFORM_VARIATION * noise(cell) + PLATFORM_OFFSET);
    float platforms = max(abs(p.x) - PLATFORM_WIDTH, abs(p.y) - PLATFORM_THICKNESS);

    return min(ground, platforms);
}

// Rocket distance
float mapRocket(in vec2 p) {
    p = vec2(abs(p.x), p.y - 0.0243594417401);
    float dist = sdIsosceles(p + vec2(0.0, 0.09), vec2(0.03, 0.08));
    dist = min(dist, sdIsosceles(Rotate2D(-0.7) * (p - vec2(0.08, -0.06)), vec2(0.03, 0.08)));
    dist = min(dist, sdIsosceles(Rotate2D(-1.8) * (p - vec2(0.07, 0.03)), vec2(0.03, 0.08)));
    dist = min(dist, sdIsosceles(p, vec2(0.08, 0.2)));
    return dist;
}

