const int CHARACTERS[14] = int[14](31599, 9362, 31183, 31207, 23524, 29671, 29679, 30994, 31727, 31719, 1488, 448, 2, 3640);

float digitIsOn(in int digit, in vec2 id) {   
    if (id.x < 0.0 || id.y < 0.0 || id.x > 2.0 || id.y > 4.0 ) return 0.0;
    return floor(mod(float(CHARACTERS[int(digit)]) / pow(2.0, id.x + id.y * 3.0), 2.0));
}

float digitSign(in float v, in vec2 id) {
    return digitIsOn(10 - int((sign(v) - 1.0) * 0.5), id);
}

int digitCount(in float v) {
    return int(floor(log(max(v, 1.0) ) / log(10.0)));
}

float digitFirst(in vec2 uv, in float scale, in float v, in int decimalPlaces) {
    vec2 id = floor(uv * scale);

    if (0.0 < digitSign(v, id)) return 1.0;
    v = abs(v);

    int digits = digitCount(v);
    float power = pow(10.0, float(digits));

    float offset = floor(0.1 * scale);
    id.x -= offset;

    float n;
    for (int i = 0; i < 3; i++, id.x -= offset, v -= power * n, power /= 10.0) {
        n = floor(v / power);
        if (0.0 < digitIsOn(int(n), id)) return 1.0;   
        if (i == digits) {
            id.x -= offset;
            if (0.0 < digitIsOn(int(12), id)) return 1.0;
        }

        if (i >= digits + decimalPlaces) return 0.0;
    }

    return 0.0;
}

float digitFirst(in vec2 uv, in float scale, in float v) {
    return digitFirst(uv, scale, v, 3);
}

vec3 digitIn(in vec3 color, in vec3 toMix, in vec2 uv, in float scale, in float v) {
    float f = digitFirst(uv, scale, v);
    return mix(color, toMix, f);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    fragColor = vec4(0.0);
    ivec2 iAddr = ivec2(fragCoord);

    // Initialize
    if (iFrame == 0) {
        if (iAddr == ivec2(0, 0)) fragColor.xy = INITIAL_POSITION;
    }

    // Update
    if (iFrame > 0) {
        if (iAddr.y < 2) { // Save calculations for the relevant pixels
            // Load previous state
            fragColor = texelFetch(iChannel0, iAddr, 0);
            vec4 stateBlock1 = load(iChannel0, ivec2(0, 0));
            vec4 stateBlock2 = load(iChannel0, ivec2(1, 0));

            vec2 pos = stateBlock1.xy, vel = stateBlock1.zw;
            float angle = stateBlock2.x, angVel = stateBlock2.y;
            float score = stateBlock2.z, flag = stateBlock2.w;
            mat2 basis = Rotate2D(angle);

            // Get the closest rocket vertex to the environment
            // Note that even checking the full list of vertices is not enough
            // but since we're being cheap, the convex hull is fine
            vec2 contact = basis * convexHull[0] + pos;
            float contactDist = mapEnv(contact);
            for (int i=1; i < 11; i++) {
                vec2 newContact = basis * convexHull[i] + pos;
                float newContactDist = mapEnv(newContact);
                if (newContactDist < contactDist) {
                    contact = newContact;
                    contactDist = newContactDist;
                }

                // In the rare case of a parallel edge/edge collision
                // Really only occurs when everything is first initialized
                if (abs(newContactDist - contactDist) < 1e-6) {
                    contact = (contact + newContact) / 2.0;
                }
            }

            // Align thrust vectors to the rocket's new orientation
            vec4[5] orientedThrusters;
            for (int i=0; i < 5; i++) {
                orientedThrusters[i].xy = basis * thrusters[i].xy;
                orientedThrusters[i].zw = basis * thrusters[i].zw;
            }

            // Linear and angular forces
            vec2 force = vec2(0.0);
            float torque = 0.0;

            // Apply forces
            force.y -= GRAVITY * MASS;

            // Upper left thruster
            if (keypress(iChannel1, KEY_q)) {
                vec2 newForce = -orientedThrusters[0].zw * AUXILIARY_THRUST;
                force += newForce;
                torque += cross(vec3(orientedThrusters[0].xy, 0.0), vec3(newForce, 0.0)).z;
            }

            // Lower left thruster
            if (keypress(iChannel1, KEY_a) || keypress(iChannel1, LEFT_CONTROL)) {
                vec2 newForce = -orientedThrusters[1].zw * AUXILIARY_THRUST;
                force += newForce;
                torque += cross(vec3(orientedThrusters[1].xy, 0.0), vec3(newForce, 0.0)).z;
            }

            // Main thruster
            if (keypress(iChannel1, KEY_s) || keypress(iChannel1, MAIN_CONTROL)) {
                float mainThrust = MAIN_THRUST;
                if (keypress(iChannel1, KEY_SPACE)) {
                    mainThrust = MAX_THRUST;
                }

                vec2 newForce = -orientedThrusters[2].zw * mainThrust;
                force += newForce;
                torque += cross(vec3(orientedThrusters[2].xy, 0.0), vec3(newForce, 0.0)).z;
            }

            // Lower right thruster
            if (keypress(iChannel1, KEY_d) || keypress(iChannel1, RIGHT_CONTROL)) {
                vec2 newForce = -orientedThrusters[3].zw * AUXILIARY_THRUST;
                force += newForce;
                torque += cross(vec3(orientedThrusters[3].xy, 0.0), vec3(newForce, 0.0)).z;
            }

            // Upper right thruster
            if (keypress(iChannel1, KEY_e)) {
                vec2 newForce = -orientedThrusters[4].zw * AUXILIARY_THRUST;
                force += newForce;
                torque += cross(vec3(orientedThrusters[4].xy, 0.0), vec3(newForce, 0.0)).z;
            }

            // Collision handling
            if (contactDist < 0.0) {
                float d0 = mapEnv(contact);
                vec2 contactNormal = normalize(vec2(mapEnv(contact + vec2(EPS, 0.0)), mapEnv(contact + vec2(0.0, EPS))) - d0);
                pos -= contactNormal * d0;
                contact -= pos;

                // Calculate force required to reflect contact point's velocity
                // I'm not sure if this is correct, but it gives reasonable behavior
                vec2 contactVel = vel + vec2(-contact.y, contact.x) * angVel;
                mat2 sys = mat2(
                    MOI / MASS + contact.y * contact.y, -contact.x * contact.y,
                    -contact.x * contact.y, MOI / MASS + contact.x * contact.x
                );

                vec2 deltaV = FRICTION * reflect(contactVel, contactNormal) - contactVel;
                vec2 collisionForce = inverse(sys) * (deltaV * MOI / iTimeDelta);

                // Apply force
                force += collisionForce;
                torque += cross(vec3(contact, 0.0), vec3(collisionForce, 0.0)).z;

                // Check for a platform collision
                contact += pos;
                float cell = round(contact.x / PLATFORM_SPACING) * PLATFORM_SPACING;
                vec2 toPlatform = contact - vec2(cell, PLATFORM_VARIATION * noise(cell) + PLATFORM_OFFSET);
                bool newPlatformCollision = abs(toPlatform.x) <= PLATFORM_WIDTH && abs(toPlatform.y) <= PLATFORM_THICKNESS;
                if (newPlatformCollision) {
                    // Check if this platform has already been collided with
                    for (int platform=0; platform < int(score); platform++) {
                        if (load(iChannel0, ivec2(platform, 1)).x == cell) {
                            newPlatformCollision = false;
                            break;
                        }
                    }
                }

                // New high score!
                if (newPlatformCollision) {
                    if (iAddr == ivec2(score, 1)) fragColor.x = cell; // Remember this platform
                    score++;
                }
            }

            // Emergency brake!
            if (keypress(iChannel1, KEY_r)) {
                force = vec2(0.0);
                vel = vec2(0.0);
                angle = 0.0;
                angVel = 0.0;
            }

            // Simulation step
            vel += force / MASS * iTimeDelta;
            pos += vel * iTimeDelta;

            angVel += torque / MOI * iTimeDelta;
            angle += angVel * iTimeDelta;

            // Store rocket state
            if (iAddr == ivec2(0, 0)) fragColor = vec4(pos, vel);
            if (iAddr == ivec2(1, 0)) fragColor = vec4(angle, angVel, score, flag);
        }
    }
}
