#define PX 30./iResolution.y

float c(vec2 p, float r){return smoothstep(PX, 0., length(p) - r);}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (2.0 * fragCoord - iResolution.xy)/iResolution.y;
    float r = texture(iChannel0, abs(uv)).r;
    float s = texture(iChannel0, -abs(uv)).r;
    float warp = sin(uv.x/1.5*1000.)*cos(iTime*10.+uv.x*8.)*0.1;

    vec2 eyeUv = vec2(abs(uv.x),uv.y) - vec2(0.5,0.5);
    eyeUv.x += warp*.25*r*2.;
    float eyes = c(eyeUv, 0.1);

    vec2 mouthUv = uv / vec2(1.5,1.) + vec2(0.,warp*s);


    vec2 mouthOffset = vec2(0, 0.05);
    float mouth = c(mouthUv + mouthOffset, .5);
    float mouthMask = c(mouthUv, .5);
    mouth = mouth - mouthMask;

    float f = max(eyes, mouth);
    vec3 col = 0.5 + 0.5*cos(iTime+s*2.+uv.xyx*cos(uv.xxy*2.)+vec3(0,2,4));
    fragColor = vec4(col * f,1.0);
}
