// Multiply complex numbers a and b
vec2 complexMultiply(vec2 a, vec2 b) {
    return vec2(a.x * b.x - a.y * b.y, a.x * b.y + a.y * b.x);
}

// Divide complex number a by b
vec2 complexDivide(vec2 a, vec2 b) {
    float denom = b.x * b.x + b.y * b.y;
    return vec2((a.x * b.x + a.y * b.y) / denom, (a.y * b.x - a.x * b.y) / denom);
}

// Calculate the complex conjugate of a
vec2 complexConjugate(vec2 a) {
    return vec2(a.x, -a.y);
}

// Raise complex number a to the power of n
vec2 complexPower(vec2 a, float n) {
    float r = length(a);
    float theta = atan(a.y, a.x);
    float newR = pow(r, n);
    float newTheta = theta * n;
    return newR * vec2(cos(newTheta), sin(newTheta));
}

// Calculate cos(z) for complex number z
vec2 complexCos(vec2 z) {
    return vec2(cos(z.x) * cosh(z.y), -sin(z.x) * sinh(z.y));
}

// Calculate sin(z) for complex number z
vec2 complexSin(vec2 z) {
    return vec2(sin(z.x) * cosh(z.y), cos(z.x) * sinh(z.y));
}

// Calculate exp(z) for complex number z
vec2 complexExp(vec2 z) {
    float r = exp(z.x);
    return float(r) * vec2(cos(z.y), sin(z.y));
}

// Calculate log(z) for complex number z
// This returns the principal value
vec2 complexLog(vec2 z) {
    float r = float(length(z));
    float theta = atan(z.y, z.x);  // in range [-pi, pi]
    return vec2(log(r), theta);
}

// Convert a vec3 from hsv to rgb colour space
vec3 hsv2rgb(vec3 c) {
    vec4 K = vec4(1.0, 2.0 / 3.0, 1.0 / 3.0, 3.0);
    vec3 p = abs(fract(c.xxx + K.xyz) * 6.0 - K.www);
    return c.z * mix(K.xxx, clamp(p - K.xxx, 0.0, 1.0), c.y);
}

// Convert 2D vector from cartesian (x,y) to polar (r, theta) coordinates
vec2 toPolar(vec2 cartesian)
{
    float radius = length(cartesian);
    float theta = atan(cartesian.y, cartesian.x) / 3.1415926535 / 2.0; // normalize to [0,1]

    // wrap theta
    theta = theta - (1.0 * floor(theta));
    theta += 1.0;
    theta = theta - (1.0 * floor(theta));

    return vec2(radius, theta);
}

