vec2 GetDist(vec3 p  ) 
{	vec2 res= vec2(9999.0, -1.0);  
    p.y=p.y+10.0;
    vec3 p0=p;
	float planeDist1 = p.y+10.0;  //piso inf
    res =opU2(res, vec2(planeDist1,67.0));
   float d2= sdBox(p-vec3(0.0,10.0,0.0), vec3(6.0,20.0,2.0) );
   float d3= sdBox(p-vec3(28.0,10.0,15.0), vec3(8.0,20.0,2.0) );
    res =opU2(res, vec2(d2,68.0));
    res =opU2(res, vec2(d3,69.0));
    return res;
}

vec3 GetNormal(vec3 p)
{   float d = GetDist(p).x;
    vec2 e = vec2(.001, 0);
    vec3 n = d - vec3(GetDist(p-e.xyy).x,GetDist(p-e.yxy).x,GetDist(p-e.yyx).x);
    return normalize(n);
}

vec2 RayMarch(vec3 ro, vec3 rd, int PMaxSteps)
{   vec3 p;
    vec2 hit, object=vec2(0.1,0.0);
    for(int i=0; i <= PMaxSteps; i++) 
    { p = ro + rd*object.x;
      hit = GetDist(p);
      object.x += hit.x;
      object.y = hit.y;
      if (abs(hit.x) < EPSILON || object.x > MAX_DIST) break;
    }    
    return object;
}

