vec3 truc1(vec2 uv)
{vec3 col = vec3(0);
    vec2 gv = fract(uv) - 0.5;
    vec2 idBox = floor(uv);
    float n = Noise21(idBox); 
    float width = .05;
    float lw=0.15;
    if (n<.5){gv.x *=  -1.; }
    float d;
    float mask =0.0;
    float d1,d2;
    float lW=0.15;

    if(n>.35){
        // Cross tile.
        d1 = abs(gv.y) - lW;
        d2 = abs(gv.x) - lW;    
    }
    else { 
        // Dual arc tile.
        d1 = length(gv - .5) - .5;
        d2 = length(gv + .5) - .5;
        d1 = abs(d1) - lW;
        d2 = abs(d2) - lW;
    }    
    d=min(d1,d2);
    mask+= smoothstep(.01,-.01,abs(d)- width);
    col += mask*vec3(1.0,3.0,4.0)/4.0;
    return col;
}

