// golfing 1171 chars "pseudo-recursive tiles" by ianertson. https://shadertoy.com/view/cdGGDK

/**/ // -5 chars by Xor

void mainImage( out vec4 O, vec2 U )
{
    vec4  R = iResolution.xyzz, H;
    for (U = ( U+U - R.xy ) / R.y * ( 1.1 + sin(iTime) ) + sin(iTime/2. + vec2(11, O *= 0.) );
         R.w < 4.; U *= ceil( H.w *5.5 ) ) 
        H = fract( 1e4* sin( ceil(U+U) * mat4x2(R,R.yzxw)-.1)),
        O += H/3. + dot( smoothstep(2.8-R.w++, R.w, abs( mod(U,.5)*24. - 6. ) -4. ), U/U );
}






