Main {

    Q = A(U);
    vec4 dQ = vec4(0);
    // PV/V = nKT/V -> P = d T
    /*
        xy : velocity
        z  : temperature
        w  : density
    */
    for (float x = -1.; x <= 1.;x++)
    for (float y = -1.; y <= 1.;y++)
    if (abs(x)!=abs(y))
    {
        vec2 u = vec2(x,y);
        vec4 a = A(U+u);
        float d = dot(u,a.xy-Q.xy);
        if (d > 0.) Q.w -= .25*d*Q.w;
        if (d < 0.) Q.w -= .25*d*a.w;
        Q.z -= .5*d;
    }
    Q += dQ;

    if (map(U)<0.) Q.xy *= 0.;
}

