vec4 T (vec2 U) {
    U -= .5*A(U).xy;
    U -= .5*A(U).xy;
    return A(U);
}
Main {

    Q = T(U);
    float bu = 0.;
    for (float i = 0.; i < N; i++) {
        vec4 q = D(.5+vec2(i,0));
        float s = smoothstep(3.,1.,length(U-q.xy));
        Q.xy = mix(Q.xy,q.zw,s);
        bu = min(bu,s);
    }
    float m = 1.;
    if (bu>.5) m = 10.;
    for (float x = -1.; x <= 1.;x++)
    for (float y = -1.; y <= 1.;y++)
    if (abs(x)!=abs(y))
    {
        vec2 u = vec2(x,y);
        vec4 a = T(U+u);
        Q.xy -= .5*u/m*(a.z*a.w);
    }
    if (map(U)<0.)
        Q.xy *= 0.;
    if (iFrame < 1) {
        Q = vec4(0,0,0,.1);

    }
    if (iFrame < 3*int(U.x)&&length(U-vec2(.95,.5)*R)<.2*H)
