vec4 bullet (float i) {
    vec4 Q = vec4(0);
    if (i<.25*N) Q = vec4(((4.*i/N)*2.-1.)*.5*W,-.5*H+3.,0,0);
    else if (i<.5*N) Q = vec4(.5*W,(.5*H-3.)*((4.*(i-.25*N)/N)*2.-1.),0,0);
    else if (i<.75*N) Q = vec4(.5*W*((4.*(i-.5*N)/N)*2.-1.),.5*H-3.,0,0);
    else Q = vec4(-.5*W+.5*H*cos((.5*(4.*(i-.75*N)/N)+.25)*2.*pi),(.5*H-3.)*sin((.5*(4.*(i-.75*N)/N)+.25)*2.*pi),0,0);

    Q.xy += vec2(.9,.5)*R-vec2(.5*W,0);
    return Q;
}
Main {
    if (U.y>1. || U.x > N) discard;
    Q = D(U);
    vec4 n = A(Q.xy+vec2(0,1));
    vec4 e = A(Q.xy+vec2(1,0));
    vec4 s = A(Q.xy-vec2(0,1));
    vec4 w = A(Q.xy-vec2(1,0));
    vec2 f = -.0*vec2(e.z*e.w-w.z*w.w,n.z*n.w-s.z*s.w);
    n.w = map(Q.xy+vec2(0,1));
    e.w = map(Q.xy+vec2(1,0));
    s.w = map(Q.xy-vec2(0,1));
    w.w = map(Q.xy-vec2(1,0));
    Q.zw = mix(Q.zw,A(Q.xy).xy,.01);
    if (map(Q.xy)<0.)Q.zw = vec2(0),f += .1*vec2(e.w-w.w,n.w-s.w);
    for (float i = 0.; i < N; i++) {
        vec4 q = D(.5+vec2(i,0));
        vec2 v = q.xy-Q.xy;
        float l = length(v);
        float L = length(bullet(i).xy-bullet(U.x-.5).xy);
        if (l>.5)
        f += .01*v*(l-L)*exp(-.01*L)/N;
    }

    if (length(f)>.5)f = .5*normalize(f);
    Q.zw += f-vec2(0,.0/R.y);
    Q.xy += Q.zw+f;
    if (length(Q.zw)>.5) Q.zw = .5*normalize(Q.zw);
    if (iFrame < 1000) 
    Q = bullet(U.x-.5);
}

