// cleanup by https://www.shadertoy.com/user/FabriceNeyret2
float rand(vec2  n){ return fract(sin(n.x*11.+n.y*17.) * 43758.5453123);}
float rand(float n){ return fract(sin(n)               * 43758.5453123);}

float sdRoundBox( vec2 p, vec2 b, float r )
{
  vec2 q = abs(p) - b;
  return length(max(q,0.)) + min(max(q.x,q.y),0.) - r;
}

vec4 hsb2rgb( in vec4 c )
{
    vec3 rgb = clamp(abs(mod(c.x*6.0+vec3(0.0,4.0,2.0),
                             6.0)-3.0)-1.0,
                     0.0,
                     1.0 );
    rgb = rgb*rgb*(3.0-2.0*rgb);
    return vec4(c.z * mix( vec3(1.0), rgb, c.y), 1.);
}

void mainImage( out vec4 O, vec2 u ) {

float myTime = iTime+31.*71.;

    O = vec4(0);
    vec2  R = iResolution.xy,
          U = ( u - R*.5 ) / R.y;
    float t = -myTime;
    U.x += sin(t*.05)*.25;
    U.y += sin(t*.10)*.25;
    for(float i = 0. ; i < 1. ; i += .2) {
        float u = fract(t*.125+i),
              q = rand(i+10.)*9. + u*2. + sin(t*.3)*2.;
        vec2  P = U *u*90. * mat2(cos(q), -sin(q), sin(q), cos(q)),
              f = 2.*fract(P)-1.;
        float rn = rand(floor(P)),
              c = pow( abs(sin( (iTime*.04+rn)*3.14159 )) ,16.),
              s = smoothstep(.02, .01, length(f) - c);
        vec4 gumball = hsb2rgb(vec4(  1.7*u* c*U.x*q,1.,1.,1.)*s*(1.-u));
        O = max(O, gumball);//(vec4(1,1, .2*c,1)*s*(1.-u)));
    }

    O *= min(1., iTime*.5);
}
