#define matrot(x) mat2(cos(x),-sin(x),sin(x),cos(x))
#define amount 4.0
#define saturate(x) clamp(x,0.0,1.0)
#define downcol vec3(0,1,0.25)
#define upcol vec3(0,0.25,1)
#define steps 32.0

highp float voronoi(
	highp vec2 x){
highp float n = 0.0;
for(int i = 0; i<int(amount); i++){
n = max(n, abs(
		sin(x.x)*
        cos(x.y))),

x *= matrot(0.25),

x += vec2(sin(iTime),
	cos(iTime))/(
	amount/2.0)*0.0,

x += float(i)/amount;
	}
return 1.0-(n);
	}/*voronoi from https://www.shadertoy.com/view/7d2cWR*/

highp float fbm(
	highp vec2 x,
	highp float s){
x -= iTime;
highp float f = voronoi(x);
f *= (voronoi(x*6.0+4.0+iTime*6.0)+s);

return saturate(pow(f,3.0));
	}

highp vec3 aurora(
	highp vec2 x,
	highp vec3 sky){
for(float i = 0.0; i<steps; i++){
x *= 0.97;
sky = mix(sky, sqrt(mix(upcol, downcol, i/(steps))),
	fbm(x,(i/(steps*2.0))));
	}
return sky;
	}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-iResolution.xy*vec2(0.5,0))/
    (iResolution.x,iResolution.y);

    if(uv.y>0.5){
    uv /= uv.y-0.5;
    fragColor = vec4(aurora(uv,vec3(0)),1.0);
}else{ fragColor = vec4(0);}}
