void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    fragColor = vec4(0.0);
    vec2 uv = (fragCoord)/iResolution.y/16.0
    + vec2(iTime/2.0,iTime/3.0)/64.;
    if(iMouse.z>.5)
    uv = (uv.xy + iMouse.xy / iResolution.xy/4.);
    float col = 0.;
    for(int k = 0; k < 9; k++){
        uv =
            abs(fract(uv.yx-vec2(uv.x,-uv.y)*2.)-.5)
        ;
        col =
            max(length(uv/2.),col)
        ;
        col =
            max(abs(col*2.-1.),col/4.);
    }
    fragColor = vec4(min(vec3(col*2.),vec3(1.)),1.0);
}

