#define fmod(x,y) mod(floor(x),y)
vec2 triangle_wave(vec2 a){
    vec2 a2 =
        vec2(1.,.5)
    ,
    a1 = a+a2;
    return abs(fract((a1)*(a2.x+a2.y))-.5);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    fragColor = vec4(0.0);
    vec3 col = vec3(0.);
    float t1 = 8.;
    vec2 uv = (fragCoord)/iResolution.y/t1/2.0;
    uv.x += iTime/t1/12.0;
    if(iMouse.z>.5)
    uv = uv.xy + iMouse.xy / iResolution.xy/t1;
    float scale = 1.5;
    vec2 t2 = vec2(0.);
    for(int k = 0; k < 12; k++){
        uv = abs(uv+t2+.5);
        t2 =
            triangle_wave(uv+.5)/scale
            //triangle_wave(uv-.5*sign(t2.y-uv.x))/scale
        ;
        uv = (t2-triangle_wave(uv.yx))/1.5;
        col.x =
            max(length(uv+t2)/2.,col.x)
        ;
        col.x =
            max(abs(col.x-(1.-col.x)),col.x/4.);
    }
    fragColor = vec4(vec3(col.x),1.0);
}

float fmod(float a, float b){
    //float a1 = a;
    //a += floor((a1)/2.);
    //a += floor(floor(a1/2.)/3.);
    //a += floor(floor(a1/4.)/5.);

    return
        mod(floor(a),b)
    ;
}

vec2 mainSound(int samp, float time){
  float tempo = 1.,
  t2=(1.+fmod(time*2.*pow(2.,fmod(time/8.,3.)),2.));
  time /= t2*2.;
  tempo /= (1.+fmod(time/2.,2.));
  time /= tempo;
  float t1 = (1.+fmod(time/2.,2.));
  float s1 =
      8./t1;

  float s2 = 4.*t1,
  t=
      time*t1
  ,
  m1 =
      fmod(t*4.,s2)
  ,
  m3 =
      (fmod(t,abs(s2*2.-m1)+1.)-m1);
  t /=
      (1.+fmod(t/s1,2.))*4./s1/s1
  ;
  float m2 =
      1. + fmod(t/s1,s1)
  ;
  float a=
      64.*(1.-sqrt(fract(t/s1)/1.25))*.2
  ,
  nb = 4.*time*tempo*pow(2.,floor((m3+m2)/2.)/5.+6.5);
  return
      abs(.5-vec2(fract(nb*.998*2.),fract(nb)))*a
  ;
}
