vec2 triangle_wave(vec2 a){
    vec2 a2 =
        vec2(1.,0.5)
    ,
    a1 = a+a2;
    return
        abs(fract((a1)*(a2.x+a2.y))-.5)
    ;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    fragColor = vec4(0.0);
    vec3 col = vec3(0.);
    float t1 = 1.;
    vec2 uv = (fragCoord)/iResolution.y/t1/2.0;
    uv.y += iTime/t1/16.0;
    if(iMouse.z>.5)
    uv = uv.xy + iMouse.xy / iResolution.xy/t1;
    vec2 t2 = vec2(0.);
    for(int k = 0; k < 5; k++){
        uv =
            2.*abs(.5+uv+t2)/1.5
        ; 
        //uv += floor(uv*2.); //another interesting pattern
        //uv += floor(uv*2.)+floor(uv*1.5); //another interesting pattern
        uv += sign(uv-uv.yx)/2.+.5+floor(uv*2.)*floor(uv*1.5); //another interesting pattern

        bool b = uv.y>uv.x;
        if(b) uv = uv.yx;
        //else k += 1;
        t2 =
            -triangle_wave(uv-.5)
        ;
        //if(uv.y>uv.x) uv = uv.yx-vec2(1.5,.5);
        uv =
            t2-triangle_wave(uv.yx)
        ;
        //if(b||t2.y<t2.x)
        col =
            vec3(col.zx,max(abs(uv.x-uv.y)*sign(t2.x-t2.y),0.))
        ;
    }
    fragColor = vec4(col*2.,1.0);
}

float fmod(float a, float b){
    a /= (1. + floor(mod(a*4.,2.)))*4.;
    return mod(floor(a),b);
}

vec2 mainSound(int samp, float t){
  //float p1 = 1. + floor(mod(mod(t,1.+t/4.)/2.,2.));
  t *= 1./2.;
  t += fract(t);
  float s1 = 8.;
  float a=
      pow((1.-sqrt(fract(t/2.))),2.)
  ,
  nb = pow(2.,(2.+fmod(t*2.,s1)+1. + fmod(floor(t/s1),2.))/5.+7.);
  return
      abs(.5-vec2(fract(t*nb*.998*2.),fract(t*nb)))*a
  ;
}
