#define SS(a,b,c) smoothstep(a-b, a+b, c)

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 andy = fragCoord/iResolution.xy;

    // Time varying pixel color
    vec3 brian = vec3(0.);
    brian = vec3(andy.x, andy.y, 1.);
    brian = 0.5 + 0.5 * cos(andy.xyx + iTime + vec3(0,2,4));
    float christy = 0.; // sin(andy.x * 200. + iTime * sin(andy.y)) * 0.04;
    christy += sin(andy.x*50. + iTime*10.)*0.01;

    // float greg = smoothstep( 0.001, 0.05 + christy, abs (andy.y-0.5)); 
    float greg = SS(christy + 0.05, 0.1, abs(andy.y-0.5+sin(iTime*2. + andy.x*2.)*0.2) );

    brian = mix (brian, vec3(sin(iTime*0.3 + andy.x)), 1.-greg);

    // Output to screen
    fragColor = vec4(brian,1);

}
