vec2 times(vec2 a, vec2 b)
{
    float re = a.x*b.x-a.y*b.y;
    float im = a.x*b.y+a.y*b.x;
    return vec2(re, im);
}

vec2 power(vec2 a, int n)
{
    vec2 res = a;
    for(int i=0; i<n-1; i++) res=times(res,a);
    return res;    
}

mat2 rm(float t)
{
    return mat2(cos(t),-sin(t),sin(t),cos(t));
}

vec3 color(vec2 z)
{
    vec2 x1 = vec2(0);
    vec2 x2 = vec2(-1.0/2.0, sqrt(3.0)/2.0);
    vec2 x3 = vec2(-1.0/2.0, -sqrt(3.0)/2.0);
    float d1 = distance(z,x1);
    float d2 = distance(z,x2);
    float d3 = distance(z,x3);
    float m = min(min(d1,d2),d3);
    if (d1==m) return vec3(1,0,0);
    if (d2==m) return vec3(0,1,0);
    if (d3==m) return vec3(0,0,1);
    return vec3(1,0.8,1);

}

float sdCircle(vec2 p)
{
    return length(p)-1.0;
}

