    vec2 C = length(pos)<2.0 ? pos:vec2(0.6,0.5);
    for(int i=0; i<int(100); i++) z = C-power(z,5);

    vec3 col = color(z);

    if (abs(sdCircle(uv))<0.0015) col =vec3(0.5);
