#define USE_LIGHT_PROXIMITY 1
#define USE_FAKE_NORMALS 1

float getLightIntensity(vec2 lightPosNorm, vec2 viewerPos) {
    return pow(1.0-max(0.0,min(1.0,distance(lightPosNorm, viewerPos))),2.0);
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    vec2 lightPosition = vec2(cos(iTime)/2.0+0.5,sin(iTime)/2.0+0.5);

    float intensity = getLightIntensity(lightPosition, uv);
    vec3 bg = vec3(intensity*1.0,intensity*0.8,intensity*0.4);

    vec2 start = vec2(0.4,0.3);
    vec2 end = vec2(0.7,0.7);

    vec2 boxCoords = uv / (end-start) - start*2.5;
    if(boxCoords.x < 0.0 || boxCoords.x > 1.0 || boxCoords.y < 0.0 || boxCoords.y > 1.0) {
        fragColor = vec4(bg,1.0);
    } else {
        vec4 spriteUV = texture(iChannel1,boxCoords);
        vec4 fakeNormalUV = texture(iChannel0,boxCoords);
        if(USE_LIGHT_PROXIMITY == 1) {
            spriteUV *= intensity*10.0;
        }
        vec2 directionMagnitudeNormal;
        directionMagnitudeNormal = normalize(vec2((1.0-fakeNormalUV.x)-0.5,(1.0-fakeNormalUV.y)-0.5));
        float amt = dot(normalize(lightPosition-uv),directionMagnitudeNormal)/2.0+1.0;
        if(USE_FAKE_NORMALS == 1) {
            spriteUV *= amt;
        }
        fragColor = vec4(spriteUV.xyz,1.0);
    }
}
