void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.y;

    // Random points
    vec2 p0 = vec2(sin(iTime*.72)*.5+.5, sin(iTime*1.21-.34)*.5+.5);
    vec2 p1 = vec2(sin(iTime*.31+2.53)*.5+.5, sin(iTime*.13+.92)*.5+.5);
    vec2 p2 = vec2(sin(iTime*.96-5.35)*.5+.5, sin(iTime*.35-1.41)*.5+.5);
    vec2 p3 = vec2(sin(iTime*.15+.56)*.5+.5, sin(iTime*.26+.51)*.5+.5);

    // Length of each point to current sample (uv)
    float p0Len = length(p0 - uv);
    float p1Len = length(p1 - uv);
    float p2Len = length(p2 - uv);
    float p3Len = length(p3 - uv);

    vec3 col = vec3(0);

    // Minimum Length
    float mLen = min(p0Len, min(p1Len, min(p2Len, p3Len)));

    // Each point gets a unique color
    if(mLen == p0Len)
        col = vec3(1,0,0);
    else if(mLen == p1Len)
        col = vec3(1,1,0);
    else if(mLen == p2Len)
        col = vec3(0,1,0);
    else if(mLen == p3Len)
        col = vec3(0,1,1);

    // Show the point
    col = col * step(.05, mLen);    

    fragColor = vec4(col, 1.);
}
