void RecalibTris(inout Triangle tso[TriCount], vec3 ro) {
    for(int i = 0; i < TriCount; i++) {
        vec3 a = vec3(tso[i].a.xy-.5,tso[i].a.z);
        vec3 b = vec3(tso[i].b.xy-.5,tso[i].b.z);
        vec3 c = vec3(tso[i].c.xy-.5,tso[i].c.z);
        a -= ro;
        b -= ro;
        c -= ro;
        tso[i] = Triangle(a,b,c);
    }
}

vec3 CalcNorm(Triangle t) {
    vec3 U = t.b-t.a;
    vec3 V = t.c-t.a;

    vec3 n;

    n.x = U.y*V.z - U.z*V.y;
    n.y = U.z*V.x - U.x*V.z;
    n.z = U.x*V.y - U.y*V.x;

    return normalize(n);
}

vec2 CalcUv(vec2 p, Triangle tp, Triangle tuv, Triangle top, out vec3 pos) {
    float w1p1 = (tp.b.y-tp.c.y)*(p.x-tp.c.x)+(tp.c.x-tp.b.x)*(p.y-tp.c.y);
    float w1p2 = (tp.b.y-tp.c.y)*(tp.a.x-tp.c.x)+(tp.c.x-tp.b.x)*(tp.a.y-tp.c.y);
    float w1 = w1p1/w1p2;

    float w2p1 = (tp.c.y-tp.a.y)*(p.x-tp.c.x)+(tp.a.x-tp.c.x)*(p.y-tp.c.y);
    float w2p2 = (tp.b.y-tp.c.y)*(tp.a.x-tp.c.x)+(tp.c.x-tp.b.x)*(tp.a.y-tp.c.y);
    float w2 = w2p1/w2p2;

    float w3 = 1. - w1 - w2;

    //vec3 auv = w1*tuv.a + w2*tuv.b + w3*tuv.c;
    vec3 puv = w1*tuv.a/tp.a.z + w2*tuv.b/tp.b.z + w3*tuv.c/tp.c.z;
    vec4 p0a = vec4(top.a,1.);
    vec4 p0b = vec4(top.b,1.);
    vec4 p0c = vec4(top.c,1.);
    vec4 p1 = w1*p0a/p0a.z + w2*p0b/p0b.z + w3*p0c/p0c.z;

    pos = p1.xyz / p1.w;
    return puv.xy / puv.z;
}

bool isInside(vec2 p, Triangle tri) {
    float s = (tri.a.x-tri.c.x)*(p.y-tri.c.y)-(tri.a.y-tri.c.y)*(p.x-tri.c.x);
    float t = (tri.b.x-tri.a.x)*(p.y-tri.a.y)-(tri.b.y-tri.a.y)*(p.x-tri.a.x);

    if((s<0.) != (t<0.) && s != 0. && t  != 0.) return false;

    float d = (tri.c.x-tri.b.x) * (p.y - tri.b.y) - (tri.c.y - tri.b.y) * (p.x - tri.b.x);
    return d == 0. || (d<0.) == (s+t <= 0.);
}

Triangle Project(Triangle to,vec2 Mouse) {
    vec2 ra = PI*(vec2(1.,0.)-3.*Mouse);

    vec3 a = to.a;
    rotY(a,vec2(cos(-ra.x),sin(-ra.x)));
    rotX(a,vec2(cos(-ra.y),sin(-ra.y)));

    vec3 b = to.b;
    rotY(b,vec2(cos(-ra.x),sin(-ra.x)));
    rotX(b,vec2(cos(-ra.y),sin(-ra.y)));

    vec3 c = to.c;
    rotY(c,vec2(cos(-ra.x),sin(-ra.x)));
    rotX(c,vec2(cos(-ra.y),sin(-ra.y)));

    a.xy /= a.z;
    b.xy /= b.z;
    c.xy /= c.z;
    return Triangle(a,b,c);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord / iResolution.xy;
    uv -= .5;
    vec2 MOUSE = (iMouse.xy/iResolution.xy)-.5;
    if(iMouse.xy == vec2(0.)) MOUSE = vec2(0.);

    TriDat tris[TriCount];
    SetTriData(tris,iTime);


    Triangle tos[TriCount];
    Triangle uvs[TriCount];

    SplitTriDat(tris,tos,uvs);

    vec3 rd = crd((iMouse.xy/iResolution.xy)-.5,uv);
    vec3 centrRd = crd(MOUSE,vec2(0.));
    vec3 ro = vec3(.5,.5,0.)-centrRd*8.;
    //ro = vec3(0.,0.,-2.5);
    RecalibTris(tos,ro);
    vec3 ld = vec3(.5,.5,5.*sin(iTime)+1.);

    fragColor = vec4(0.,0.,.3,100.);
    for(int i = 0; i < TriCount; i++) {
        Triangle ts = Project(tos[i],MOUSE);
        if(isInside(uv,ts)&& !(ts.a.z<=0.||ts.b.z<=0.||ts.c.z<=0.)) {
            vec3 p;
            vec2 textureUv = CalcUv(uv,ts,uvs[i],tos[i],p);
            //p = vec3(0.);

            vec3 col = texture(iChannel0,textureUv).xyz;

            vec3 n = CalcNorm(tos[i]);
            if(tris[i].flipNorm) n *= -1.;
            float dif = clamp(dot(n,normalize(ld-p))*.5+.5,0.,1.);
            col *= dif;
            vec3 h = normalize(rd+normalize(ld-p));
            col += dif * vec3(pow(clamp(dot(h,n),0.,1.),30.));

            if(p.z<=fragColor.w) fragColor = vec4(col,p.z);
            if(textureUv.x>1.||textureUv.x<0.||textureUv.y>1.||textureUv.y<0.) fragColor = vec4(.15,.15,3.,1.);
        }
    }
    //fragColor.xyz = rd;
    fragColor.w = 1.;
}

#define TriCount 2
#define PI 3.14159265

struct Triangle {
    vec3 a;
    vec3 b;
    vec3 c;
};

struct TriDat {
    Triangle Vp;
    Triangle Vuv;
    bool flipNorm;
    int colMode;
};

void rotX(inout vec3 p, vec2 at) {
    p.z = at.y*p.y-at.x*p.z;
    p.y = at.x*p.y+at.y*p.z;
}

void rotY(inout vec3 p, vec2 at) {
    p.x = at.y*p.z-at.x*p.x;
    p.z = at.x*p.z-at.y*p.x;
}

void rotZ(inout vec3 p, vec2 at) {
    p.y = at.y*p.x-at.x*p.y;
    p.x = at.x*p.x-at.y*p.y;
}

vec3 crd(vec2 iMouse,vec2 uv) {
    vec3 rd = normalize(vec3(uv,1.));
    vec2 ra = PI*(vec2(1.,0.)-3.*iMouse);
    rotY(rd,vec2(cos(ra.x),sin(ra.x)));
    rotX(rd,vec2(cos(ra.y),sin(ra.y)));
    //rd.y *= -1.;
    return rd;
}

void SetTri(inout TriDat t[TriCount], int i,
  vec3 v1,vec3 v2,vec3 v3,
  vec2 uv1,vec2 uv2, vec2 uv3,
  bool fn, int txtr) {
    t[i] = TriDat(Triangle(v1,v2,v3),Triangle(vec3(uv1,1.),vec3(uv2,1.),vec3(uv3,1.)),fn,txtr);
}

void SetTriData(out TriDat t[TriCount],float iTime) {
    SetTri(t,0,vec3(-1.,-1.,1.),vec3(-1.,2.,1.),vec3(2.,-1.,1.),vec2(0.,0.),vec2(0.,1.),vec2(1.,0.),false,0);
    SetTri(t,1,vec3(2.,-1.,1.),vec3(2.,2.,1.),vec3(-1.,2.,1.),vec2(1.,0.),vec2(1.,1.),vec2(0.,1.),true,0);
}

void SplitTriDat(TriDat t[TriCount],out Triangle tp[TriCount],out Triangle tuv[TriCount]) {
    for(int i = 0; i < TriCount; i++) {
        tp[i] = t[i].Vp;
        tuv[i] = t[i].Vuv;
    }
}
