// -9 thanks to Fabrice üê≠

void mainImage( out vec4 O, vec2 U )
{
    float i,
          j,
          z,
          t = .1*iTime, 
          d = 9.; 

    for(vec3 p, 
             R = iResolution,
             n = normalize(2.+cos(t*vec3(7,8,9))); 
        i++<1e2 && d>.1; 
        O.rgb = -log(p*p)/i // log2 for more brightness
        ) {    
        // Set p = (uv, z) then rotate p around n by t radians
        p = mix(dot(n, p = vec3(33.*(U+U-R.xy)/R.y, z - 2e2)) * n, 
                p, 
                cos(t))
          + cross(n,p)*sin(t);

        // Do abs fractal magic then rotate around n by pi
        for(j = 40.; j > .2; j *= .5)
             p = reflect(abs(abs(p)-j), n);

        z += d = length(p)-.4;                     
    }
}

