void mainImage(out vec4 c,in vec2 i){
    c = texelFetch(iChannel0,ivec2(i),0);
    c /= c.w;
}

#define R iResolution.xy
#define F float(iFrame)+1.
struct Material {
    float roughness;
    float shininess;
    float glossiness;
    vec3 color;
};

struct PLight {
    float intensity;
    vec3 color;
    vec3 pos;
};

#define A vec3(29.,11.,17.)
float seed;
float rand(void){
  seed = mod(seed*A.x+A.y,A.z)/A.z;
  return seed;
}
vec2 rand2(void){return
vec2(rand(),rand());}
float srand(float s) {
  return mod(s*A.x+A.y,A.z)/A.z;
}
void irand(float t, vec2 p) {
  seed = abs(-t*p.y*sin(p.y)+t*p.x*cos(p.x));
}

#define Inf 9999.
#define IG 2.2
#define G 1./IG

vec3 cosDir(in vec3 nor){
    vec3 tc = vec3( 1.0+nor.z-nor.xy*nor.xy, -nor.x*nor.y)/(1.0+nor.z);
    vec3 uu = vec3( tc.x, tc.z, -nor.x );
    vec3 vv = vec3( tc.z, tc.y, -nor.y );

    float u = rand();
    float a = 6.283185 * rand();

    return sqrt(u)*(cos(a)*uu + sin(a)*vv) + sqrt(1.0-u)*nor;
}
//Above and below are from Ingo Quilezles
vec3 uniformVector()
{
    float a = 3.141593*rand();
    float b = 6.283185*rand();
    return vec3( sin(b)*sin(a), cos(b)*sin(a), cos(a) );
}

vec3 getSky(vec3 d,float t) {
    return vec3(.5,.65,.95);//.5,.65,.95
}

float planeInt( in vec3 ro, in vec3 rd, in vec4 p )
{
    return -(dot(ro,p.xyz)+p.w)/dot(rd,p.xyz);
}
vec2 sphInt( in vec3 ro, in vec3 rd, in vec3 ce, float ra )
{
    vec3 oc = ro - ce;
    float b = dot( oc, rd );
    float c = dot( oc, oc ) - ra*ra;
    float h = b*b - c;
    if( h<0.0 ) return vec2(-1.0); // no intersection
    h = sqrt( h );
    return vec2( -b-h, -b+h );
}
vec2 boxInt( in vec3 ro, in vec3 rd, vec3 boxSize, out vec3 outNormal ) 
{
    vec3 m = 1.0/rd; // can precompute if traversing a set of aligned boxes
    vec3 n = m*ro;   // can precompute if traversing a set of aligned boxes
    vec3 k = abs(m)*boxSize;
    vec3 t1 = -n - k;
    vec3 t2 = -n + k;
    float tN = max( max( t1.x, t1.y ), t1.z );
    float tF = min( min( t2.x, t2.y ), t2.z );
    if( tN>tF || tF<0.0) return vec2(-1.0); // no intersection
    outNormal = (tN>0.0) ? step(vec3(tN),t1) : // ro ouside the box
                           step(t2,vec3(tF));  // ro inside the box
    outNormal *= -sign(rd);
    return vec2( tN, tF );
}

vec2 worldInt(in vec3 ro, in vec3 rd,int lId,float t) {
    float d0 = sphInt(ro,rd,vec3(-1.,-.2,3.),.8).x;
    if(d0 < 0.||lId==0) d0 = Inf;
    float d1 = planeInt(ro,rd,vec4(0.,1.,0.,1.));
    if(d1 < 0.) d1 = Inf;
    float d2 = sphInt(ro,rd,vec3(-2.5,1.,5.),2.).x;
    if(d2 < 0.) d2 = Inf;
    float d3 = sphInt(ro,rd,vec3(2.,.5,4.5),1.5).x;
    if(d3 < 0.) d3 = Inf;
    float d4 = sphInt(ro,rd,vec3(1.75,0.,2.),.3).x;
    if(d4 < 0.||lId==2) d4 = Inf;

    float d = min(min(d0,min(d2,min(d3,d4))),d1);
    //d = min(d0,d1);
    if(d==Inf) return vec2(-1.);

    float m = -1.;
    if(d==d0) m = 1.;
    if(d==d1) m = 2.;
    if(d==d2) m = 3.;
    if(d==d3) m = 4.;
    if(d==d4) m = 5.;

    return vec2(d,m);
}

PLight genLight(float i,vec3 c,vec3 p,float r){
    vec3 o = r*normalize(-1.+2.*vec3(rand2(),rand()));
    return PLight(i,c,p+o);
}

vec3 getCam(float t,out vec3 uu,out vec3 vv,out vec3 ww) {
    uu = vec3(1.,0.,0.);
    vv = vec3(0.,1.,0.);
    ww = vec3(0.,0.,1.);
    //Keep above

    //Write below



    //Keep below
    uu = normalize(uu);
    vv = normalize(vv);
    ww = normalize(ww);
    return vec3(0.,1.,0.);
}

vec3 getObjectNorm(vec3 p, float id,float t) {
    if(id==1.) return normalize(p-vec3(-1.,-.2,3.));
    if(id==2.) return normalize(vec3(0.,1.,0.));
    if(id==3.) return normalize(p-vec3(-2.5,1.,5.));
    if(id==4.) return normalize(p-vec3(2.,.5,4.));
    if(id==5.) return normalize(p-vec3(1.75,0.,2.));
    //return vec3(0.,1.,0.);
}

Material getObjectData(vec3 pos,float id,float t) {
    if(id==1.) return Material(.85,35.,.05,vec3(.2,.2,.2));
    if(id==2.) return Material(.95,15.,.5,vec3(.2,.2,.2));
    if(id==3.) return Material(.01,15.,.05,vec3(.2,.2,.2));
    if(id==4.) return Material(.9,15.,.5,vec3(.05,.2,.125));
    if(id==5.) return Material(.85,35.,.05,vec3(.2,.2,.2));
}
//Glossiness (3rd comp) and Roughness (1st comp) should NEVER be 0.
void getRay(out vec3 ro,inout vec3 rd,float r,vec3 p,vec3 n){
    ro = p;
    rd = normalize(reflect(rd,n)*(1.-r)+uniformVector()*r);
}

bool inLight(vec3 ro,vec4 ld,int lID,float t){
    vec2 tres = worldInt(ro,normalize(ld.xyz),lID,t);
    return tres.x>=ld.w||tres.y<0.||tres.x==-1.;
    return true;
}

//Lower this if rt is false?
#define  spp    1.
#define numLvls 5
#define   rt    false
//Set to true if the camera or 
//objects can move

vec3 getPixCol(vec2 I) {
    float frame = F*spp;
    float ftime = rt?iTime:0.;
    vec3 ttcol = vec3(0.);
    for(float i=0.;i<spp;i++) {
    irand(frame,I);
    vec2 p = (-R + 2.*(I+rand2()))/R.y;

    //.6 is shutter aperture
    float ctime = ftime + (.6/24.)*rand();

    vec3 uu;vec3 vv;vec3 ww;
    vec3 ro = getCam(ctime,uu,vv,ww);

    vec3 er = normalize(vec3( p.xy, 1. ));
    vec3 rd = er.x*uu+er.y*vv+er.z*ww;

    //.0015 is blur amount
    vec3 go = .0015*vec3( -1. + 2.*rand2(), 0.);
    //1.3 is focus distance
    vec3 gd = normalize( er*1.3 - go );

    ro += go.x*uu+go.y*vv;
    rd += gd.x*uu+gd.y*vv;
    rd = normalize(rd);

    vec3 tcol = vec3(0.);
    vec3 fcol = vec3(1.);
    //3 is number of levels
    for(int i = 0; i < numLvls; i++){
        vec2 tres = worldInt(ro,rd,-1,ctime);

        if(tres.y<0.) {tcol+=fcol*getSky(rd,ctime);break;}

        vec3 pos = ro + rd * tres.x;

        vec3 nor     = getObjectNorm(pos,tres.y,ctime);
        vec3 ofp     = pos + nor*.001;
        //ofp = pos;
        Material dat = getObjectData(pos,tres.y,ctime);
        dat.shininess = 2./(dat.glossiness*(1.-dat.roughness));

        vec3 color = vec3(0.);
        {
            PLight l = genLight(30.,vec3(1.,.5,0.),vec3(-1.,-.2,3.),.8);
            vec4 ld = vec4(l.pos-pos,length(l.pos-pos));
            if(tres.y==1.){color+=l.color*fcol;}
            else{if(inLight(ofp,ld,0,ctime)){
                vec3 nld = normalize(ld.xyz);
                float i = l.intensity/(ld.w*ld.w);
                color += dat.color*dat.roughness*l.color*i*fcol*max(dot(nor,nld),0.);
                vec3 h = normalize(ld.xyz+(ro-pos));
                color += (1.-dat.roughness)*fcol*i*l.color*pow(max(0.,dot(h,nor)),dat.shininess);
            }}
        }
        {
            PLight l = genLight(8.,vec3(.1,.55,1.),vec3(1.75,0.,2.),.3);
            vec4 ld = vec4(l.pos-pos,length(l.pos-pos));
            if(tres.y==5.){color+=l.color*fcol;}
            else{if(inLight(ofp,ld,2,ctime)){
                vec3 nld = normalize(ld.xyz);
                float i = l.intensity/(ld.w*ld.w);
                color += dat.color*dat.roughness*l.color*i*fcol*max(dot(nor,nld),0.);
                vec3 h = normalize(ld.xyz+(ro-pos));
                color += (1.-dat.roughness)*fcol*i*l.color*pow(max(0.,dot(h,nor)),dat.shininess);
            }}
        }
        {
             vec4 ld = vec4(cosDir(nor),Inf);
             if(inLight(ofp,ld,1,ctime)) color += getSky(ld.xyz,ctime)*fcol*dat.color;
        }

        fcol *= dat.color;
        tcol += color;
        getRay(ro,rd,dat.roughness,ofp,nor);
    }
    ttcol += tcol;
    frame++;
    }
    return ttcol/spp;
}

void mainImage( out vec4 O, in vec2 I ){
    O = vec4(clamp(getPixCol(I),0.,1.),1.);
    O.xyz = pow(O.xyz,vec3(G));
    if(iFrame>0&&!rt) O += texelFetch(iChannel0,ivec2(I),0);
}
