void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = (fragCoord * 2. - iResolution.xy) / iResolution.y;
    vec2 orgUv = uv;

    // setup Uv
    uv *= 20.;
    uv = abs(uv);

    // central time variable
    float time = iTime;
    time *= 3.;

    //periodically blur uv
    uv *= 1. + .02 * cos(iTime*5.);

    //actual distance to the center
    float dCenter = max(uv.x, uv.y);
    // warp distance
    dCenter *= 1. + .2 * cos(iTime*3.);

    // repeating distance
    float d = (1. + cos(dCenter - time))/0.5;

    // black lines
    vec3 col = vec3(smoothstep(0.1, 0.15, d));
    // black lines grey
    col = max(col, vec3(0.3, 0.3, 0.3));

    // center box
    col = mix(col, vec3(1., 0., 0.4), smoothstep(2., 1.7, max(uv.x, uv.y)) - .015);

    // footsteps
    /*vec2 stepUv = abs(orgUv * 20.);
    stepUv = sin(stepUv - time);
    vec2 stepPos = vec2(.7, -1.2);
    float radius = .4;
    col = mix(col, vec3(.2), smoothstep(.1, .0, length(stepUv-stepPos) -radius));*/
    // TODO Unfinished. Might add in the future

    // shade
    if (uv.x > uv.y) col *= pow(0.995, length(dCenter));
    else if (orgUv.y < 0.) col *= pow(0.97, length(dCenter));

    // apply color
    fragColor = vec4(col,1.0);
}
