#define ROT(a)      mat2(cos(a), sin(a), -sin(a), cos(a))


const float pi = acos(-1.0), tau=2.0*pi;

float circle(vec2 p, float r) {
  return length(p) - r;
}

// License: MIT, author: Inigo Quilez, found: https://iquilezles.org/www/articles/distfunctions2d/distfunctions2d.htm
float star5(in vec2 p, in float r, in float rf){
    const vec2 k1 = vec2(0.809016994375, -0.587785252292);
    const vec2 k2 = vec2(-k1.x,k1.y);
    p.x = abs(p.x);
    p -= 2.0*max(dot(k1,p),0.0)*k1;
    p -= 2.0*max(dot(k2,p),0.0)*k2;
    p.x = abs(p.x);
    p.y -= r;
    vec2 ba = rf*vec2(-k1.y,k1.x) - vec2(0,1);
    float h = clamp( dot(p,ba)/dot(ba,ba), 0.0, r );
    return length(p-ba*h) * sign(p.y*ba.x-p.x*ba.y);
}

vec3 palette(float a) {
  return 0.5 + 0.5*sin(vec3(0., 1., 2.) + a);
}

// License: Unknown, author: Unknown, found: don't remember
float hash(float co) {
  return fract(sin(co*12.9898) * 13758.5453);
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
  vec2 q = fragCoord/iResolution.xy;
  vec2 p = -1. + 2. * q;
  p.x *= iResolution.x/iResolution.y;


  vec3 col = vec3(0.);


  for (int i = 0; i < 5; ++i ){
    float itime = iTime+0.2*float(i);
    float dr = abs(sin(p.y*p.x+iTime)+p.y+0.5*sin(itime))-0.1;
    if (dr < 0.0) {
      col = (palette(iTime+float(i))+3.0*(-dr));
      col *= col;
      col *= sqrt(smoothstep(0.0, 0.1, -dr));
    }
  }


  for (int i = 0; i < 5; ++i) {
  float itime = iTime + sqrt(0.5)*float(i);
    float h = hash(float(i)+0.5);  
  vec2 p0 = p;
  p0.x += sin(itime);

  float b = itime;
  b = fract(b)-0.5;
  b *= b;
  b *= 2.0;
  p0.y += b;

  p0 *= ROT(4.0*cos(itime));

  float d = h > 0.5 ? circle(p0,0.3) : star5(p0, 0.5, 0.4);
  float od = abs(d)-0.02;

  if (d < 0.) {
    col = palette(d*8.-2.0*itime);
  }

  if (od < 0.) {
    col = vec3(1.0);
  }
  }
//  col.x += sin(40.0*d);

  col = sqrt(col);

  fragColor = vec4(col, 1.0);
}
