#define BG_CUBEMAP

// ray casting bounding shape
const float CAM_DIST = 4.;
const float CAM_FLE = 1.3;
const float BBOX_RADIUS = 8.;
const float RAY_MIN_DIST = 0.;
const float RAY_MAX_DIST = CAM_DIST+1.1*BBOX_RADIUS;

// ray march
const int MAX_MARCHING_STEPS = 700; // <1, 700, 2000>
float RAY_STEP_FACTOR3 = 0.27755;   // <0.05, 0.27755 ,0.5>
const float RAY_PRECISION = 0.00001;

// 4D rotation
const bool ROT_4D = false;
float ROT_4D_t = 0.; 

// dodecahedron
const int PS_f = 12;
const int PS_e_f= 5;
float PS_a0 = 8.;

// torus
float J1_sh_a0 = -1.0; // -1.0
float J1_sh_a1 = -0.4; // -0.4
int   J1_sh_f1 = 1*PS_e_f;
float J1_sh_pa1 = 0.03;
int   J1_to_c1 = 6*PS_e_f;
int   J1_to_f1 = 2*PS_e_f;
const float J1_to_pa1 = 0.;

// texture
// 0: dodeca_cell_id <-> stripes, 1: stripes, 2: normal, 3: dodeca_cell_id, else: plain color
const int   TEX_MODE = 0;
const bool  TEX_UV_ON = false;
const float TEX_UV_SHARPNESS = 0.00000001;
const vec2  TEX_UV_SUBDIV = vec2(1*PS_e_f, 1*PS_e_f);

// fog
bool  FOG_ON = true;
const float FOG_DENSITY = 0.003;

float map(in vec3 p, out vec4 mat) {   
    //// sdf's
    float rxy = 0., au = 0.0, av = 0.0;
    float cell_id = 0., sr = 0.; //sig(sr): sens of rotation, abs(sr) : number edges per face
    vec2 z = vec2(0.);

    // stereographic 4D, rotation
    if(ROT_4D) p = rot4Dz( p, ROT_4D_t, sqrt(abs(PS_a0)));

    //// map platonic solid cells to one cell
    vec3 pn = normalize(p); 

    // projection from n cells to 1 cell. 
    pn = dodeca_cells(pn, cell_id, sr);
    // invers stereographic projection
    //if (pn.z < 0.) // north pol
    {
        z = pn.xy/(1.-pn.z);
        z = cDodecaPolyArc2disk(z);
    }

    /// r-polygon-distribution
    vec2 e = vec2(3., 0.5);
    float en = 1.+( (PS_a0<0.?e.x:e.y)-1. )*smoothstep(1.,5., abs(PS_a0));        
    float r = length(z); vec2 zn = z/r;
    z = dist(r, en) * zn;   

    /// stereograpic projection    
    pn = sp(vec3(z,sr));

    /// scale radius
    p = length(p) * pn;

    float fracu = 1.0;
    ////fractal level 0: Torus
    au = atan(p.y, p.x);
    rxy = length(p.xy); 
    z = vec2(rxy, p.z);
    z = cmul(z,z); z.x -= PS_a0;

    ////fractal level 1: 2Djulia realaxis-translation,fraction,rotation
    float sh1 = J1_sh_a0;
    float to1 = au*float(J1_to_c1)/float(J1_to_f1);
    sh1 += J1_sh_a1*(1.-pulsecollision(float(J1_sh_f1),au/PI, J1_sh_pa1, 0.5));
    to1 += J1_to_pa1;  
    z = crpt(z, to1, float(J1_to_f1), sh1);
    fracu *= float(J1_sh_f1);

    // distance estimation
    float d = log(length(z));

    #if 0
    st_assert( d != INF, 0 ); // Shows the +INF singularity as a sphere (floating point precision).
    #else
    if (d == INF) {d = 1.;} // step through the singularity
    #endif

    mat.x = cell_id; mat.yz = z; mat.w = au *fracu; // platonic solid cell_id, metatorus complex plane, metatorus angle_u
    return d;
}

float intersect(in vec3 ro, in vec3 rd, out vec4 rescol, in float px) {
    float res = -1.0; // init no intersection

    // bounding shape BB
    vec2 dis = isphere( ro, rd , abs(BBOX_RADIUS));

    if( dis.y<0.0 ) return -1.0; // check BB behind came

    dis.x = max( dis.x, RAY_MIN_DIST ); // check min ray length
    dis.y = min( dis.y, RAY_MAX_DIST ); // check max ray length

    // raymarch signed distance field
    vec4 trap; // data from surface intersection point

	float fh = RAY_STEP_FACTOR3;
    float t = dis.x; // ray starts at boundingbox
    for( int i=0; i<MAX_MARCHING_STEPS; i++  ) // max steps
    { 
        vec3 pos = ro + rd*t;
        float th = RAY_PRECISION * px * t; //th = 0.0001; // minimal bound surface hit
        float h = map( pos, trap );
        if( t>dis.y || h<th ) break; // ray outside BB or hit.
        t += h*fh; // distance
    }
    if( t<dis.y ) // ray inside BB
    {
        rescol = trap;
        res = t;
    }
    return res;
}

vec3 calcNormal( in vec3 p, in float prec ) {
    vec4 tmp; // dummie
    vec2 e = vec2( prec, 0. );
    return normalize(vec3(
		map(p+e.xyy,tmp) - map(p-e.xyy,tmp),
		map(p+e.yxy,tmp) - map(p-e.yxy,tmp),
		map(p+e.yyx,tmp) - map(p-e.yyx,tmp) )
    	);
}

vec3 transform(in vec3 p, in float time) {
	// camera transformation
	if (iMouse.x > 0.0) {
        vec2 m = (2.0*iMouse.xy-iResolution.xy)/iResolution.y;
		p.yz = crot(p.yz,-PI*m.y);
		p.zx = crot(p.zx,-PI*m.x);
	}
	return p.xyz;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    // slider mapping
    RAY_STEP_FACTOR3 = (0.5-0.0001)* pow(RAY_STEP_FACTOR3, 3.) + 0.0001;

    // time modulation shape
    const float time_periode = 30.; // 2.*2.*(1.5+3.+3.) in seconds
    float ntime    = iTime/time_periode;
    float ntime_id = floor(ntime);     // in {0., 1., 2., ... }
    float ntime_fr = ntime - ntime_id; // in [ 0.,+1.]
    ROT_4D_t = ntime;

    PS_a0 *= sin(2.*PI*ntime); // in [-PS_a0, +PS_a0] in length units fractal level 0;

    int sw0 = int(ntime_id) % 2;   
    if (PS_a0 >= 0.) {
        J1_sh_a0 = -1.0 * smoothstep( 2., 4., PS_a0);
        J1_sh_a1 = -0.4 * smoothstep( 4., 6., PS_a0);
        J1_sh_f1 = sw0*PS_e_f;
        J1_sh_pa1 *= 30. * ntime; //
        J1_to_c1 = (3 -4 * sw0) * 2 * PS_e_f; // 
        //J1_to_f1 = 2*PS_e_f;
    }
    else {
        J1_sh_a0 = 2. *smoothstep( 2., 4., -PS_a0); // -2.
        J1_sh_a1 = 0. *smoothstep( 4., 6., -PS_a0);
        //J1_sh_f1 = 1*PS_e_f;
        J1_sh_pa1 *= 0. * ntime;
        J1_to_c1 = 0*PS_e_f;
        J1_to_f1 = (sw0+1) *PS_e_f;    
    }

    // normalized coords
    vec2 uv = (2.*fragCoord.xy-iResolution.xy)/iResolution.y;

    // camera viewport --> worldspace
    vec3 ro = vec3(0,0,-CAM_DIST);
    vec3 rd = vec3(-uv.x,uv.y,CAM_FLE);
    	 ro = transform(ro,iTime);
	     rd = normalize(transform(rd,iTime));

    // ray
    vec4 mat = vec4(0.0); // material-data: vec4(float cell_id, vec2 complex plane, float angle-u)
    float px = 2.0/( iResolution.y*CAM_FLE );
    float t = intersect( ro, rd, mat, px ); // distance hit ray surface

    // color
    vec3 col_sun = vec3(1.,1.,0.8);
    vec3 col_sky = vec3(0.8,0.8,1.);
    #ifdef BG_CUBEMAP
    vec3 col_nebula = srgb2rgb(texture(iChannel0, rd).rgb);
    // background-color
    vec3 bg_col = col_sky * col_nebula;
    #else
    vec3 bg_col = vec3(0.9);    
    #endif

    vec3 col = vec3(0.3); // init, test-color
    // no hit -> sky
    if (t < 0.0) {
        col = bg_col;
    }
    // hit -> object surface
    else {
    	// surface properties
        vec3 p = ro + rd * t;
        vec3 n = calcNormal( p, 0.001);        
        // object id
        float obj_id = mat.x;
        // uv-coords
        vec2 mat_uv;
        mat_uv.x = 0.5*mat.w/PI;
        mat_uv.y = atan(mat.z,mat.y)/PI;
        // uv-texture
        col = (TEX_MODE == 0) ? mix( h2rgb(obj_id/float(PS_f)),h2rgb(abs(mat_uv.y)) ,smoothstep(1.,3., abs(PS_a0)) ) // dodeca_cell_id <-> stripes
            : (TEX_MODE == 1) ? h2rgb(abs(mat_uv.y))       // stripes
            : (TEX_MODE == 2) ? 0.6*n+vec3(0.4)            // normal
            : (TEX_MODE == 3) ? h2rgb(obj_id/float(PS_f))  // dodeca_cell_id
            : vec3(0.8);

        // uv-grid (TODO: not orthogonal!)
        if(TEX_UV_ON) {
            mat_uv *= PI*TEX_UV_SUBDIV;
            col *= 0.5+ 0.5*smoothstep(-TEX_UV_SHARPNESS,TEX_UV_SHARPNESS, sin(mat_uv.x)*sin(mat_uv.y) );
        }        
        // lighting
        // point_light
        vec3 point_lo = vec3(0, -10000, 0);
        vec3 point_ld = normalize(point_lo - p);
        // cubemap light
        #ifdef BG_CUBEMAP
        vec3 col_spe = srgb2rgb(texture(iChannel0, reflect(rd, n)).rgb);
        #endif
        // Phong: ambient- diffuse- specular- light reflections
        #ifdef BG_CUBEMAP        
        col = 0.25  * col * col_sky
            + 0.5   * col * col_sky * col_spe
            + 0.125 * col * col_sun * clamp(dot(point_ld, n), 0., 1.)
            + 0.125 * col * col_sun * pow(clamp(dot(reflect(point_ld, n), -rd), 0., 1.), 10.);
        // fresnel
        col += pow(clamp(1. - dot(n, -rd), 0., 1.), 5.) * 0.4 * bg_col;
        #else
        col = 0.5  * col
            + 0.25 * col * col_sun * clamp(dot(point_ld, n), 0., 1.)
            + 0.25 * col * col_sun * pow(clamp(dot(reflect(point_ld, n), -rd), 0., 1.), 10.);
        #endif

    }
    // fog
    if(FOG_ON) col = mix(col, bg_col, 1.0 - exp(-FOG_DENSITY * pow(t,3.)));
    // gamma correction
    fragColor = vec4(rgb2srgb(col),1.);
}

// consts
const float PI = abs(atan(0.,-1.));
const float PI_2 = PI/2.;
const float INF = abs(1./0.);
const float NAN = abs(0./0.);

/// 1D transformations
float pulsecollision(in float pf, in float x, in float x0, in float pw) {
    /*
    x  : periodic in [0,1]
    x0 : offset
    pw : pulse width in [0,1]
    pf : pulse frequency 
    */
    float ntriw_p = abs( fract( pf*(x - x0) )-0.5); // x_normalized triangle wave function moving in +x_direction
    float ntriw_n = abs( fract( pf*(x + x0) )-0.5); // x_normalized triangle wave function moving in -x_direction
    float pp = smoothstep( 0.5*pw, 0., ntriw_p );   // pulse moves in +x_direction
    float pn = smoothstep( 0.5*pw, 0., ntriw_n );   // pulse moves in -x_direction
    return (pp+pn);                                 // smooth superposition
}

/// 2D transformations
/// complex
vec2 cmul(vec2 za,vec2 zb){
    return za*mat2(zb.x,-zb.y,zb.yx);}

vec2 crot(vec2 z,float a){
    return cmul(vec2(cos(a),sin(a)),z);}

vec2 cinv(vec2 z){
    return z*vec2(1,-1)/dot(z,z);}

vec2 cdiv(vec2 z, vec2 w){
    return cmul(z,cinv(w));}

vec2 cpow(vec2 z, float q){
    float r=pow(length(z), q);
	float a=q*atan(z.y,z.x);
    return vec2(r*cos(a),r*sin(a));}

vec2 cpow(vec2 z, int n){
  float r = length(z);
  float theta = atan(z.y,z.x);
  return pow(r,float(n))*normalize(vec2(cos(float(n)*theta),sin(float(n)*theta)));}

vec2 crpt(vec2 z, float a, float p, float x0){
  /* (z*e^ia)^p-x0 */
  return cpow( cmul(z, vec2(cos(-a),sin(-a))),p) - vec2(x0, 0.);}

vec2 cDodecaPolyArc2disk(vec2 z){
    // https://people.maths.ox.ac.uk/trefethen/lightning.html
    const int NUM_j = 25; // poles
    const vec2[] rf = vec2[NUM_j]
    (vec2(-0.8260, - 0.5637)
    ,vec2( 0.8091, + 0.5877)
    ,vec2(-0.3090, - 0.9511)
    ,vec2(-0.3089, - 0.9511)
    ,vec2( 0.8089, - 0.5879)
    ,vec2(-1.0000, + 0.0000)
    ,vec2(-1.0000, + 0.0001)
    ,vec2(-0.3090, + 0.9511)
    ,vec2(-0.3089, + 0.9511)
    ,vec2( 0.9996, - 0.0294)
    ,vec2( 0.9103, + 0.4140)
    ,vec2( 0.2242, + 0.9746)
    ,vec2( 0.3369, - 0.9415)
    ,vec2(-0.8576, + 0.5143)
    ,vec2(-0.4307, - 0.9025)
    ,vec2(-0.3567, + 0.9342)
    ,vec2(-0.9988, - 0.0485)
    ,vec2( 0.7796, + 0.6263)
    ,vec2( 0.7796, - 0.6263)
    ,vec2(-0.2828, - 0.9592)
    ,vec2(-0.3161, + 0.9487)
    ,vec2(-1.0000, + 0.0075)
    ,vec2(-0.2187, + 0.9758)
    ,vec2( 0.8366, - 0.5479)
    ,vec2( 0.8134, + 0.5817));

    const vec2[] rw = vec2[NUM_j]
    (vec2(-0.1213, - 0.0   )
    ,vec2( 0.0028, + 0.0187)
    ,vec2( 0.0000, - 0.0000)
    ,vec2(-0.0010, + 0.0008)
    ,vec2(-0.0017, - 0.0008)
    ,vec2(-0.0000, - 0.0000)
    ,vec2(-0.0041, - 0.0007)
    ,vec2(-0.0000, - 0.0000)
    ,vec2( 0.0219, - 0.0012)
    ,vec2(-0.2767, - 0.2629)
    ,vec2( 0.2548, - 0.3937)
    ,vec2( 0.4733, + 0.0936)
    ,vec2(-0.0954, - 0.0232)
    ,vec2(-0.1703, + 0.0600)
    ,vec2(-0.0347, - 0.0277)
    ,vec2(-0.3617, + 0.0048)
    ,vec2(-0.0493, + 0.0086)
    ,vec2( 0.1573, + 0.1009)
    ,vec2(-0.0406, - 0.0040)
    ,vec2(-0.0118, - 0.0116)
    ,vec2( 0.0485, + 0.1712)
    ,vec2(-0.0014, + 0.0204)
    ,vec2( 0.1498, + 0.3189)
    ,vec2(-0.0419, - 0.0255)
    ,vec2( 0.1042, - 0.0471));

    const vec2[] rz = vec2[NUM_j]
    (vec2(-0.2339, - 0.1614)
    ,vec2( 0.2737, + 0.1982)
    ,vec2(-0.1045, - 0.3217)
    ,vec2(-0.1039, - 0.3216)
    ,vec2( 0.2731, - 0.1991)
    ,vec2(-0.3383, + 0.0   )
    ,vec2(-0.3379, + 0.0006)
    ,vec2(-0.1045, + 0.3217)
    ,vec2(-0.1039, + 0.3216)
    ,vec2( 0.2841, - 0.0069)
    ,vec2( 0.2810, + 0.1078)
    ,vec2( 0.0682, + 0.2764)
    ,vec2( 0.0943, - 0.2680)
    ,vec2(-0.2418, + 0.1502)
    ,vec2(-0.1496, - 0.2698)
    ,vec2(-0.1287, + 0.2944)
    ,vec2(-0.3202, - 0.0306)
    ,vec2( 0.2411, + 0.2130)
    ,vec2( 0.2411, - 0.2130)
    ,vec2(-0.0808, - 0.3165)
    ,vec2(-0.1114, + 0.3141)
    ,vec2(-0.3331, + 0.0088)
    ,vec2(-0.0510, + 0.3095)
    ,vec2( 0.2770, - 0.1635)
    ,vec2( 0.2747, + 0.1886));

    // polynomial barycentric representation
    vec2 zn = vec2(0.); vec2 zd = vec2(0.);       
    for(int j = 0; j<NUM_j; j++)
    {
        vec2 zj = cdiv( rw[j], (z-rz[j]));
        zd += zj; // sum denominator
        zn += cmul(rf[j], zj); // sum nominator
    }
    return cdiv(zn, zd);
}

/// 4D transformation
vec3 rot4Dz(in vec3 p, in float t, in float r_inv) {
    float a = 2.*PI*t;    
    p /= r_inv;
    float rn = length(p);
    float rn2 = dot(rn,rn);
    vec2 zw = cmul(vec2(2.*p.z, rn2-1.),vec2(cos(a),sin(a)));
    p = vec3(2.*p.xy,zw.x)/(rn2+1.-zw.y);   
    return r_inv * p;
}

// map spherical platonic solid pyramidal-face-cells to one cell located at -z-axis.
vec3 dodeca_cells(in vec3 p, out float cell_id, out float sr) {
	//// dodecaeder
	const float gr0 = (1.+sqrt(5.))/2.; const float gr1 = 1./gr0; const float gr2 = gr0-gr1;
	// cell-wall-face-normals
	bool nx = dot(vec3( 1., 0., 0.),p)>=0.;
	bool ny = dot(vec3( 0., 1., 0.),p)>=0.;
	bool nz = dot(vec3( 0., 0., 1.),p)>=0.;
	//
	bool n0A = dot(vec3( gr2,-gr0, gr1),p)>=0.;
	bool n0B = dot(vec3( gr1, gr2,-gr0),p)>=0.;
	bool n0C = dot(vec3(-gr0, gr1, gr2),p)>=0.;
	//
	bool n1A = dot(vec3(-gr2,-gr0,-gr1),p)>=0.;
	bool n1B = dot(vec3(-gr1, gr2, gr0),p)>=0.;
	bool n1C = dot(vec3( gr0, gr1,-gr2),p)>=0.;
	//
	bool n2A = dot(vec3(-gr2, gr0, gr1),p)>=0.;
	bool n2B = dot(vec3(-gr1,-gr2,-gr0),p)>=0.;
	bool n2C = dot(vec3( gr0,-gr1, gr2),p)>=0.;
	//
	bool n3A = dot(vec3( gr2, gr0,-gr1),p)>=0.;
	bool n3B = dot(vec3( gr1,-gr2, gr0),p)>=0.;
	bool n3C = dot(vec3(-gr0,-gr1,-gr2),p)>=0.;

	// cells
    float a = PI_2-atan((sqrt(5.)-1.)/2.);
    float b = PI/10.; // 18Â°
    float a_x = 0.; float a_y = 0.; float a_z = 0.;
	// ---------------------------------------------------------------
	//Fpxpy
    if(ny && !n2B && n2C && !n0C && n0B)	
	{
		cell_id = 0.;
		a_y = PI_2;
        a_x = a-PI_2;
		a_z = b;
	}
	//Fnxny
	else if(!ny && n2B && !n2C && n0C && !n0B)
	{
		cell_id = 1.;
		a_y = PI_2;
        a_x = a+PI_2;
		a_z = b;
	}
	// ---------------------------------------------------------------
	//Fpxny
	else if(ny && n1B && !n1C && n3C && !n3B)
	{
		cell_id = 2.;
		a_y = -PI_2;
		a_x =  a-PI_2;
		a_z =  b;
	}
	//Fnxpy
	else if(!ny && !n1B && n1C && !n3C && n3B)
	{
		cell_id = 3.;
		a_y = -PI_2;
		a_x =  a+PI_2;
		a_z =  b;
	}
	// ---------------------------------------------------------------
	//Fpypz
	else if(!nz && n3C && !n3A && n0A && !n0C)
	{
		cell_id = 4.;
		a_y =  0.;
        a_x =  a;
		a_z =  b;
	}
	//Fnynz
	else if(nz && !n3C && n3A && !n0A && n0C)
	{
		cell_id = 5.;
		a_y =  0.; 
		a_x =  a+PI;
		a_z =  b;
	}

	// ---------------------------------------------------------------
	//Fpynz
	else if(nz && n2C && !n2A && n1A && !n1C)
	{
		cell_id = 6.;
		a_y =  0.;
        a_x = -a+PI;
		a_z = -b;        
	}
	//Fnypz
	else if(!nz && !n2C && n2A && !n1A && n1C)
	{
		cell_id = 7.;
		a_y =  0.;
        a_x = -a;
		a_z = -b;       
	}
	// ---------------------------------------------------------------
	//Fpzpx
	else if(!nx && n1A && !n1B && n0B && !n0A)
	{
		cell_id = 8.;
		a_y =  a-PI_2;
		a_x =  0.;
		a_z =  0.;
	}
	//Fnznx
	else if( nx && !n1A && n1B && !n0B && n0A)
	{
		cell_id = 9.;
		a_y =  a+PI_2;
		a_x =  0.;
		a_z =  0.;
	}
	// ---------------------------------------------------------------
	//Fpznx
	else if(nx && n3A && !n3B && n2B && !n2A)
	{
		cell_id = 10.;
		a_y = -a+PI_2;
		a_x =  0.;
		a_z =  b+PI_2;
	}
	//Fnzpx
	else if(!nx && !n3A && n3B && !n2B && n2A )
	{
		cell_id = 11.;
		a_y = -a+PI_2;
		a_x =  PI;
		a_z =  b-PI_2;
	}
	// ---------------------------------------------------------------
	// cell orientation
	vec2 ei_y = vec2(cos(a_y),sin(a_y)); p.zx = cmul(p.zx,ei_y); // rotate around y-axis
	vec2 ei_x = vec2(cos(a_x),sin(a_x)); p.yz = cmul(p.yz,ei_x); // rotate around x-axis
	vec2 ei_z = vec2(cos(a_z),sin(a_z)); p.xy = cmul(p.xy,ei_z); // rotate around z-axis
	// non cyclic
	sr = 5.;
    return p;
}

// stereographic projection 
float sig(float x) {
    return x < 0. ? -1. : +1.; 
    }

float dist(float x, float e) {
    // e in [0., +inf]
    return sign(x)*abs( pow(sign(x)*x, abs(e)) );
    //return sign(x)*abs( pow(abs(x), abs(e)) );
    }

vec2 r_poly_dist(vec2 z, vec2 e, float sh_a) {   
    /// r-polygon-distribution
    float r = length(z); z = (r<0.)? vec2(0) : z/r; 
    float d0 = 0.5 *abs(e.y-e.x);
    float e2 = e.x + d0 + d0 * (2.*smoothstep(-1., 1., -sh_a) - 1.);
    return dist(r, e2) * z;
}   

vec3 sp(vec3 zsr){
    vec2 z = zsr.xy; float sr = zsr.z;
    // stereopraphic projection (complex plane to unit sphere)
    float zz = dot(z,z); vec3 pn;
    if (bool(sig(sr))) {// north pole
        pn = vec3(2. * z.x,  2. * z.y, -1. + zz);
        }
    else { // south pol
        z = cinv(z);
        pn = vec3(2. * z.x, -2. * z.y,  1. - zz);
        }
    pn /= 1.+zz;
    return pn;
}

vec3 spb(vec3 zsr) {
    vec2 z = zsr.xy; float sr = zsr.z;
    // inverse stereograpic projection
    float k = 2./(1.+dot(z,z)); vec3 pn;
    if (bool(sig(sr))) {// north pole
        pn = vec3(k*z, k-1.); }
    else { // south pol
        z = cinv(z);
        pn = vec3(k*z, -(k-1.)); }
    return pn;
}

// bb
vec2 isphere(in vec3 ro, in vec3 rd, in float r) {
    // https://iquilezles.org/
	float b = dot(ro,rd);
	float c = dot(ro,ro) - r*r;
    float h = b*b - c;   
    if( h<0.0 ) return vec2(-1.0);
    h = sqrt( h );
    return -b + vec2(-h,h);
}

// colorspace transformations
vec3 rgb2hsv(vec3 col) {
    // https://en.wikipedia.org/wiki/HSL_and_HSV
    col = clamp(col, 0., 1.); 
    //
    float m_max = max(col.r,max(col.g,col.b));
    float m_min = min(col.r,min(col.g,col.b));
    float d = m_max-m_min;
    // hue
    float h = 0.; // if(d == 0.) undefined
    if(m_max==col.r) {h = (col.g-col.b)/d+0.;}
    if(m_max==col.g) {h = (col.b-col.r)/d+2.;}
    if(m_max==col.b) {h = (col.r-col.g)/d+4.;}
    h = fract(h/6.); // periodic in[0.,1.]
    // value
    float v = m_max;
    // saturation
    float s = (v == 0.)?0.: d/v;
    return vec3(h,s,v);
}

vec3 h2rgb(float h){
    vec3 rgb = clamp( abs(mod(h*6.0+vec3(0.0,4.0,2.0),6.0)-3.0)-1.0, 0.0, 1.0 );
    return rgb*rgb*(3.0-2.0*rgb); /* cubic smoothing */
}

vec3 hsv2rgb(float h, float s, float v){     
    return v * mix( vec3(1.0), h2rgb(h), s); /* hue, saturation, value */
}

// srgb <--> rgb (linear)
vec3 srgb2rgb(vec3 col) {
    return pow(col, vec3(2.2));
}

vec3 rgb2srgb(vec3 col) {
    return pow(col, vec3(1./2.2));
}
