// variant of https://shadertoy.com/view/mtS3Wd
// variant of https://shadertoy.com/view/mlBGWd
// variant of https://shadertoy.com/view/dlBGWd

#define rot(a)  mat2(cos(a+vec4(0,11,33,0)))                // rotation 
#define H(p)    fract(1e4*sin(dot(p,R.xy-17.)))

void mainImage(out vec4 O, vec2 U)
{
    float t=9.,m, h; 
    vec3  R = iResolution, 
          D = normalize(vec3(U+U, -12.*R.y) - R),           // ray direction
          p = vec3(0,0,20), q,a,                            // marching point along ray 
          M = iMouse.z > 0. ? 6.*iMouse.xyz/R -3.
              :  vec3( 1,.5,0) * cos(.3*iTime + vec3(0,11,0)); 

    for ( O=vec4(1); O.x > 0. && t > .01; O-=.01 )          // march scene
        q = p, // t = 9.,
        q.yz *= rot(-M.y),                                  // rotations
        q.xz *= rot(-M.x-1.57), 
        t = length(q),
        a = abs(q), 
        m = max(a.x, max(a.y,a.z)), 
     // U = m==a.x ? q.yz : m==a.y ? q.xz : q.xy,           // cube-sphere mapping
        q = m==a.x ? q.yzx : m==a.y ? q.xzy : q,        
        U = vec2(atan(q.x,q.z),atan(q.y,q.z))*q.z*4./3.1416,// kastorp's way : more regular
        U *= 8./m, h = H(ceil(U)),
        q.xy = ( fract(U) - .5 ) / 8. *.7,                  // distance to walls
        t = min ( t-1.35, max( t-1.5 ,                      // trimmed by sphere
                  min( abs(q.x+sign(h-.5)*q.y),                     // wall = random tile diagonal
                       length(abs(.5-abs(fract(U)-.5)))/8.*.7) ) ), // columns at wall ends
               // min(abs(q.x),abs(q.y)) )),                // wall = waffle
        p += .5*t*D;                                        // step forward = dist to obj    


   O *= O*O*1.5;                                            // color scheme
// O *= .5+.5*vec4(m==a.x, m==a.y, m==a.z, 0)* (.4+.6*mod(ceil(U.x)+ceil(U.y),2.));  // debug
}
