vec2 hash( vec2 p )
{
    p = vec2(dot(p,vec2(127.1,311.7)), dot(p,vec2(269.5,183.3)));
	return -1.0 + 2.0*fract(sin(p)*43758.5453123);
}

mat2 Rot(float a)
{
    float s = sin(a);
    float c = cos(a);
    return mat2(c, -s, s, c);
}

// Copied from the book of shaders: https://thebookofshaders.com/13/
// Based on Morgan McGuire @morgan3d
// https://www.shadertoy.com/view/4dS3Wd
float noise( in vec2 p ) {
    const float K1 = 0.366025404; // (sqrt(3)-1)/2;
    const float K2 = 0.211324865; // (3-sqrt(3))/6;
	vec2 i = floor(p + (p.x+p.y)*K1);	
    vec2 a = p - i + (i.x+i.y)*K2;
    vec2 o = (a.x>a.y) ? vec2(1.0,0.0) : vec2(0.0,1.0); //vec2 of = 0.5 + 0.5*vec2(sign(a.x-a.y), sign(a.y-a.x));
    vec2 b = a - o + K2;
	vec2 c = a - 1.0 + 2.0*K2;
    vec3 h = max(0.5-vec3(dot(a,a), dot(b,b), dot(c,c) ), 0.0 );
	vec3 n = h*h*h*h*vec3( dot(a,hash(i+0.0)), dot(b,hash(i+o)), dot(c,hash(i+1.0)));
    return dot(n, vec3(44.0));	
}

//Copied from the same book of shaders chapter
#define NUM_OCTAVES 3

float fbm ( in vec2 _st) {
    float v = 0.0;
    float a = 0.5;
    vec2 shift = vec2(100.0);
    // Rotate to reduce axial bias
    mat2 rot = mat2(cos(0.5), sin(0.5),
                    -sin(0.5), cos(0.50));
    for (int i = 0; i < NUM_OCTAVES; ++i) {
        v += a * noise(_st);
        _st = rot * _st * 2.0 + shift;
        a *= 0.5;
    }
    return v;
}

//The shader code that embeds a 938x84 frame buffer at its largest size without stretching the input

const vec2 target_resolution = vec2(938, 84);

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Same texCoord as in Minecraft
    vec2 texCoord = fragCoord/iResolution.xy;
    // Own uv variable
    vec2 uv = texCoord;

    // Center origin around screen center (range -0.5, 0.5)
    uv -= .5;

    // Squish image in excessive dimension
    float r = (iResolution.x/iResolution.y) / (target_resolution.x/target_resolution.y);
    if (r >= 1.) uv.x *= r;
    else uv.y /= r;

    // Recenter origin to start at 0 (range 0.0, 1.0)
	uv += .5;
    uv.y -= 0.5/r - 0.5;    

    //color1/2/3均为可调参数
    vec4 color1 = vec4(0.71373,0.85882,0.92157,1.0); 
    vec4 color2 = vec4(0.60000,0.67843,1.0,1.0);
    vec4 color3 = vec4(0.180,0.180,1.000,1.0);

    float PI = 3.1415926;
    float time = iTime * 0.3; 

    //scale为可调参数
    float scale = 3.0;
    //colorStep1为可调参数
    float colorStep1 = 0.33333;
    //colorStep2为可调参数
    float colorStep2 = 0.66666;

    float ease[3];
    ease[0] = -(cos(PI * fract(time/scale)) - 1.0)/2.0 * scale;  
    ease[1] = fract(time/scale)*scale;
    ease[2] = (1.0 - pow(1.0 - fract(time/scale),3.0))*scale;

    float easeChoice = mod(time/scale,4.0);
    int i = int(easeChoice);
    float offset  = ease[i] + floor(time/scale)*scale;
    offset = uv.y - offset;
    float y = mod(offset,scale);

    float step1 = scale * (colorStep1);
    float step2 = scale * max(colorStep1+0.1, (colorStep2)); 

    if (y < step1) {
    float dist1 = smoothstep(0.0, step1, y);
    fragColor = mix(color3, color1, dist1);
    }

    //红到蓝 step1 - step2
    else if (y > step1 && y < step2) {
    float dist2 = smoothstep(step1, step2, y);
    fragColor = vec4(dist2,dist2,dist2,1.0);
    fragColor = mix(color1, color2, dist2);
    }

    //蓝到绿 step2 - 1.0
    else{
    float dist3 = smoothstep(step2, 1.0 * scale, y);
    fragColor = vec4 (dist3,dist3,dist3,1.0);
    fragColor = mix(color2, color3, dist3);
    }

    //fbm color
    vec2 uv2 = uv;
	uv2.x *= 4.0;
	uv2.y *= 1.0; 
    uv2.x += time * 0.4;
    uv2.y -= time;
	vec3 col = mix(vec3(0.0,0.0,0.), vec3(1.), fbm(uv2 - vec2(2.0*iTime)));
	vec3 color = vec3(0.0);

    //第一层基础fbm的流动 uv+time
	vec2 q = vec2(0.);
    q.x = fbm(uv2 - .4 * iTime);
    q.y = fbm(uv2 - vec2(1.0));

    //在第一层fbm的基础上再进行一次fbm，uv+ r + time, time可以改变流速
	vec2 g = vec2(0.);
    g.x = fbm( uv2 - 1.0*q + vec2(1.7,9.2) -  .5 * iTime );
    g.y = fbm( uv2 - 1.0*q + vec2(8.3,2.8) -  .26 * iTime);

    //得到基础的fbm
	float f = fbm(uv2+g);

    color = mix(vec3(0.0,0.0,0.0),
                vec3(.2,.2,.2),
                clamp((f*f)*4.0,0.0,1.0));

    color = mix(color,
                vec3(0.7,0.7,0.7),
                clamp(length(q),0.0,1.0));

    color = mix(color,
                vec3(1.0,1.0,1.0),
                clamp(length(g.x),0.0,1.0));

    //提升黑白区的对比度
    vec4 fbmColor = vec4((f*f*f+.6*f*f+.5*f)*vec3(1.),1.);
	fragColor = mix(fragColor,vec4(1.0,1.0,1.0,1.0),color.r);

    // Black bars at the sides
    if (uv.x < 0. || uv.x > 1. || uv.y < 0. || uv.y > 1.) fragColor = vec4(0);    
}
