#define DISPLAY_STYLE vec2(0.1, 0.02)

#define AA(d) smoothstep(px, -px, d)

#define HIDE_ZERO

struct displayInfo
{
    bool a, b, c, d, e, f, g;
};

float box(in vec2 uv, vec2 b)
{
    uv = abs(uv)-b; return max(uv.x, uv.y);
}

#define t true
#define f false
displayInfo[] info = displayInfo[] (
displayInfo(t, t, t, t, t, t, f),
displayInfo(f, t, t, f, f, f, f),
displayInfo(t, t, f, t, t, f, t),
displayInfo(t, t, t, t, f, f, t),
displayInfo(f, t, t, f, f, t, t),
displayInfo(t, f, t, t, f, t, t),
displayInfo(t, f, t, t, t, t, t),
displayInfo(t, t, t, f, f, f, f),
displayInfo(t, t, t, t, t, t, t),
displayInfo(t, t, t, t, f, t, t));
#undef t
#undef f

#define vc(p, b, z) if(i.z) d = min(d, box(uv-vec2(size.xx*p.xy), size.yx+vec2(0, size.y)))
#define hc(p, b, z) if(i.z) d = min(d, box(uv-vec2(0, size.x*p*2.), size.xy+vec2(size.y, 0)))
float segDisplay(vec2 uv, vec2 size, displayInfo i)
{
    float d = 1e20;
    hc(1., size.x, a);
    vc(vec2(1, 1), size.x, b);
    vc(vec2(1, -1), size.x, c);
    hc(-1., size.x, d);
    vc(vec2(-1, -1), size.x, e);
    vc(vec2(-1, 1), size.x, f);
    hc(0., size.x, g);
    return d;
}

float getClock(vec2 uv, float px, vec2 time, float blink)
{
    //float hours = mod(time/60./60.-offset, 12.)+offset;
    //float minutes = mod(time/60., 60.);
    float hours = time.x;
    float minutes = time.y;

    float col = 0.;
    col +=
    #ifdef HIDE_ZERO
    step(0.1, floor(time.x/10.))*
    #endif
    AA(segDisplay(uv+vec2(DISPLAY_STYLE.x*1.5, 0)-vec2(-DISPLAY_STYLE.x*4., 0), DISPLAY_STYLE, info[int(mod(hours/10., 10.))]));
    col += AA(segDisplay(uv-vec2(DISPLAY_STYLE.x*1.5, 0)-vec2(-DISPLAY_STYLE.x*4., 0) , DISPLAY_STYLE, info[int(mod(hours, 10.))]));
    col += AA(segDisplay(uv+vec2(DISPLAY_STYLE.x*1.5, 0)-vec2( DISPLAY_STYLE.x*4., 0), DISPLAY_STYLE, info[int(mod(minutes/10., 10.))]));
    col += AA(segDisplay(uv-vec2(DISPLAY_STYLE.x*1.5, 0)-vec2( DISPLAY_STYLE.x*4., 0) , DISPLAY_STYLE, info[int(mod(minutes, 10.))]));
    col += AA(box(uv-vec2(0, DISPLAY_STYLE.x*1.3), vec2(DISPLAY_STYLE.y)))*blink;
    col += AA(box(uv+vec2(0, DISPLAY_STYLE.x*1.3), vec2(DISPLAY_STYLE.y)))*blink;
    return col;
}

float circles(vec2 p, float px, float time)
{
    float sum = 0.;
    for(float i = -2.01; i <= 2.; i++) // An artifact appears if i is initialized at -2 instead of -2.01
    {
        float currentCircle = floor(p.x+i);
        float circleSize = 1.2+0.2*sin(currentCircle*0.5+time);
        sum += smoothstep(px, -px, distance(p, vec2(currentCircle, 0))-circleSize);
    }
    return sum;
}

float pulse(float x, float k)
{
    return round(min(1., fract(x)/(2.*k)));
}

#define time iDate.w

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord*2.-iResolution.xy)/iResolution.y;
    float px = 1.5/iResolution.y;

    float clock1 = getClock(uv, px, vec2(mod(time/60./60.-1., 12.)+1., mod(time/60., 60.)), mix(1., 0.8, pulse(time*0.5, 0.5)));
    float clock2 = getClock(uv, px, vec2(88., 88.), 0.);

    vec3 col = ((circles((vec2(uv.x*sign(uv.y), abs(uv.y))-vec2(0, 1))*6., 6.*px, iTime*0.5)*0.2)+0.1)*vec3(1);
    col += clamp(clock2*0.1+clock1, 0., 1.)*0.4;
    fragColor = vec4(col, 1);
}
