// [abstract]
// Capsids house viral genetic material. Understanding their structure is
// essential for developing antivirals, gene therapies, and vaccines. This is an
// app for modeling based on Caspar-Klug theory and lattice geometry. The goal is
// reimplementation of the democapsid app (https://github.com/dnanto/democapsid)
// and to show that it's a cool effect for demoscene.

// [acknowledgements]
// @iq, @FabriceNeyret2, @inspirnathan, @mla

// [controls]
// * ctrl+1-3: icosahedron, facet, or plane view (default: ctrl+1)
// * (shift+)1-7: tilings and their duals (default: 1)
// * (shift+)h/k: decrement/increment plane step parameters (>= 0) (default: h=2, k=1)
// * (shift+)t/f/z: decrement/increment edge thickness, fiber length, camera zoom
// * l/p/c/r/s: outline, h/k-walk arrows, centroid point, random Wythoff, spin (default: off/off/on/off/off)

float scene(vec3 p) {
    // dynamic scene parameters
    vec4 para_1 = texelFetch(iChannel1, IDX_PARA_1, 0);
    vec4 para_2 = texelFetch(iChannel1, IDX_PARA_2, 0);
    vec4 mode_1 = texelFetch(iChannel1, IDX_MODE_1, 0);
    vec4 mode_2 = texelFetch(iChannel1, IDX_MODE_2, 0);
    bool is_lines = mode_1.g == MODE_LINES;
    bool is_paths = mode_1.b == MODE_PATHS;
    bool is_point = mode_2.r == MODE_POINT;
    bool[5] config = bool[5] (true, true, is_lines, is_paths, is_point);
    //// Caspar-Klug
    float h = para_1.r;
    float k = para_1.g;
    //// Wythoff
    vec4 tiling = TILINGS[int(para_1.b)];
    vec2 g = tiling.rg;                 // generator point
    float m = tiling.b;                 // mirror toggles
    float w = tiling.w;                 // wall toggles
    float t = THICK_STEP * para_1.a;    // thickness
    float f = FIBER_STEP * para_2.r;    // fiber length
    if (mode_1.a == MODE_RANDO) {
        ////// pick point within fundamental triangle
        ////// https://mathworld.wolfram.com/TrianglePointPicking.html
        ////// http://extremelearning.com.au/evenly-distributing-points-in-a-triangle/
        float a1 = 0.5 + sin(1.25 * iTime / 1.25) / 2.0;
        float a2 = 0.5 + cos(2.00 * iTime / 1.75) / 2.0;
        ////// generator point
        g = (((a1 + a2) < 1.0) ? (a1 * ft.zw + a2 * ft.xy) : ((1.0 - a1) * ft.zw + (1.0 - a2) * ft.xy));
        ////// mirrors
        m = mod(round((iTime - 2.0 / 1.5) / 2.0), 10.0);
        ////// rotate
    }
    //// spin
    p = mode_2.g == MODE_RSPIN ? rotmat3(vec3(iTime * 0.25)) * p : p;

    // distance
    float d = 1.0;
    //// scale to 1.0 / (h/k-facet side length)
    float s = 1.0 / length(mat2(1.5, cos30, 0.0, 2.0 * cos30) * vec2(h, k));
    /****/ if (mode_1.r == MODE_ICOSA) { 
        //// find nearest centroid facet
        int argkey = 0;
        float argval = distance(p, centroids[0]);
        for (int i = 1; i < transforms.length(); i++) {
            float result = distance(p, centroids[i]);
            if (result < argval) {
                argkey = i;
                argval = result;
            }
        }
        //// now calc
        vec4 q = transforms[argkey] * vec4(p - centroids[argkey], 1);
        config[0] = false;
        d = sdCasparKlugFacet(q.xyz / s, h, k, g, m, w, t, config) * s;
        vec3 vertex = normalize(vec3(1, 0, PHI));
        vec3[4] dir = vec3[4] (vec3( 1, 0,  1), vec3(-1, 0,  1), vec3( 1, 0, -1), vec3(-1, 0, -1));
        // vertex fibers
        for (int i = 0; f > 0.0 && i < dir.length(); i++) {
            vec3 u, v;
            u = ICO_CIRCUMRADIUS * (vertex * dir[i]).xyz; v = u + f * u;
            d = opUnion(d, sdCapsule(p.xyz, u, v, t / 4.0));
            d = opUnion(d, sdSphere(p.xyz - v, t / 2.0));
            u = ICO_CIRCUMRADIUS * (vertex * dir[i]).yzx, v = u + f * u;
            d = opUnion(d, sdCapsule(p.xyz, u, v, t / 4.0));
            d = opUnion(d, sdSphere(p.xyz - v, t / 2.0));
            u = ICO_CIRCUMRADIUS * (vertex * dir[i]).zxy, v = u + f * u;
            d = opUnion(d, sdCapsule(p.xyz, u, v, t / 4.0));
            d = opUnion(d, sdSphere(p.xyz - v, t / 2.0));
        }
    } else if (mode_1.r == MODE_FACET) {
        d = sdCasparKlugFacet(p.xyz / s, h, k, g, m, w, t, config) * s;
    } else if (mode_1.r == MODE_PLANE) {
        config[1] = false;
        d = sdCasparKlugFacet(p.xyz / s, h, k, g, m, w, t, config) * s;
    }

    return d;
}

float march(vec3 ro, vec3 rd) {
    float depth = MIN_DIST;
    float d;

    for (int i = 0; i < MAX_ITER; i++) {
        vec3 p = ro + depth * rd;
        d = scene(p);
        depth += d;
        if (d < PRECISION || depth > MAX_DIST) {
            break;
        }
    }

    d = depth;

    return d;
}

vec3 normal(in vec3 p) {
    vec2 e = vec2(1, -1) * EPSILON;
    return normalize(
        e.xyy * scene(p + e.xyy) +
        e.yyx * scene(p + e.yyx) +
        e.yxy * scene(p + e.yxy) +
        e.xxx * scene(p + e.xxx)
    );
}

mat3 camera(vec3 cameraPos, vec3 lookAtPoint) {
	vec3 cd = normalize(lookAtPoint - cameraPos);
	vec3 cr = normalize(cross(vec3(0, 1, 0), cd));
	vec3 cu = normalize(cross(cd, cr));
	return mat3(-cr, cu, -cd);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 p = (fragCoord - 0.5 * iResolution.xy) / iResolution.y;
    vec2 m = iMouse.xy / iResolution.xy;

    if (m == vec2(0.0)) m = vec2(0.5);

    vec3 col = vec3(0);
    vec3 lp = vec3(0);
    vec4 para_2 = texelFetch(iChannel1, IDX_PARA_2, 0);
    vec3 ro = vec3(0, 0, para_2.g * ZOOM_STEP);

    float camRadius = 1.0;
    ro.yz = ro.yz * camRadius * rotmat2(mix(-PI / 2.0, PI / 2.0, m.y));
    ro.xz = ro.xz * rotmat2(mix(-PI, PI, m.x)) + vec2(lp.x, lp.z);

    vec3 rd = camera(ro, lp) * normalize(vec3(p, -1));

    float d = march(ro, rd);

    if (d > MAX_DIST) {
        col = COLOR_BACKGROUND;
    } else {
        vec3 p = ro + rd * d;
        vec3 norm = normal(p);

        vec3 lightPos = vec3(0, 2, 2);
        vec3 lightDir = normalize(lightPos - p) * 0.65;

        float diff = clamp(dot(norm, lightDir), 0.0, 1.0) * 0.5 + 0.5;

        vec3 rnd = 0.5 + 0.5 * cos(iTime + p.xyx + vec3(0, 2, 4));
        col = vec3(diff) + rnd;    
    }

    fragColor = vec4(col, 1.0);
}

