// keyboard
///// https://www.shadertoy.com/view/llySRh
#define keyClick(ascii)   ( texelFetch(iChannel0, ivec2(ascii, 1), 0).x > 0. )
#define keyDown(ascii)    ( texelFetch(iChannel0, ivec2(ascii, 0), 0).x > 0. )

// buffer data/indexing
#define IDX_PARA_1 ivec2(0, 0)
#define IDX_PARA_2 ivec2(0, 1)
#define IDX_MODE_1 ivec2(0, 2)  
#define IDX_MODE_2 ivec2(0, 3)  
#define MODE_ICOSA 1.0
#define MODE_FACET 2.0
#define MODE_PLANE 3.0
#define MODE_LINES 4.0
#define MODE_PATHS 5.0
#define MODE_RANDO 6.0
#define MODE_POINT 7.0
#define MODE_RSPIN 8.0
#define THICK_STEP 0.005
#define FIBER_STEP 0.05
#define ZOOM_STEP  0.25

// ray marching
#define MAX_ITER 255
#define MIN_DIST 0.0
#define MAX_DIST 100.0
#define PRECISION 0.001
#define EPSILON 0.0005
#define COLOR_BACKGROUND vec3(0, 0, 0)
#define COLOR_AMBIENT vec3(0.42, 0.20, 0.1);

// math
//// constants
#define PI 3.1415926535897932384626433
#define PHI (1.0 + sqrt(5.0)) / 2.0
#define ICO_CIRCUMRADIUS sin((2.0 * PI) / 5.0)
#define cos30 cos(radians(30.0))
#define sqrt3 sqrt(3.0)
//// fundamental triangle
#define ft vec4(0, cos30, 0.5, cos30)
//// special generator points
#define gq vec2(0.25, cos30)
#define gc (ft.xy + cos30 * ft.zw) / (0.5 + cos30 + 1.0)
#define gx vec2((3.0 - sqrt3) / 4.0, tan(radians(60.0)) * ((3.0 - sqrt3) / 4.0))
//// Wythoff construction ===========================================================
const vec4[14] TILINGS = vec4[14] (
    ////// Platonic and Archimedean tilings -----------------------------------------
    vec4(ft.zw, 1.0, 0.0), // Hexagonal tiling (hextille)
    vec4(ft.xy, 2.0, 0.0), // Trihexagonal tiling (hexadeltille)
    vec4(gq   , 3.0, 0.0), // Truncated hexagonal tiling (truncated hextille)
    vec4(ft.xx, 4.0, 0.0), // Triangular tiling (deltille) 
    vec4(gx   , 5.0, 0.0), // Rhombitrihexagonal tiling (rhombihexadeltille)
    vec4(gc   , 7.0, 0.0), // Truncated trihexagonal tiling (truncated hexadeltille)
    vec4(ft.xx, 8.0, 0.0), // Snub trihexagonal tiling (snub hextille)
    ////// Dual Laves tilings -------------------------------------------------------
    vec4(ft.xx, 4.0, 0.0), // Triangular tiling (deltille) 
    vec4(ft.xx, 0.0, 2.0), // Rhombille tiling (rhombille)
    vec4(ft.xx, 0.0, 3.0), // Triakis triangular tiling (kisdeltille)
    vec4(ft.zw, 1.0, 0.0), // Hexagonal tiling (hextille)
    vec4(ft.xx, 0.0, 5.0), // Deltoidal trihexagonal tiling (tetrille)
    vec4(ft.xx, 0.0, 7.0), // Kisrhombille tiling (kisrhombille)
    vec4(ft.xx, 9.0, 0.0)  // Floret pentagonal tiling (6-fold pentille)
); // ===============================================================================
////// centroid vectors
const vec3[20] centroids = vec3[20] (
	vec3( 0. , -0.26967233, 0.70601133),
	vec3( 0.436339, -0.436339, 0.436339),
	vec3(0.70601133, 0. , 0.26967233),
	vec3(0.436339, 0.436339, 0.436339),
	vec3(0. , 0.26967233, 0.70601133),
	vec3( 0. , -0.26967233, -0.70601133),
	vec3(-0.436339, -0.436339, -0.436339),
	vec3(-0.70601133, 0. , -0.26967233),
	vec3(-0.436339, 0.436339, -0.436339),
	vec3( 0. , 0.26967233, -0.70601133),
	vec3(-0.70601133, 0. , 0.26967233),
	vec3(-0.436339, -0.436339, 0.436339),
	vec3(-0.26967233, -0.70601133, 0. ),
	vec3( 0.26967233, -0.70601133, 0. ),
	vec3( 0.436339, -0.436339, -0.436339),
	vec3( 0.70601133, 0. , -0.26967233),
	vec3( 0.436339, 0.436339, -0.436339),
	vec3(0.26967233, 0.70601133, 0. ),
	vec3(-0.26967233, 0.70601133, 0. ),
	vec3(-0.436339, 0.436339, 0.436339)
);
////// transformation matrices
const mat4[20] transforms = mat4[20] (
	mat4( 0.5 , 0.8660254 , 0. , 0. , -0.80901699, 0.46708618, -0.35682209, 0. , -0.30901699, 0.17841104, 0.93417236, -0.75576131, 0. , 0. , 0. , 1. ),
	mat4( 0.80901699, 0.11026409, 0.57735027, 0. , 0.30901699, 0.75576131, -0.57735027, 0. , -0.5 , 0.64549722, 0.57735027, -0.75576131, 0. , 0. , 0. , 1. ),
	mat4( 0. , -0.35682209, 0.93417236, 0. , 1. , 0. , 0. , 0. , 0. , 0.93417236, 0.35682209, -0.75576131, 0. , 0. , 0. , 1. ),
	mat4(-0.80901699, 0.11026409, 0.57735027, -0. , 0.30901699, -0.75576131, 0.57735027, 0. , 0.5 , 0.64549722, 0.57735027, -0.75576131, 0. , 0. , 0. , 1. ),
	mat4(-0.5 , 0.8660254 , 0. , 0. , -0.80901699, -0.46708618, 0.35682209, 0. , 0.30901699, 0.17841104, 0.93417236, -0.75576131, 0. , 0. , 0. , 1. ),
	mat4(-0.5 , -0.8660254 , 0. , 0. , -0.80901699, 0.46708618, 0.35682209, 0. , 0.30901699, -0.17841104, 0.93417236, 0.75576131, 0. , 0. , 0. , 1. ),
	mat4(-0.80901699, -0.11026409, 0.57735027, 0. , 0.30901699, 0.75576131, 0.57735027, 0. , 0.5 , -0.64549722, 0.57735027, 0.75576131, 0. , 0. , 0. , 1. ),
	mat4( 0. , 0.35682209, 0.93417236, 0. , 1. , 0. , 0. , 0. , 0. , -0.93417236, 0.35682209, 0.75576131, 0. , 0. , 0. , 1. ),
	mat4( 0.80901699, -0.11026409, 0.57735027, -0. , 0.30901699, -0.75576131, -0.57735027, 0. , -0.5 , -0.64549722, 0.57735027, 0.75576131, 0. , 0. , 0. , 1. ),
	mat4( 0.5 , -0.8660254 , 0. , 0. , -0.80901699, -0.46708618, -0.35682209, 0. , -0.30901699, -0.17841104, 0.93417236, 0.75576131, 0. , 0. , 0. , 1. ),
	mat4( 0. , 0.35682209, -0.93417236, 0. , -1. , 0. , 0. , 0. , 0. , 0.93417236, 0.35682209, -0.75576131, 0. , 0. , 0. , 1. ),
	mat4( 0.80901699, -0.11026409, -0.57735027, -0. , -0.30901699, 0.75576131, -0.57735027, 0. , 0.5 , 0.64549722, 0.57735027, -0.75576131, 0. , 0. , 0. , 1. ),
	mat4( 0.80901699, 0.46708618, 0.35682209, -0. , -0.30901699, -0.17841104, 0.93417236, -0. , -0.5 , 0.8660254 , -0. , 0.75576131, 0. , 0. , 0. , 1. ),
	mat4( 0.80901699, -0.46708618, -0.35682209, 0. , 0.30901699, -0.17841104, 0.93417236, -0. , 0.5 , 0.8660254 , -0. , 0.75576131, 0. , 0. , 0. , 1. ),
	mat4( 0.5 , 0.64549722, -0.57735027, -0. , 0.80901699, -0.11026409, 0.57735027, 0. , -0.30901699, 0.75576131, 0.57735027, 0.75576131, 0. , 0. , 0. , 1. ),
	mat4( 0.30901699, 0.17841104, -0.93417236, 0. , 0.5 , -0.8660254 , -0. , -0. , 0.80901699, 0.46708618, 0.35682209, 0.75576131, 0. , 0. , 0. , 1. ),
	mat4(-0.5 , 0.64549722, -0.57735027, 0. , 0.80901699, 0.11026409, -0.57735027, 0. , 0.30901699, 0.75576131, 0.57735027, 0.75576131, 0. , 0. , 0. , 1. ),
	mat4( 0. , 0.93417236, -0.35682209, 0. , 0. , -0.35682209, -0.93417236, 0. , 1. , 0. , 0. , 0.75576131, 0. , 0. , 0. , 1. ),
	mat4(-0.80901699, 0.46708618, 0.35682209, 0. , -0.30901699, 0.17841104, -0.93417236, -0. , 0.5 , 0.8660254 , -0. , 0.75576131, 0. , 0. , 0. , 1. ),
	mat4( 0.30901699, 0.75576131, -0.57735027, 0. , -0.5 , 0.64549722, 0.57735027, 0. , 0.80901699, 0.11026409, 0.57735027, -0.75576131, 0. , 0. , 0. , 1. )
);

// functions

//// rotations

mat2 rotmat2(float angle) {
    float c = cos(angle), s = sin(angle);
    return mat2(c, -s, s, c);
}

mat3 rotmat3(vec3 angle) {
    float sintht = sin(angle.x), sinpsi = sin(angle.y), sinphi = sin(angle.z);
    float costht = cos(angle.x), cospsi = cos(angle.y), cosphi = cos(angle.z);
    return mat3(
        costht * cospsi, sintht * cospsi, -sinpsi, 
        costht * sinpsi * sinphi - sintht * cosphi, sintht * sinpsi * sinphi + costht * cosphi, cospsi * sinphi,
        costht * sinpsi * cosphi + sintht * sinphi, sintht * sinpsi * cosphi - costht * sinphi, cospsi * cosphi
    );
}

//// intersection

vec2 intersection(vec2 p1, vec2 p2, vec2 p3, vec2 p4) {
    // http://paulbourke.net/geometry/pointlineplane/
    float d = (p4.y - p3.y) * (p2.x - p1.x) - (p4.x - p3.x) * (p2.y - p1.y);
    float ua = ((p4.x - p3.x) * (p1.y - p3.y) - (p4.y - p3.y) * (p1.x - p3.x)) / d;
    // float ub = ((p2.x - p1.x) * (p1.y - p3.y) - (p2.y - p1.y) * (p1.x - p3.x)) / d;
    return p1 + ua * (p2 - p1);
}

//// signed distance functions
////// ------------------------------------------------
////// https://iquilezles.org/articles/distfunctions/
////// https://iquilezles.org/articles/distfunctions2d/
////// vvvvvvvvvvvvvvvvvvvvvvvvvvvvvvvvvvvvvvvvvvvvvvvv

float opIntersection( float d1, float d2 ) { 
    // https://iquilezles.org/articles/distfunctions/
    return max(d1, d2); 
}


float opUnion(float d1, float d2) {
    // https://iquilezles.org/articles/distfunctions/
    return min(d1, d2); 
}

float opOnion(in float sdf, in float thickness) {
    // https://iquilezles.org/articles/distfunctions/
    return abs(sdf) - thickness;
}

vec3 opRep(in vec3 p, in vec3 c) {
    // https://iquilezles.org/articles/distfunctions/
    return mod(p + 0.5 * c, c) - 0.5 * c;
}

float sdCapsule(vec3 p, vec3 a, vec3 b, float r) {
    vec3 pa = p - a, ba = b - a;
    float h = clamp(dot(pa, ba) / dot(ba, ba), 0.0, 1.0);
    return length(pa - ba * h) - r;
}

float sdSphere(vec3 p, float s) {
    return length(p) - s;
}

float sdCone(in vec3 p, in vec2 c, float h) {
    // c is the sin/cos of the angle, h is height
    // Alternatively pass q instead of (c,h),
    // which is the point at the base in 2D
    vec2 q = h * vec2(c.x / c.y, -1.0);

    vec2 w = vec2(length(p.xz), p.y);
    vec2 a = w - q * clamp(dot(w,q) / dot(q,q), 0.0, 1.0);
    vec2 b = w - q * vec2(clamp(w.x / q.x, 0.0, 1.0 ), 1.0);
    float k = sign(q.y);
    float d = min(dot(a, a), dot(b, b));
    float s = max(k * (w.x * q.y - w.y * q.x), k * (w.y - q.y));
    return sqrt(d) * sign(s);
}

float sdTriangle(in vec2 p, in vec2 p0, in vec2 p1, in vec2 p2) {
    vec2 e0 = p1 - p0, e1 = p2 - p1, e2 = p0 - p2;
    vec2 t0 = p  - p0, t1 = p  - p1, t2 = p  - p2;
    vec2 pq0 = t0 - e0 * clamp(dot(t0, e0)/ dot(e0, e0), 0.0, 1.0);
    vec2 pq1 = t1 - e1 * clamp(dot(t1, e1)/ dot(e1, e1), 0.0, 1.0);
    vec2 pq2 = t2 - e2 * clamp(dot(t2, e2)/ dot(e2, e2), 0.0, 1.0);
    float s = sign(e0.x * e2.y - e0.y * e2.x );
    vec2 d = min(min(vec2(dot(pq0, pq0), s * (t0.x * e0.y - t0.y * e0.x)),
                     vec2(dot(pq1, pq1), s * (t1.x * e1.y - t1.y * e1.x))),
                     vec2(dot(pq2, pq2), s * (t2.x * e2.y - t2.y * e2.x)));
    return -sqrt(d.x) * sign(d.y);
}

float sdEquilateralTriangle(in vec2 p, in float r) {
    // r is half of the side
    const float k = sqrt(3.0);
    p.x = abs(p.x) - r;
    p.y = p.y + r / k;
    if (p.x + k * p.y > 0.0) p = vec2(p.x - k * p.y, -k * p.x - p.y) / 2.0;
    p.x -= clamp(p.x, -2.0 * r, 0.0);
    return -length(p) * sign(p.y);
}

